import { render, screen, waitFor } from '@testing-library/react';
import React from 'react';
import { BrowserRouter } from 'react-router-dom';

import { SeasonProvider } from '../context/SeasonContext';
import DashboardPage from '../pages/DashboardPage';
import * as api from '../services/api';

// Mock the API calls
jest.mock('../services/api');
jest.mock('react-router-dom', () => ({
  ...jest.requireActual('react-router-dom'),
  useNavigate: () => jest.fn()
}));

// Mock the chart components from recharts
jest.mock('recharts', () => ({
  ...jest.requireActual('recharts'),
  BarChart: () => <div data-testid='bar-chart'>Bar Chart</div>,
  Bar: () => <div data-testid='bar'>Bar</div>,
  XAxis: () => <div data-testid='x-axis'>X Axis</div>,
  YAxis: () => <div data-testid='y-axis'>Y Axis</div>,
  CartesianGrid: () => <div data-testid='cartesian-grid'>Cartesian Grid</div>,
  Tooltip: () => <div data-testid='tooltip'>Tooltip</div>,
  Legend: () => <div data-testid='legend'>Legend</div>,
  ResponsiveContainer: ({ children }) => <div data-testid='responsive-container'>{children}</div>,
  LineChart: () => <div data-testid='line-chart'>Line Chart</div>,
  Line: () => <div data-testid='line'>Line</div>,
  PieChart: () => <div data-testid='pie-chart'>Pie Chart</div>,
  Pie: () => <div data-testid='pie'>Pie</div>,
  Cell: () => <div data-testid='cell'>Cell</div>
}));

// Mock the components that are imported
jest.mock('../components/KPICard', () => ({
  __esModule: true,
  default: ({ title, value, changeText, suffix }) => (
    <div data-testid='kpi-card'>
      <span data-testid='kpi-title'>{title}</span>
      <span data-testid='kpi-value'>
        {value}
        {suffix}
      </span>
      {changeText && <span data-testid='kpi-change'>{changeText}</span>}
    </div>
  ),
  CircularKPICard: ({ title, value }) => (
    <div data-testid='circular-kpi-card'>
      <span data-testid='circular-kpi-title'>{title}</span>
      <span data-testid='circular-kpi-value'>{value}</span>
    </div>
  )
}));

jest.mock('../components/AlertBanner', () => {
  return ({ message, severity, dismissible, onClose }) => (
    <div data-testid='alert-banner' data-severity={severity}>
      {message}
      {dismissible && <button onClick={onClose}>Close</button>}
    </div>
  );
});

jest.mock('../components/AquacultureTooltip', () => {
  return ({ children }) => <div data-testid='aquaculture-tooltip'>{children}</div>;
});

jest.mock('../components/PredictiveInsight', () => {
  return ({ title, insight, confidence, projectedDate }) => (
    <div data-testid='predictive-insight'>
      <span data-testid='insight-title'>{title}</span>
      <span data-testid='insight-content'>{insight}</span>
      {confidence && <span data-testid='insight-confidence'>{confidence}%</span>}
      {projectedDate && <span data-testid='insight-date'>{projectedDate}</span>}
    </div>
  );
});

jest.mock('../components/HealthScore', () => {
  return ({ score }) => (
    <div data-testid='health-score' role='progressbar' aria-valuenow={score}>
      {score}
    </div>
  );
});

jest.mock('../components/PondCard', () => {
  return ({ pond }) => (
    <div data-testid='pond-card'>
      <span data-testid='pond-name'>{pond.name}</span>
      <span data-testid='pond-status'>{pond.status}</span>
      <span data-testid='pond-health'>{pond.health}</span>
      <span data-testid='pond-progress'>{pond.progress}%</span>
    </div>
  );
});

jest.mock('../components/DataTrend', () => {
  return ({ title, data }) => (
    <div data-testid='data-trend'>
      <span data-testid='data-trend-title'>{title}</span>
      <span data-testid='data-trend-content'>Chart with {data?.length || 0} data points</span>
    </div>
  );
});

jest.mock('../components/QuickActions', () => {
  return ({ onActionClick }) => <div data-testid='quick-actions'>Quick Actions</div>;
});

// Mock SeasonContext
const mockSeasonContext = {
  seasons: [{ id: 1, name: 'Test Season', status: 'Active' }],
  selectedSeason: { id: 1, name: 'Test Season', status: 'Active' },
  selectSeason: jest.fn(),
  setSelectedSeason: jest.fn(),
  loading: false,
  error: null
};

jest.mock('../context/SeasonContext', () => ({
  ...jest.requireActual('../context/SeasonContext'),
  useSeason: () => mockSeasonContext
}));

describe('DashboardPage', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  it('should render loading state initially', async () => {
    // Mock API to simulate loading
    api.getPonds.mockImplementation(() => new Promise(() => {})); // Never resolves

    render(
      <BrowserRouter>
        <SeasonProvider>
          <DashboardPage />
        </SeasonProvider>
      </BrowserRouter>
    );

    // Should show loading spinner
    expect(screen.getByRole('progressbar')).toBeInTheDocument();
  });

  it('should render dashboard content when data is loaded', async () => {
    // Mock API responses
    api.getPonds.mockResolvedValue({
      data: [
        {
          id: 1,
          name: 'Pond A',
          status: 'Active',
          seasonId: 1,
          health: 'Good',
          progress: 75,
          healthScore: 85
        },
        {
          id: 2,
          name: 'Pond B',
          status: 'Inactive',
          seasonId: 1,
          health: 'Fair',
          progress: 45,
          healthScore: 65
        }
      ]
    });

    render(
      <BrowserRouter>
        <SeasonProvider>
          <DashboardPage />
        </SeasonProvider>
      </BrowserRouter>
    );

    // Wait for loading to complete
    await waitFor(() => {
      expect(screen.queryByRole('progressbar')).not.toBeInTheDocument();
    });

    // Check that key elements are rendered
    expect(screen.getByText('Farm Dashboard')).toBeInTheDocument();
    expect(screen.getByText('Individual Pond Management')).toBeInTheDocument();
    expect(screen.getByText('AI Insights & Recommendations')).toBeInTheDocument();

    // Check that KPI cards are rendered (there are 6)
    expect(screen.getAllByTestId('kpi-card')).toHaveLength(6);

    // Check that pond cards are rendered
    expect(screen.getAllByTestId('pond-card')).toHaveLength(2);

    // Check that data trends are rendered
    expect(screen.getByText('Water Quality Trend')).toBeInTheDocument();
    expect(screen.getByText('Feed Consumption Trend')).toBeInTheDocument();
  });

  it('should show error message when API fails', async () => {
    // Mock API to simulate error
    const errorMessage = 'Failed to fetch ponds';
    api.getPonds.mockRejectedValue(new Error(errorMessage));

    render(
      <BrowserRouter>
        <SeasonProvider>
          <DashboardPage />
        </SeasonProvider>
      </BrowserRouter>
    );

    // Wait for error to be displayed
    await waitFor(
      () => {
        expect(
          screen.getByText((content, element) => {
            return content.includes('Error loading dashboard data');
          })
        ).toBeInTheDocument();
      },
      { timeout: 10000 }
    );
  });

  it('should filter ponds based on status', async () => {
    // Mock API responses
    api.getPonds.mockResolvedValue({
      data: [
        {
          id: 1,
          name: 'Pond A',
          status: 'Active',
          seasonId: 1,
          health: 'Good',
          progress: 75,
          healthScore: 85
        },
        {
          id: 2,
          name: 'Pond B',
          status: 'Inactive',
          seasonId: 1,
          health: 'Fair',
          progress: 45,
          healthScore: 65
        }
      ]
    });

    render(
      <BrowserRouter>
        <SeasonProvider>
          <DashboardPage />
        </SeasonProvider>
      </BrowserRouter>
    );

    // Wait for loading to complete
    await waitFor(() => {
      expect(screen.queryByRole('progressbar')).not.toBeInTheDocument();
    });

    // Check that both ponds are initially shown
    expect(screen.getAllByTestId('pond-card')).toHaveLength(2);
  });
});

/**
 * Expense Management Integration Tests
 * Tests complete user workflows and component integration
 */

import { screen, waitFor, within } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import React from 'react';

import { SeasonProvider } from '../../context/SeasonContext';
import ExpenseManagementPage from '../../pages/ExpenseManagementPage';
import { renderUtils, testDataFactories, mockUtils } from '../../utils/testUtils';

// Mock API responses
const mockExpenses = [
  testDataFactories.createExpense({
    _id: '1',
    description: 'Fish Feed Purchase',
    amount: 500,
    mainCategory: 'Culture',
    subCategory: 'Feed',
    date: '2024-01-15'
  }),
  testDataFactories.createExpense({
    _id: '2',
    description: 'Pond Maintenance',
    amount: 200,
    mainCategory: 'Farm',
    subCategory: 'Maintenance',
    date: '2024-01-10'
  })
];

const mockSeasons = [
  testDataFactories.createSeason({
    _id: 'season-1',
    name: 'Season 2024',
    status: 'Active'
  })
];

describe('Expense Management Integration Tests', () => {
  let mockFetch;

  beforeEach(() => {
    // Setup comprehensive API mocks
    mockFetch = mockUtils.createMockFetch({
      '/seasons': mockSeasons,
      '/expenses': mockExpenses,
      '/expenses/create': { success: true, data: mockExpenses[0] },
      '/expenses/update': { success: true },
      '/expenses/delete': { success: true }
    });
  });

  afterEach(() => {
    mockFetch.cleanup();
    jest.restoreAllMocks();
  });

  const renderExpenseManagementPage = () => {
    return renderUtils.renderWithProviders(
      <SeasonProvider>
        <ExpenseManagementPage />
      </SeasonProvider>
    );
  };

  describe('Page Load and Navigation', () => {
    it('loads the expense management page and navigates between tabs', async () => {
      renderExpenseManagementPage();

      // Wait for page to load
      await waitFor(() => {
        expect(screen.getByText('Expense Management')).toBeInTheDocument();
      });

      // Check that tabs are present
      expect(screen.getByRole('tab', { name: /dashboard/i })).toBeInTheDocument();
      const cultureTab = screen.getByRole('tab', { name: /culture expenses/i });
      expect(cultureTab).toBeInTheDocument();
      const farmTab = screen.getByRole('tab', { name: /farm expenses/i });
      expect(farmTab).toBeInTheDocument();
      expect(screen.getByRole('tab', { name: /salaries/i })).toBeInTheDocument();
      expect(screen.getByRole('tab', { name: /reports/i })).toBeInTheDocument();

      // Navigate to Culture Expenses tab
      await userEvent.click(cultureTab);

      await waitFor(() => {
        expect(screen.getByText('Culture Expenses')).toBeInTheDocument();
      });

      // Navigate to Farm Expenses tab
      await userEvent.click(farmTab);

      await waitFor(() => {
        expect(screen.getByText('Farm Expenses')).toBeInTheDocument();
      });
    });
  });

  describe('Expense List Integration', () => {
    it('displays and filters expenses correctly', async () => {
      renderExpenseManagementPage();

      // Check Culture expenses
      const cultureTab = screen.getByRole('tab', { name: /culture expenses/i });
      await userEvent.click(cultureTab);

      await waitFor(() => {
        expect(screen.getByText('Fish Feed Purchase')).toBeInTheDocument();
      });
      await waitFor(() => {
        expect(screen.queryByText('Pond Maintenance')).not.toBeInTheDocument();
      });

      // Verify expense details
      expect(screen.getByText('$500.00')).toBeInTheDocument();
      expect(screen.getByText('Feed')).toBeInTheDocument();

      // Check Farm expenses
      const farmTab = screen.getByRole('tab', { name: /farm expenses/i });
      await userEvent.click(farmTab);

      await waitFor(() => {
        expect(screen.getByText('Pond Maintenance')).toBeInTheDocument();
      });
      await waitFor(() => {
        expect(screen.queryByText('Fish Feed Purchase')).not.toBeInTheDocument();
      });
    });
  });

  describe('Expense Creation and Validation', () => {
    it('completes full expense creation workflow and handles validation', async () => {
      renderExpenseManagementPage();

      // Navigate to Culture Expenses
      const cultureTab = screen.getByRole('tab', { name: /culture expenses/i });
      await userEvent.click(cultureTab);

      // Wait for page to load and click Add button
      await waitFor(() => {
        expect(screen.getByRole('button', { name: /add culture expense/i })).toBeInTheDocument();
      });

      const addButton = screen.getByRole('button', { name: /add culture expense/i });
      await userEvent.click(addButton);

      // Wait for form modal to open
      await waitFor(() => {
        expect(screen.getByRole('dialog')).toBeInTheDocument();
      });

      // Try to submit empty form
      const modal = screen.getByRole('dialog');
      const saveButton = within(modal).getByRole('button', { name: /save/i });
      await userEvent.click(saveButton);

      // Check for validation errors
      await waitFor(() => {
        expect(within(modal).getByText('Description is required')).toBeInTheDocument();
      });

      // Fill out the form
      const descriptionInput = within(modal).getByLabelText(/description/i);
      await userEvent.type(descriptionInput, 'New Test Expense');

      const amountInput = within(modal).getByLabelText(/amount/i);
      await userEvent.type(amountInput, '150');

      const categorySelect = within(modal).getByLabelText(/sub category/i);
      await userEvent.selectOptions(categorySelect, 'Equipment');

      const dateInput = within(modal).getByLabelText(/date/i);
      await userEvent.type(dateInput, '2024-01-20');

      // Submit form
      await userEvent.click(saveButton);

      // Wait for form to close and expense to appear
      await waitFor(() => {
        expect(screen.queryByRole('dialog')).not.toBeInTheDocument();
      });

      // Verify expense was added (mock API would return success)
      expect(mockFetch.mockFetch).toHaveBeenCalledWith(
        expect.stringContaining('/expenses'),
        expect.objectContaining({
          method: 'POST',
          body: expect.stringContaining('New Test Expense')
        })
      );
    });
  });

  describe('Expense Editing Workflow', () => {
    it('completes full expense editing workflow', async () => {
      renderExpenseManagementPage();

      const cultureTab = screen.getByRole('tab', { name: /culture expenses/i });
      await userEvent.click(cultureTab);

      // Wait for expenses to load and find edit button
      await waitFor(() => {
        expect(screen.getByText('Fish Feed Purchase')).toBeInTheDocument();
      });

      // Find and click edit button for the first expense
      const editButtons = screen.getAllByRole('button', { name: /edit/i });
      await userEvent.click(editButtons[0]);

      // Wait for edit form to open
      const modal = await screen.findByRole('dialog');

      // Verify form is pre-filled
      const descriptionInput = within(modal).getByDisplayValue('Fish Feed Purchase');
      expect(descriptionInput).toBeInTheDocument();

      // Update the description
      await userEvent.type(descriptionInput, ' - Updated');

      // Update amount
      const amountInput = within(modal).getByDisplayValue('500');
      await userEvent.clear(amountInput);
      await userEvent.type(amountInput, '600');

      // Save changes
      const saveButton = within(modal).getByRole('button', { name: /save/i });
      await userEvent.click(saveButton);

      // Wait for form to close
      await waitFor(() => {
        expect(screen.queryByRole('dialog')).not.toBeInTheDocument();
      });

      // Verify API was called with update
      expect(mockFetch.mockFetch).toHaveBeenCalledWith(
        expect.stringContaining('/expenses/1'),
        expect.objectContaining({
          method: 'PUT',
          body: expect.stringContaining('Fish Feed Purchase - Updated')
        })
      );
    });
  });

  describe('Expense Deletion Workflow', () => {
    it('completes expense deletion with confirmation and handles cancellation', async () => {
      const confirmSpy = jest.spyOn(window, 'confirm');

      // Test deletion cancellation
      confirmSpy.mockReturnValue(false);
      renderExpenseManagementPage();

      const cultureTab = screen.getByRole('tab', { name: /culture expenses/i });
      await userEvent.click(cultureTab);

      await waitFor(() => {
        expect(screen.getByText('Fish Feed Purchase')).toBeInTheDocument();
      });

      const deleteButtons = screen.getAllByRole('button', { name: /delete/i });
      await userEvent.click(deleteButtons[0]);

      expect(confirmSpy).toHaveBeenCalledWith('Are you sure you want to delete this expense?');

      // Verify API was NOT called for deletion
      expect(mockFetch.mockFetch).not.toHaveBeenCalledWith(
        expect.stringContaining('/expenses/1'),
        expect.objectContaining({ method: 'DELETE' })
      );

      // Test deletion confirmation
      confirmSpy.mockReturnValue(true);
      await userEvent.click(deleteButtons[0]);

      // Verify API was called
      await waitFor(() => {
        expect(mockFetch.mockFetch).toHaveBeenCalledWith(
          expect.stringContaining('/expenses/1'),
          expect.objectContaining({ method: 'DELETE' })
        );
      });
    });
  });

  describe('Error Handling Integration', () => {
    it('displays error when API fails and handles network timeout', async () => {
      // Mock API failure
      mockFetch.cleanup();
      mockFetch = mockUtils.createMockFetch({
        '/seasons': mockSeasons,
        '/expenses': Promise.reject(new Error('API Error'))
      });

      renderExpenseManagementPage();

      const cultureTab = screen.getByRole('tab', { name: /culture expenses/i });
      await userEvent.click(cultureTab);

      // Wait for error to be displayed
      await waitFor(() => {
        expect(screen.getByRole('alert')).toBeInTheDocument();
      });

      const errorElement = screen.getByRole('alert');
      expect(errorElement).toHaveTextContent(/error/i);

      // Mock slow API response
      mockFetch.cleanup();
      global.fetch = jest.fn(
        () =>
          new Promise((_, reject) => setTimeout(() => reject(new Error('Network timeout')), 100))
      );

      renderExpenseManagementPage();

      await userEvent.click(cultureTab);

      // Should show loading state first
      await waitFor(() => {
        expect(screen.getByRole('status') || screen.getByText(/loading/i)).toBeInTheDocument();
      });

      // Then show error after timeout
      await waitFor(
        () => {
          expect(screen.getByRole('alert')).toBeInTheDocument();
        },
        { timeout: 5000 }
      );
    });
  });

  describe('Season Context Integration', () => {
    it('updates expenses when season changes', async () => {
      const multipleSeasons = [
        ...mockSeasons,
        testDataFactories.createSeason({
          _id: 'season-2',
          name: 'Season 2023',
          status: 'Completed'
        })
      ];

      mockFetch.cleanup();
      mockFetch = mockUtils.createMockFetch({
        '/seasons': multipleSeasons,
        '/expenses': mockExpenses
      });

      renderExpenseManagementPage();

      const cultureTab = screen.getByRole('tab', { name: /culture expenses/i });
      await userEvent.click(cultureTab);

      // Wait for initial load
      await waitFor(() => {
        expect(screen.getByText('Fish Feed Purchase')).toBeInTheDocument();
      });

      // Change season (this would typically be done through a season selector)
      // For this test, we'll verify that changing the season context would trigger a refetch
      expect(mockFetch.mockFetch).toHaveBeenCalledWith(
        expect.stringContaining('/expenses'),
        expect.objectContaining({
          method: 'GET'
        })
      );
    });
  });

  describe('Performance and Loading States', () => {
    it('shows loading states and handles concurrent operations', async () => {
      // Mock slow API
      mockFetch.cleanup();
      global.fetch = jest.fn(
        () =>
          new Promise(resolve =>
            setTimeout(
              () =>
                resolve({
                  ok: true,
                  json: () => Promise.resolve(mockExpenses)
                }),
              500
            )
          )
      );

      renderExpenseManagementPage();

      const cultureTab = screen.getByRole('tab', { name: /culture expenses/i });
      await userEvent.click(cultureTab);

      // Should show loading state
      await waitFor(() => {
        expect(screen.getByRole('status') || screen.getByText(/loading/i)).toBeInTheDocument();
      });

      // Wait for data to load
      await waitFor(
        () => {
          expect(screen.getByText('Fish Feed Purchase')).toBeInTheDocument();
        },
        { timeout: 1000 }
      );

      // Start multiple operations concurrently
      const addButton = screen.getByRole('button', { name: /add culture expense/i });
      const editButtons = screen.getAllByRole('button', { name: /edit/i });

      // This should not cause race conditions or crashes
      await Promise.all([
        userEvent.click(addButton),
        // Small delay to prevent exact simultaneous clicks
        new Promise(resolve =>
          setTimeout(() => {
            userEvent.click(editButtons[0]).then(resolve);
          }, 10)
        )
      ]);

      // Should handle gracefully (one operation at a time)
      await waitFor(() => {
        expect(screen.getByRole('dialog')).toBeInTheDocument();
      });
    });
  });
});

import { renderHook, waitFor } from '@testing-library/react';

import { useApiData, useApiMutation, clearAllCache } from '../hooks/useApi';
import { waitUtils, testDataFactories } from '../utils/testUtils';

// Mock API functions
const mockApiFunction = jest.fn();
const mockApiMutationFunction = jest.fn();

describe('useApiData', () => {
  beforeEach(() => {
    jest.clearAllMocks();
    clearAllCache();
  });

  it('should fetch data successfully', async () => {
    const mockData = testDataFactories.createPond();
    mockApiFunction.mockResolvedValue(mockData);

    const { result } = renderHook(() => useApiData(mockApiFunction, []));

    // Initial state
    expect(result.current.loading).toBe(true);
    expect(result.current.error).toBe(null);

    // Wait for the hook to update using proper wait utilities
    await waitFor(
      () => {
        expect(result.current.loading).toBe(false);
      },
      { timeout: 3000 }
    );

    // Verify final state
    expect(result.current.data).toEqual(mockData);
    expect(result.current.error).toBe(null);
    expect(mockApiFunction).toHaveBeenCalledTimes(1);
  });

  it('should handle API errors', async () => {
    const errorMessage = 'API Error';
    mockApiFunction.mockRejectedValue(new Error(errorMessage));

    const { result } = renderHook(
      () => useApiData(mockApiFunction, [], null, 0) // Set retryCount to 0 to avoid retries
    );

    // Initial state
    expect(result.current.loading).toBe(true);
    expect(result.current.error).toBe(null);

    // Wait for error state using reliable wait utility
    await waitFor(
      () => {
        expect(result.current.loading).toBe(false);
      },
      { timeout: 3000 }
    );

    // Verify error state
    expect(result.current.data).toBe(null);
    expect(result.current.error).toEqual({ message: errorMessage });
    expect(mockApiFunction).toHaveBeenCalledTimes(1);
  });

  it('should refetch data when called', async () => {
    const mockData = testDataFactories.createPond();
    mockApiFunction.mockResolvedValue(mockData);

    const { result } = renderHook(() => useApiData(mockApiFunction, []));

    // Wait for initial fetch to complete
    await waitFor(
      () => {
        expect(result.current.loading).toBe(false);
      },
      { timeout: 3000 }
    );

    expect(mockApiFunction).toHaveBeenCalledTimes(1);

    // Call refetch and wait for completion
    await waitUtils.waitForAsync(async () => {
      await result.current.refetch();
      return mockApiFunction.mock.calls.length === 2;
    });

    // Verify refetch was called
    expect(mockApiFunction).toHaveBeenCalledTimes(2);
  });

  it('should use cache when available', async () => {
    const mockData = { id: 1, name: 'Test Data' };
    mockApiFunction.mockResolvedValue(mockData);

    const { result, rerender } = renderHook(
      ({ cacheKey }) => useApiData(mockApiFunction, [], cacheKey),
      { initialProps: { cacheKey: 'test-cache-key' } }
    );

    // Wait for initial fetch
    await new Promise(resolve => setTimeout(resolve, 10));

    expect(mockApiFunction).toHaveBeenCalledTimes(1);

    // Rerender with same cache key
    rerender({ cacheKey: 'test-cache-key' });

    // Should not call API again if cache is used
    expect(mockApiFunction).toHaveBeenCalledTimes(1);
    expect(result.current.data).toEqual(mockData);
  });
});

describe('useApiMutation', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  it('should execute mutation successfully', async () => {
    const mockData = testDataFactories.createExpense();
    mockApiMutationFunction.mockResolvedValue(mockData);

    const { result } = renderHook(() => useApiMutation(mockApiMutationFunction));

    expect(result.current.loading).toBe(false);
    expect(result.current.error).toBe(null);

    // Execute mutation with proper async handling
    const mutationPromise = result.current.mutate({ name: 'Test Item' });

    // Wait for mutation to complete
    const mutationResult = await waitUtils.waitForAsync(async () => {
      return await mutationPromise;
    });

    expect(result.current.loading).toBe(false);
    expect(result.current.error).toBe(null);
    expect(mutationResult.data).toEqual(mockData);
    expect(mockApiMutationFunction).toHaveBeenCalledWith({ name: 'Test Item' });
  });

  it('should handle mutation errors', async () => {
    const errorMessage = 'Mutation Error';
    mockApiMutationFunction.mockRejectedValue(new Error(errorMessage));

    const { result } = renderHook(
      () => useApiMutation(mockApiMutationFunction, 0) // Set maxRetryCount to 0 to avoid retries
    );

    // Execute mutation
    const mutationResult = await result.current.mutate({ name: 'Test Item' });

    expect(result.current.loading).toBe(false);
    expect(mutationResult.error).toBe(errorMessage);
  });
});

