            {mainCategory === 'Salary' && (
              <Grid item xs={12}>
                <Controller
                  name='employee'
                  control={control}
                  render={({ field }) => (
                    <TextField {...field} select label='Employee' fullWidth>
                      {employees.map(emp => (
                        <MenuItem key={emp._id} value={emp._id}>
                          {emp.name}
                        </MenuItem>
                      ))}
                    </TextField>
                  )}
                />
              </Grid>
            )}
            <Grid item xs={12}>
              <Controller
                name='description'
                control={control}
                render={({ field }) => (
                  <TextField {...field} label='Description' fullWidth multiline rows={3} />
                )}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={onClose}>Cancel</Button>
          <Button type='submit' variant='contained'>
            Save
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
};

export default ExpenseForm;

