import {
  Lightbulb as LightbulbIcon,
  WaterDrop as WaterIcon,
  Restaurant as FeedIcon,
  Science as GrowthIcon,
  Build as MaintenanceIcon,
  Warning as WarningIcon
} from '@mui/icons-material';
import {
  Card,
  CardContent,
