// Mobile Navigation Bar
export const MobileNavBar = ({ title, onMenuClick, actions = [] }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  if (!isMobile) return null;

  return (
    <AppBar position='fixed' sx={{ zIndex: theme.zIndex.drawer + 1 }}>
      <Toolbar sx={{ minHeight: 56, px: 2 }}>
        <IconButton edge='start' color='inherit' onClick={onMenuClick} sx={{ mr: 2 }}>
          <MenuIcon />
        </IconButton>

        <Typography variant='h6' sx={{ flexGrow: 1, fontSize: '1.1rem' }}>
          {title}
        </Typography>

        {actions.map((action, index) => (
          <IconButton key={index} color='inherit' onClick={action.onClick} size='small'>
            {action.icon}
          </IconButton>
        ))}
      </Toolbar>
    </AppBar>
  );
};

// Mobile Drawer with swipe support
export const MobileDrawer = ({ open, onClose, onOpen, menuItems = [], user }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const drawerContent = (
    <Box sx={{ width: 280, pt: 2 }}>
      {user && (
        <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider' }}>
          <Typography variant='subtitle1' fontWeight='medium'>
            {user.name}
          </Typography>
          <Typography variant='body2' color='text.secondary'>
            {user.email}
          </Typography>
        </Box>
      )}

      <List>
        {menuItems.map((item, index) => (
          <ListItem
            button
            key={index}
            onClick={() => {
              item.onClick?.();
              onClose();
            }}
            sx={{
              minHeight: 48,
              px: 2,
              '&:hover': {
                backgroundColor: 'action.hover'
              }
            }}
          >
            <ListItemIcon sx={{ minWidth: 40 }}>{item.icon}</ListItemIcon>
            <ListItemText primary={item.text} primaryTypographyProps={{ fontSize: '0.95rem' }} />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  if (!isMobile) return null;

  return (
    <SwipeableDrawer
      anchor='left'
      open={open}
      onClose={onClose}
      onOpen={onOpen}
      disableSwipeToOpen={false}
      ModalProps={{
        keepMounted: true // Better mobile performance
      }}
      sx={{
        '& .MuiDrawer-paper': {
          boxSizing: 'border-box',
          width: 280
        }
      }}
    >
      {drawerContent}
    </SwipeableDrawer>
  );
};

// Mobile Bottom Navigation
export const MobileBottomNav = ({ value, onChange, items = [], showLabels = false }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  if (!isMobile) return null;

  return (
    <BottomNavigation
      value={value}
      onChange={onChange}
      showLabels={showLabels}
      sx={{
        position: 'fixed',
        bottom: 0,
        left: 0,
        right: 0,
        zIndex: theme.zIndex.appBar,
        borderTop: 1,
        borderColor: 'divider',
        backgroundColor: 'background.paper'
      }}
    >
      {items.map((item, index) => (
        <BottomNavigationAction
          key={index}
          label={item.label}
          icon={item.icon}
          value={item.value}
        />
      ))}
    </BottomNavigation>
  );
};

// Mobile Speed Dial for quick actions
export const MobileSpeedDial = ({ actions = [] }) => {
  const [open, setOpen] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  if (!isMobile || actions.length === 0) return null;

  return (
    <SpeedDial
      ariaLabel='Quick actions'
      sx={{
        position: 'fixed',
        bottom: 80,
        right: 16
      }}
      icon={<SpeedDialIcon />}
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
    >
      {actions.map((action, index) => (
        <SpeedDialAction
          key={index}
          icon={action.icon}
          tooltipTitle={action.name}
          onClick={() => {
            action.onClick?.();
            setOpen(false);
          }}
        />
      ))}
    </SpeedDial>
  );
};

