const ResponsiveTable = ({ columns, data, onRowClick, rowKey }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  if (isMobile) {
    // Render as cards on mobile
    return (
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        {data.map((row, index) => (
          <Card
            key={rowKey ? row[rowKey] : index}
            variant='outlined'
            onClick={() => onRowClick && onRowClick(row)}
            sx={{
              cursor: onRowClick ? 'pointer' : 'default',
              '&:hover': onRowClick ? { boxShadow: 3 } : {}
            }}
          >
            <CardContent>
              {columns.map(column => (
                <Box key={column.id} sx={{ mb: 1 }}>
                  <Typography variant='caption' color='text.secondary'>
                    {column.label}
                  </Typography>
                  <Typography variant='body2' sx={{ wordBreak: 'break-word' }}>
                    {column.render ? column.render(row[column.id], row) : row[column.id]}
                  </Typography>
                </Box>
              ))}
            </CardContent>
          </Card>
        ))}
      </Box>
    );
  }

  // Render as table on desktop
  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map(column => (
              <TableCell key={column.id} sx={{ fontWeight: 'bold' }}>
                {column.label}
              </TableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          {data.map((row, index) => (
            <TableRow
              key={rowKey ? row[rowKey] : index}
              onClick={() => onRowClick && onRowClick(row)}
              sx={{
                cursor: onRowClick ? 'pointer' : 'default',
                '&:hover': onRowClick ? { backgroundColor: 'action.hover' } : {}
              }}
            >
              {columns.map(column => (
                <TableCell key={column.id}>
                  {column.render ? column.render(row[column.id], row) : row[column.id]}
                </TableCell>
              ))}
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default ResponsiveTable;

import { Info as InfoIcon } from '@mui/icons-material';
import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Typography,
  Box,
  Chip,
  Tooltip,
  IconButton
} from '@mui/material';
import PropTypes from 'prop-types';
import React, { useState, useEffect } from 'react';

import AquacultureTooltip from '../farm/AquacultureTooltip';

const FeedCalculator = ({
  onCalculate,
  initialBiomass = 0,
  initialShrimpCount = 0,
  feedingStrategy = 'standard'
}) => {
  const [biomass, setBiomass] = useState(initialBiomass);
  const [shrimpCount, setShrimpCount] = useState(initialShrimpCount);
  const [strategy, setStrategy] = useState(feedingStrategy);
  const [calculatedFeed, setCalculatedFeed] = useState(0);

  // Standard feeding rates by strategy (percentage of biomass per day)
  const feedingRates = {
    standard: 0.03, // 3% of biomass
    growth: 0.04, // 4% of biomass for growth phase
    maintenance: 0.02, // 2% of biomass for maintenance
    highDensity: 0.05 // 5% of biomass for high density ponds
  };

  // Calculate feed quantity when inputs change
  useEffect(() => {
    if (biomass > 0) {
      const rate = feedingRates[strategy] || feedingRates.standard;
      const feedQty = biomass * rate;
      setCalculatedFeed(feedQty);
      if (onCalculate) {
        onCalculate(feedQty);
      }
    } else if (shrimpCount > 0) {
      // Estimate biomass from shrimp count (assuming average weight)
      // This is a simplified calculation - in reality, this would be more complex
      const avgWeight = 15; // grams (example)
      const estimatedBiomass = (shrimpCount * avgWeight) / 1000; // kg
      const rate = feedingRates[strategy] || feedingRates.standard;
      const feedQty = estimatedBiomass * rate;
      setCalculatedFeed(feedQty);
      if (onCalculate) {
        onCalculate(feedQty);
      }
    } else {
      setCalculatedFeed(0);
      if (onCalculate) {
        onCalculate(0);
      }
    }
  }, [biomass, shrimpCount, strategy, feedingRates, onCalculate]);

  const handleBiomassChange = e => {
    const value = parseFloat(e.target.value) || 0;
    setBiomass(value);
  };

  const handleShrimpCountChange = e => {
    const value = parseInt(e.target.value) || 0;
    setShrimpCount(value);
  };

  const handleStrategyChange = e => {
    setStrategy(e.target.value);
  };

  return (
    <Box sx={{ p: 2, border: '1px solid rgba(0, 0, 0, 0.12)', borderRadius: 1, mb: 2 }}>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        <Typography variant='h6' component='h3' sx={{ flexGrow: 1 }}>
          Feed Calculator
        </Typography>
        <AquacultureTooltip term='Feed Conversion Ratio (FCR)'>
          <IconButton size='small'>
            <InfoIcon fontSize='small' />
          </IconButton>
        </AquacultureTooltip>
      </Box>

      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, mb: 2 }}>
        <TextField
          label='Pond Biomass (kg)'
          type='number'
          value={biomass || ''}
          onChange={handleBiomassChange}
          InputProps={{
            endAdornment: <Typography variant='body2'>kg</Typography>
          }}
          sx={{ width: 150 }}
        />

        <TextField
          label='Shrimp Count'
          type='number'
          value={shrimpCount || ''}
          onChange={handleShrimpCountChange}
          sx={{ width: 150 }}
        />

        <FormControl sx={{ width: 150 }}>
          <InputLabel>Feeding Strategy</InputLabel>
          <Select value={strategy} label='Feeding Strategy' onChange={handleStrategyChange}>
            <MenuItem value='standard'>Standard (3%)</MenuItem>
            <MenuItem value='growth'>Growth Phase (4%)</MenuItem>
            <MenuItem value='maintenance'>Maintenance (2%)</MenuItem>
            <MenuItem value='highDensity'>High Density (5%)</MenuItem>
          </Select>
        </FormControl>
      </Box>

      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
        <Typography variant='body1'>Recommended Daily Feed:</Typography>
        <Chip label={`${calculatedFeed.toFixed(2)} kg`} color='primary' variant='outlined' />
        <Typography variant='body2' color='text.secondary'>
          (Based on {feedingRates[strategy] * 100}% of biomass)
        </Typography>
      </Box>
    </Box>
  );
};

export default FeedCalculator;

// Add PropTypes validation
FeedCalculator.propTypes = {
  onCalculate: PropTypes.func,
  initialBiomass: PropTypes.number,
  initialShrimpCount: PropTypes.number,
  feedingStrategy: PropTypes.oneOf(['standard', 'growth', 'maintenance', 'highDensity'])
};

// Add default props
FeedCalculator.defaultProps = {
  initialBiomass: 0,
  initialShrimpCount: 0,
  feedingStrategy: 'standard'
};

import { CalendarToday as CalendarIcon, TrendingUp as TrendingUpIcon } from '@mui/icons-material';
import { Card, CardContent, Typography, Box, LinearProgress, Tooltip, Chip } from '@mui/material';
import { motion } from 'framer-motion';
import PropTypes from 'prop-types';
import React from 'react';

const HarvestProjection = ({ currentWeight, targetWeight, growthRate, startDate, pondName }) => {
  // Calculate projected harvest date
  const calculateHarvestProjection = () => {
    if (!currentWeight || !targetWeight || !growthRate) return null;

    const remainingGrowth = targetWeight - currentWeight;
    const daysToHarvest = Math.ceil(remainingGrowth / growthRate);

    // Calculate projected date (simplified)
    const today = new Date();
    const projectedDate = new Date(today);
    projectedDate.setDate(today.getDate() + daysToHarvest);

    return {
      daysToHarvest,
      projectedDate: projectedDate.toLocaleDateString(),
      progress: Math.min(100, (currentWeight / targetWeight) * 100)
    };
  };

  const projection = calculateHarvestProjection();

  if (!projection) {
    return null;
  }

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.5 }}
      whileHover={{ scale: 1.02, transition: { duration: 0.2 } }}
    >
      <Card variant='outlined' sx={{ height: '100%' }}>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <CalendarIcon sx={{ color: 'primary.main', mr: 1 }} />
            <Typography variant='h6' component='h3'>
              Harvest Projection
            </Typography>
          </Box>

          <Box sx={{ mb: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
              <Typography variant='body2' color='text.secondary'>
                Progress to Harvest Size
              </Typography>
              <Typography variant='body2' fontWeight='bold'>
                {projection.progress.toFixed(1)}%
              </Typography>
            </Box>
            <LinearProgress
              variant='determinate'
              value={projection.progress}
              color='success'
              sx={{ borderRadius: 2, height: 8 }}
            />
          </Box>

          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Box>
              <Typography variant='body2' color='text.secondary'>
                Projected Harvest Date
              </Typography>
              <Typography variant='h6' component='div'>
                {projection.projectedDate}
              </Typography>
            </Box>
            <Chip
              icon={<TrendingUpIcon />}
              label={`${projection.daysToHarvest} days`}
              color='primary'
              variant='outlined'
            />
          </Box>

          <Tooltip title={`Based on current growth rate of ${growthRate}g/day`}>
            <Typography
              variant='body2'
              sx={{ mt: 2, fontStyle: 'italic', color: 'text.secondary' }}
            >
              {pondName ? `For ${pondName}: ` : ''}
              {currentWeight}g â†’ {targetWeight}g at {growthRate}g/day
            </Typography>
          </Tooltip>
        </CardContent>
      </Card>
    </motion.div>
  );
};

export default HarvestProjection;

// Add PropTypes validation
HarvestProjection.propTypes = {
  currentWeight: PropTypes.number,
  targetWeight: PropTypes.number,
  growthRate: PropTypes.number,
  startDate: PropTypes.instanceOf(Date),
  pondName: PropTypes.string
};

import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import {
  Typography,
  Paper,
  Button,
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  MenuItem
} from '@mui/material';
import React, { useState } from 'react';
import { useForm, Controller } from 'react-hook-form';

import { useSeason } from '../../../context/SeasonContext';
import { useApiData, useApiMutation } from '../../../hooks/useApi';
import {
  getEmployees,
  createEmployee,
  updateEmployee,
  deleteEmployee,
  createExpense
} from '../../../services/api';

const SalaryManagement = () => {
  const { selectedSeason } = useSeason();
  const [openEmployeeForm, setOpenEmployeeForm] = useState(false);
  const [openSalaryForm, setOpenSalaryForm] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [selectedEmployee, setSelectedEmployee] = useState(null);

  const { data: employees, loading, refetch } = useApiData(getEmployees, []);
  const { mutate: createEmployeeMutation } = useApiMutation(createEmployee);
  const { mutate: updateEmployeeMutation } = useApiMutation(updateEmployee);
  const { mutate: deleteEmployeeMutation } = useApiMutation(deleteEmployee);
  const { mutate: createExpenseMutation } = useApiMutation(createExpense);

  const {
    control: employeeControl,
    handleSubmit: handleEmployeeSubmit,
    reset: resetEmployeeForm
  } = useForm();
  const {
    control: salaryControl,
    handleSubmit: handleSalarySubmit,
    reset: resetSalaryForm
  } = useForm();

  const handleSaveEmployee = async data => {
    try {
      if (editingEmployee) {
        await updateEmployeeMutation(editingEmployee._id, data);
      } else {
        await createEmployeeMutation(data);
      }
      refetch();
      setOpenEmployeeForm(false);
      setEditingEmployee(null);
    } catch (error) {
      console.error('Failed to save employee', error);
    }
  };

  const handleDeleteEmployee = async id => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        await deleteEmployeeMutation(id);
        refetch();
      } catch (error) {
        console.error('Failed to delete employee', error);
      }
    }
  };

  const handleLogSalary = employee => {
    setSelectedEmployee(employee);
    resetSalaryForm({
      date: new Date().toISOString().split('T')[0],
      mainCategory: 'Salary',
      subCategory: 'Salary',
      employee: employee._id,
      season: selectedSeason?._id,
      description: `Salary for ${employee.name}`
    });
    setOpenSalaryForm(true);
  };

  const handleSaveSalary = async data => {
    try {
      await createExpenseMutation(data);
      setOpenSalaryForm(false);
    } catch (error) {
      console.error('Failed to log salary', error);
    }
  };

  return (
    <Paper sx={{ p: 2 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant='h6'>Salary Management</Typography>
        <Button
          variant='contained'
          startIcon={<AddIcon />}
          onClick={() => {
            setEditingEmployee(null);
            resetEmployeeForm();
            setOpenEmployeeForm(true);
          }}
        >
          Add Employee
        </Button>
      </Box>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Role</TableCell>
              <TableCell>Status</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {employees?.map(employee => (
              <TableRow key={employee._id}>
                <TableCell>{employee.name}</TableCell>
                <TableCell>{employee.role}</TableCell>
                <TableCell>{employee.status}</TableCell>
                <TableCell>
                  <Button onClick={() => handleLogSalary(employee)}>Log Salary</Button>
                  <IconButton
                    onClick={() => {
                      setEditingEmployee(employee);
                      resetEmployeeForm(employee);
                      setOpenEmployeeForm(true);
                    }}
                  >
                    <EditIcon />
                  </IconButton>
                  <IconButton onClick={() => handleDeleteEmployee(employee._id)}>
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Employee Form Dialog */}
      <Dialog open={openEmployeeForm} onClose={() => setOpenEmployeeForm(false)}>
        <DialogTitle>{editingEmployee ? 'Edit Employee' : 'Add Employee'}</DialogTitle>
        <form onSubmit={handleEmployeeSubmit(handleSaveEmployee)}>
          <DialogContent>
            <Controller
              name='name'
              control={employeeControl}
              render={({ field }) => <TextField {...field} label='Name' fullWidth margin='dense' />}
            />
            <Controller
              name='role'
              control={employeeControl}
              render={({ field }) => <TextField {...field} label='Role' fullWidth margin='dense' />}
            />
            <Controller
              name='status'
              control={employeeControl}
              defaultValue='Active'
              render={({ field }) => (
                <TextField {...field} select label='Status' fullWidth margin='dense'>
                  <MenuItem value='Active'>Active</MenuItem>
                  <MenuItem value='Inactive'>Inactive</MenuItem>
                </TextField>
              )}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenEmployeeForm(false)}>Cancel</Button>
            <Button type='submit' variant='contained'>
              Save
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      {/* Salary Form Dialog */}
      <Dialog open={openSalaryForm} onClose={() => setOpenSalaryForm(false)}>
        <DialogTitle>Log Salary for {selectedEmployee?.name}</DialogTitle>
        <form onSubmit={handleSalarySubmit(handleSaveSalary)}>
          <DialogContent>
            <Controller
              name='date'
              control={salaryControl}
              render={({ field }) => (
                <TextField
                  {...field}
                  type='date'
                  label='Date'
                  fullWidth
                  margin='dense'
                  InputLabelProps={{ shrink: true }}
                />
              )}
            />
            <Controller
              name='amount'
              control={salaryControl}
              render={({ field }) => (
                <TextField {...field} type='number' label='Amount' fullWidth margin='dense' />
              )}
            />
            <Controller
              name='description'
              control={salaryControl}
              render={({ field }) => (
                <TextField {...field} label='Description' fullWidth margin='dense' />
              )}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenSalaryForm(false)}>Cancel</Button>
            <Button type='submit' variant='contained'>
              Save Salary
            </Button>
          </DialogActions>
        </form>
      </Dialog>
    </Paper>
  );
};

export default SalaryManagement;

import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
  Alert,
  Typography
} from '@mui/material';
import { format } from 'date-fns';
import React, { useState, useEffect } from 'react';

import { useSeason } from '../../../context/SeasonContext';
import useApi from '../../../hooks/useApi';

const AdjustmentHistoryModal = ({ open, onClose, item }) => {
  const [adjustments, setAdjustments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const api = useApi();
  const { selectedSeason } = useSeason();

  useEffect(() => {
    if (open && item && selectedSeason) {
      const fetchAdjustments = async () => {
        setLoading(true);
        setError(null);
        try {
          const response = await api.get(
            `/inventory-items/${item._id}/adjustments?seasonId=${selectedSeason._id}`
          );
          setAdjustments(response.data);
        } catch (err) {
          console.error('Error fetching adjustment history:', err);
          setError('Failed to fetch adjustment history.');
        }
        setLoading(false);
      };
      fetchAdjustments();
    } else if (open && !selectedSeason) {
      // If no season is selected, clear the adjustments
      setAdjustments([]);
      setLoading(false);
      setError('Please select a season to view adjustment history.');
    }
  }, [open, item, api, selectedSeason]);

  return (
    <Dialog open={open} onClose={onClose} fullWidth maxWidth='md'>
      <DialogTitle>Adjustment History for {item?.itemName}</DialogTitle>
      <DialogContent>
        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity='error'>{error}</Alert>
        ) : adjustments.length === 0 ? (
          <Typography>No adjustment history found for this item.</Typography>
        ) : (
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Date</TableCell>
                  <TableCell>Type</TableCell>
                  <TableCell align='right'>Quantity Change</TableCell>
                  <TableCell>Reason</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {adjustments.map(adj => (
                  <TableRow key={adj._id}>
                    <TableCell>{format(new Date(adj.createdAt), 'yyyy-MM-dd HH:mm')}</TableCell>
                    <TableCell>{adj.adjustmentType}</TableCell>
                    <TableCell align='right'>{adj.quantityChange}</TableCell>
                    <TableCell>{adj.reason}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Close</Button>
      </DialogActions>
    </Dialog>
  );
};

export default AdjustmentHistoryModal;

import {
  Paper,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  CircularProgress,
  Alert,
  Typography,
  Box
} from '@mui/material';
import React, { useState, useEffect, useMemo } from 'react';
import { useTranslation } from 'react-i18next';

import { useSeason } from '../../../context/SeasonContext';
import useApi from '../../../hooks/useApi';

const CurrentStockView = () => {
  const { t } = useTranslation();
  const { selectedSeason } = useSeason();
  const [stockItems, setStockItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const api = useApi();

  useEffect(() => {
    const fetchStockItems = async () => {
      if (!selectedSeason || !selectedSeason._id) {
        setStockItems([]);
        setLoading(false);
        return;
      }
      setLoading(true);
      try {
        const response = await api.get(`/inventory-items/stock?seasonId=${selectedSeason._id}`);
        setStockItems(Array.isArray(response) ? response : response.data || []);
      } catch (err) {
        console.error('Error fetching stock items:', err);
        setError(t('failed_to_fetch_stock_items'));
      } finally {
        setLoading(false);
      }
    };

    fetchStockItems();
  }, [selectedSeason, api, t]);

  const getItemStatus = item => {
    const currentQty = item.currentQuantity;
    if (currentQty <= 0) return t('out_of_stock');
    if (item.lowStockThreshold && currentQty <= item.lowStockThreshold) return t('low_stock');
    return t('in_stock');
  };

  if (loading) {
    return (
      <Box display='flex' justifyContent='center' mt={5}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert severity='error' sx={{ mt: 2 }}>
        {error}
      </Alert>
    );
  }

  return (
    <Paper>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>{t('itemName')}</TableCell>
              <TableCell>{t('itemType')}</TableCell>
              <TableCell align='right'>{t('current_quantity')}</TableCell>
              <TableCell>{t('status')}</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {stockItems.length === 0 ? (
              <TableRow>
                <TableCell colSpan={5} align='center'>
                  {t('no_stock_items_found')}
                </TableCell>
              </TableRow>
            ) : (
              stockItems.map(item => (
                <TableRow key={item._id} sx={{ '&:last-child td, &:last-child th': { border: 0 } }}>
                  <TableCell component='th' scope='row'>
                    {item.itemName.en || item.itemName}
                  </TableCell>
                  <TableCell>{item.itemType}</TableCell>
                  <TableCell align='right'>{item.currentQuantity}</TableCell>
                  <TableCell align='right'>{item.lowStockThreshold}</TableCell>
                  <TableCell>
                    <Typography
                      variant='body2'
                      sx={{
                        color:
                          getItemStatus(item) === t('low_stock')
                            ? 'orange'
                            : getItemStatus(item) === t('out_of_stock')
                            ? 'red'
                            : 'green',
                        fontWeight: 'bold'
                      }}
                    >
                      {getItemStatus(item)}
                    </Typography>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
};

export default CurrentStockView;

import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  Typography,
  Box,
  Alert
} from '@mui/material';
import PropTypes from 'prop-types';
import React, { useState, useEffect } from 'react';

import { useSeason } from '../context/SeasonContext';
import useApi from '../hooks/useApi';
import { useErrorHandler } from '../utils/errorHandling';

import { InlineError } from './ErrorDisplay';

const InventoryAdjustmentModal = ({ open, onClose, item }) => {
  const api = useApi();
  const { selectedSeason } = useSeason();
  const { handleError } = useErrorHandler();
  const [adjustmentQuantity, setAdjustmentQuantity] = useState('');
  const [reason, setReason] = useState('');
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (!open) {
      setAdjustmentQuantity('');
      setReason('');
      setError(null);
    }
  }, [open]);

  const handleQuantityChange = e => {
    setAdjustmentQuantity(e.target.value);
    setError(null);
  };

  const handleReasonChange = e => {
    setReason(e.target.value);
    setError(null);
  };

  const handleSubmit = async () => {
    if (!selectedSeason) {
      setError(new Error('Please select a season before making inventory adjustments.'));
      return;
    }

    if (!adjustmentQuantity || isNaN(adjustmentQuantity) || parseFloat(adjustmentQuantity) === 0) {
      setError(new Error('Please enter a valid non-zero quantity for adjustment.'));
      return;
    }
    if (!reason.trim()) {
      setError(new Error('Please provide a reason for the adjustment.'));
      return;
    }

    setIsSubmitting(true);
    setError(null);

    try {
      // Using the new inventory adjustment endpoint with season parameter
      await api.post(`/inventory-items/adjustments?seasonId=${selectedSeason._id}`, {
        inventoryItemId: item._id,
        // For backward compatibility, we'll use quantityChange instead of quantity
        quantityChange: parseFloat(adjustmentQuantity),
        // We'll assume a default adjustment type, or you could add a dropdown for this
        adjustmentType: parseFloat(adjustmentQuantity) > 0 ? 'Purchase' : 'Manual Adjustment',
        reason: reason
      });

      onClose();
    } catch (err) {
      console.error('Error making inventory adjustment:', err);
      setError(err);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Dialog open={open} onClose={onClose} fullWidth maxWidth='sm'>
      <DialogTitle>Adjust Inventory for {item?.itemName}</DialogTitle>
      <DialogContent>
        {selectedSeason ? (
          <>
            <Typography variant='body1' sx={{ mb: 2 }}>
              Current Quantity: {item?.currentQuantity} {item?.unit}
            </Typography>
            <TextField
              margin='dense'
              label='Adjustment Quantity (e.g., -5 for deduction, 10 for addition)'
              type='number'
              fullWidth
              value={adjustmentQuantity}
              onChange={handleQuantityChange}
              error={!!error && !selectedSeason}
              helperText={error && !selectedSeason ? error : ''}
            />
            <TextField
              margin='dense'
              label='Reason for Adjustment'
              type='text'
              fullWidth
              multiline
              rows={3}
              value={reason}
              onChange={handleReasonChange}
              sx={{ mt: 2 }}
            />
            {error && selectedSeason && (
              <InlineError error={error} onRetry={() => setError(null)} sx={{ mt: 2 }} />
            )}
          </>
        ) : (
          <Alert severity='warning'>
            Please select a season before making inventory adjustments.
          </Alert>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} disabled={isSubmitting}>
          Cancel
        </Button>
        <Button
          onClick={handleSubmit}
          variant='contained'
          disabled={!selectedSeason || isSubmitting}
        >
