import ErrorIcon from '@mui/icons-material/Error';
import HomeIcon from '@mui/icons-material/Home';
import NetworkIcon from '@mui/icons-material/NetworkCheck';
import RefreshIcon from '@mui/icons-material/Refresh';
import WarningIcon from '@mui/icons-material/Warning';
import {
  Box,
  Alert,
  AlertTitle,
  Button,
  Typography,
  Stack,
  Collapse,
  IconButton,
  Card,
  CardContent
} from '@mui/material';
import PropTypes from 'prop-types';
import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';

import { API } from '../../../../constants';
import logger from '../../../../utils/logger';
import { safeExecute, safeNavigateHome } from '../../../../utils/safeNavigation';

const MAX_RETRIES = API.MAX_RETRIES;

const HTTP_STATUS_BAD_REQUEST = API.STATUS.BAD_REQUEST;
const HTTP_STATUS_UNAUTHORIZED = API.STATUS.UNAUTHORIZED;
const HTTP_STATUS_FORBIDDEN = API.STATUS.FORBIDDEN;
const HTTP_STATUS_NOT_FOUND = API.STATUS.NOT_FOUND;
const HTTP_STATUS_INTERNAL_SERVER_ERROR = API.STATUS.INTERNAL_SERVER_ERROR;
const HTTP_STATUS_REQUEST_TIMEOUT = 408;
const HTTP_STATUS_TOO_MANY_REQUESTS = 429;
const HTTP_STATUS_BAD_GATEWAY = 502;
const HTTP_STATUS_SERVICE_UNAVAILABLE = 503;

const ApiErrorHandler = ({
  error,
  onRetry,
  onGoHome,
  showRetry = true,
  showDetails = false,
  variant = 'card',
  maxRetries = MAX_RETRIES,
  retryCount = 0
}) => {
  const { t } = useTranslation();
  const [showErrorDetails, setShowErrorDetails] = useState(false);

  if (!error) return null;

  // Safe handler for retry actions
  const handleSafeRetry = () => {
    safeExecute(
      () => onRetry(),
      () => {
        logger.warn('Retry function not available or failed');
        // Show user that retry failed
        alert(t('retry_failed', 'Retry failed. Please refresh the page.'));
      },
      'API retry failed'
    );
  };

  // Safe handler for navigation actions
  const handleSafeGoHome = () => {
    safeExecute(
      () => onGoHome(),
      () => safeNavigateHome(),
      'API error navigation failed'
    );
  };

  const getErrorSeverity = () => {
    const status = error.status || error.response?.status;

    if (status >= HTTP_STATUS_INTERNAL_SERVER_ERROR) return 'error';
    if (status >= HTTP_STATUS_BAD_REQUEST) return 'warning';
    if (error.type === 'NETWORK_ERROR') return 'warning';
    return 'error';
  };

  const getErrorIcon = () => {
    const status = error.status || error.response?.status;
    const type = error.type;

    if (type === 'NETWORK_ERROR' || !navigator.onLine) {
      return <NetworkIcon />;
    }

    if (status >= HTTP_STATUS_INTERNAL_SERVER_ERROR) {
      return <ErrorIcon />;
    }

    return <WarningIcon />;
  };

  const getErrorTitle = () => {
    const status = error.status || error.response?.status;
    const type = error.type;

    if (type === 'NETWORK_ERROR' || !navigator.onLine) {
      return t('api_error_network_title', 'Connection Problem');
    }

    switch (status) {
      case HTTP_STATUS_BAD_REQUEST:
        return t('api_error_400_title', 'Invalid Request');
      case HTTP_STATUS_UNAUTHORIZED:
        return t('api_error_401_title', 'Authentication Required');
      case HTTP_STATUS_FORBIDDEN:
        return t('api_error_403_title', 'Access Denied');
      case HTTP_STATUS_NOT_FOUND:
        return t('api_error_404_title', 'Not Found');
      case HTTP_STATUS_REQUEST_TIMEOUT:
        return t('api_error_408_title', 'Request Timeout');
      case HTTP_STATUS_TOO_MANY_REQUESTS:
        return t('api_error_429_title', 'Too Many Requests');
      case HTTP_STATUS_INTERNAL_SERVER_ERROR:
        return t('api_error_500_title', 'Server Error');
      case HTTP_STATUS_BAD_GATEWAY:
        return t('api_error_502_title', 'Bad Gateway');
      case HTTP_STATUS_SERVICE_UNAVAILABLE:
        return t('api_error_503_title', 'Service Unavailable');
      default:
        return t('api_error_generic_title', 'Something went wrong');
    }
  };

  const getErrorMessage = () => {
    const status = error.status || error.response?.status;
    const type = error.type;

    if (type === 'NETWORK_ERROR' || !navigator.onLine) {
      return t('api_error_network_message', 'Please check your internet connection and try again.');
    }

    switch (status) {
      case HTTP_STATUS_BAD_REQUEST:
        return t(
          'api_error_400_message',
          'The request was invalid. Please check your input and try again.'
        );
      case HTTP_STATUS_UNAUTHORIZED:
        return t('api_error_401_message', 'Please log in to continue.');
      case HTTP_STATUS_FORBIDDEN:
        return t('api_error_403_message', "You don't have permission to perform this action.");
      case HTTP_STATUS_NOT_FOUND:
        return t('api_error_404_message', 'The requested resource was not found.');
      case HTTP_STATUS_REQUEST_TIMEOUT:
        return t('api_error_408_message', 'The request took too long. Please try again.');
      case HTTP_STATUS_TOO_MANY_REQUESTS:
        return t('api_error_429_message', 'Too many requests. Please wait a moment and try again.');
      case HTTP_STATUS_INTERNAL_SERVER_ERROR:
        return t(
          'api_error_500_message',
          "There's a problem with our server. We're working to fix it."
        );
      case HTTP_STATUS_BAD_GATEWAY:
        return t(
          'api_error_502_message',
          "There's a problem connecting to our service. Please try again."
        );
      case HTTP_STATUS_SERVICE_UNAVAILABLE:
        return t(
          'api_error_503_message',
          'Our service is temporarily unavailable. Please try again later.'
        );
      default:
        return (
          error.message ||
          t('api_error_generic_message', 'An unexpected error occurred. Please try again.')
        );
    }
  };

  const canRetry = () => {
    const status = error.status || error.response?.status;
    const type = error.type;

    // Don't retry client errors (4xx) except for timeout and network issues
    if (
      status >= HTTP_STATUS_BAD_REQUEST &&
      status < HTTP_STATUS_INTERNAL_SERVER_ERROR &&
      status !== HTTP_STATUS_REQUEST_TIMEOUT &&
      type !== 'NETWORK_ERROR'
    ) {
      return false;
    }

    return retryCount < maxRetries && showRetry && onRetry;
  };

  const handleToggleDetails = () => {
    setShowErrorDetails(!showErrorDetails);
  };

  const renderContent = () => (
    <Box>
      <Stack spacing={2}>
        {/* Main Error Display */}
        <Alert
          severity={getErrorSeverity()}
          icon={getErrorIcon()}
          action={
            canRetry() ? (
              <Button
                color='inherit'
                size='small'
                startIcon={<RefreshIcon />}
                onClick={handleSafeRetry}
                disabled={retryCount >= maxRetries}
              >
                {t('retry_button', 'Retry')}
              </Button>
            ) : null
          }
        >
          <AlertTitle>{getErrorTitle()}</AlertTitle>
          {getErrorMessage()}

          {/* Retry Information */}
          {retryCount > 0 && (
            <Typography variant='body2' sx={{ mt: 1, opacity: 0.8 }}>
              {t('api_error_retry_info', 'Attempted {{count}} time(s)', { count: retryCount })}
            </Typography>
          )}
        </Alert>

        {/* Action Buttons */}
        {(canRetry() || onGoHome) && (
          <Stack direction='row' spacing={1} justifyContent='flex-end'>
            {canRetry() && (
              <Button
                variant='outlined'
                size='small'
                startIcon={<RefreshIcon />}
                onClick={handleSafeRetry}
                disabled={retryCount >= maxRetries}
              >
                {retryCount >= maxRetries
                  ? t('max_retries_reached', 'Max retries reached')
                  : t('retry_button', 'Retry')}
              </Button>
            )}

            {onGoHome && (
              <Button variant='text' size='small' onClick={handleSafeGoHome} color='primary'>
                {t('go_to_dashboard', 'Go to Dashboard')}
              </Button>
            )}
          </Stack>
        )}

        {/* Error Details (Development) */}
        {showDetails && error && (
          <Box>
            <Button size='small' onClick={handleToggleDetails} color='inherit'>
              {showErrorDetails
                ? t('hide_error_details', 'Hide Details')
                : t('show_error_details', 'Show Details')}
            </Button>

            <Collapse in={showErrorDetails}>
              <Alert severity='info' sx={{ mt: 1 }}>
                <Typography variant='body2' component='div'>
                  <strong>Status:</strong> {error.status || 'Unknown'}
                  <br />
                  <strong>Type:</strong> {error.type || 'Unknown'}
                  <br />
                  <strong>Message:</strong> {error.message || 'No message'}
                  <br />
                  {error.retries !== undefined && (
                    <>
                      <strong>Retries:</strong> {error.retries}
                      <br />
                    </>
                  )}
                </Typography>
              </Alert>
            </Collapse>
          </Box>
        )}
      </Stack>
    </Box>
  );

  if (variant === 'card') {
    return (
      <Card variant='outlined' sx={{ my: 2 }}>
        <CardContent>{renderContent()}</CardContent>
      </Card>
    );
  }

  return renderContent();
};

ApiErrorHandler.propTypes = {
  error: PropTypes.shape({
    message: PropTypes.string,
    status: PropTypes.number,
    type: PropTypes.string,
    retries: PropTypes.number,
    response: PropTypes.object
  }),
  onRetry: PropTypes.func,
  onGoHome: PropTypes.func,
  showRetry: PropTypes.bool,
  showDetails: PropTypes.bool,
  variant: PropTypes.oneOf(['card', 'inline']),
  maxRetries: PropTypes.number,
  retryCount: PropTypes.number
};

export default ApiErrorHandler;

/**
 * Enhanced Error Boundary with Debug Integration
 * Provides comprehensive error handling with debug context and recovery options
 */

import {
  ErrorOutline,
  Refresh,
  ExpandMore,
  BugReport,
  Download,
  Visibility,
  VisibilityOff,
  ContentCopy
} from '@mui/icons-material';
import {
  Box,
  Container,
  Typography,
  Button,
  Alert,
  AlertTitle,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Chip,
  Paper,
  List,
  ListItem,
  ListItemText,
  Divider,
  IconButton,
  Tooltip
} from '@mui/material';
import React, { Component } from 'react';

import { ErrorContextEnhancer, debugStore } from '../../../utils/debugUtils';
import logger from '../../../utils/logger';

class EnhancedErrorBoundary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
      errorId: null,
      enhancedError: null,
      showDebugDetails: false,
      showTechnicalDetails: false
    };
  }

  static getDerivedStateFromError(error) {
    return {
      hasError: true,
      errorId: `error_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
    };
  }

  componentDidCatch(error, errorInfo) {
    const { onError, context = {} } = this.props;

    // Enhance error with debug context
    const enhancedError = ErrorContextEnhancer.enhanceError(error, {
      ...context,
      errorBoundary: true,
      componentStack: errorInfo.componentStack
    });

    this.setState({
      error,
      errorInfo,
      enhancedError
    });

    // Log to our logging system
    logger.error(
      'Error Boundary caught an error',
      {
        errorId: this.state.errorId,
        component: context.component || 'Unknown',
        errorBoundary: true
      },
      enhancedError
    );

    // Call custom error handler
    if (onError) {
      onError(enhancedError, errorInfo, this.state.errorId);
    }

    // Report to external service in production
    if (process.env.NODE_ENV === 'production' && process.env.REACT_APP_ERROR_REPORTING_ENABLED) {
      this.reportToExternalService(enhancedError);
    }
  }

  reportToExternalService = error => {
    // Implement external error reporting (e.g., Sentry, LogRocket, etc.)
    // This is a placeholder for the actual implementation
    console.log('Would report to external service:', error);
  };

  handleReset = () => {
    this.setState({
      hasError: false,
      error: null,
      errorInfo: null,
      errorId: null,
      enhancedError: null,
      showDebugDetails: false,
      showTechnicalDetails: false
    });

    if (this.props.onReset) {
      this.props.onReset();
    }
  };

  handleRefresh = () => {
    window.location.reload();
  };

  handleDownloadDebugData = () => {
    const debugData = {
      errorId: this.state.errorId,
      timestamp: new Date().toISOString(),
      error: {
        name: this.state.error?.name,
        message: this.state.error?.message,
        stack: this.state.error?.stack
      },
      errorInfo: this.state.errorInfo,
      debugContext: this.state.enhancedError?.debugContext,
      userAgent: navigator.userAgent,
      url: window.location.href,
      debugStore: {
        recentLogs: debugStore.getEntries({ limit: 50 }),
        componentStates: Object.fromEntries(debugStore.componentStates),
        renderCounts: Object.fromEntries(debugStore.renderCounts),
        userActions: debugStore.userActions.slice(-10),
        networkCalls: debugStore.networkCalls.slice(-5)
      }
    };

    const blob = new Blob([JSON.stringify(debugData, null, 2)], {
      type: 'application/json'
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `error-debug-${this.state.errorId}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  handleCopyErrorId = () => {
    navigator.clipboard.writeText(this.state.errorId);
  };

  toggleDebugDetails = () => {
    this.setState(prev => ({
      showDebugDetails: !prev.showDebugDetails
    }));
  };

  toggleTechnicalDetails = () => {
    this.setState(prev => ({
      showTechnicalDetails: !prev.showTechnicalDetails
    }));
  };

  renderErrorSummary() {
    const { error, errorId, enhancedError } = this.state;
    const debugContext = enhancedError?.debugContext;

    return (
      <Alert severity='error' sx={{ mb: 3, textAlign: 'left' }}>
        <AlertTitle>
          Application Error
          <Chip
            label={`ID: ${errorId}`}
            size='small'
            sx={{ ml: 2 }}
            onClick={this.handleCopyErrorId}
            icon={<ContentCopy fontSize='small' />}
          />
        </AlertTitle>

        <Typography variant='body2' sx={{ mt: 1 }}>
          <strong>Error:</strong> {error?.message || 'Unknown error occurred'}
        </Typography>

        {debugContext?.componentContext?.component && (
          <Typography variant='body2'>
            <strong>Component:</strong> {debugContext.componentContext.component}
          </Typography>
        )}

        <Typography variant='body2'>
          <strong>Time:</strong> {new Date(debugContext?.timestamp || Date.now()).toLocaleString()}
        </Typography>
      </Alert>
    );
  }

  renderRecoveryActions() {
    const { recoveryActions = [] } = this.props;

    return (
      <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap', justifyContent: 'center', mb: 3 }}>
        <Button
          variant='contained'
          color='primary'
          startIcon={<Refresh />}
          onClick={this.handleReset}
          size='large'
        >
          Try Again
        </Button>

        <Button variant='outlined' color='primary' onClick={this.handleRefresh} size='large'>
          Refresh Page
        </Button>

        {recoveryActions.map((action, index) => (
          <Button
            key={index}
            variant={action.variant || 'outlined'}
            color={action.color || 'secondary'}
            onClick={action.handler}
            startIcon={action.icon}
          >
            {action.label}
          </Button>
        ))}

        {process.env.NODE_ENV === 'development' && (
          <Button
            variant='outlined'
            color='info'
            startIcon={<Download />}
            onClick={this.handleDownloadDebugData}
          >
            Download Debug Data
          </Button>
        )}
      </Box>
    );
  }

  renderDebugDetails() {
    const { enhancedError, showDebugDetails } = this.state;
    const debugContext = enhancedError?.debugContext;

    if (!debugContext || process.env.NODE_ENV !== 'development') {
      return null;
    }

    return (
      <Accordion expanded={showDebugDetails} onChange={this.toggleDebugDetails}>
        <AccordionSummary expandIcon={<ExpandMore />}>
          <Typography variant='h6' sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <BugReport color='info' />
            Debug Information
          </Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Box sx={{ textAlign: 'left' }}>
            {/* Recent User Actions */}
            {debugContext.recentUserActions?.length > 0 && (
              <Paper sx={{ p: 2, mb: 2 }}>
                <Typography variant='subtitle2' gutterBottom>
                  Recent User Actions
                </Typography>
                <List dense>
                  {debugContext.recentUserActions.slice(-5).map((action, index) => (
                    <ListItem key={index}>
                      <ListItemText
                        primary={`${action.data.action} in ${action.component}`}
                        secondary={new Date(action.data.timestamp).toLocaleTimeString()}
                      />
                    </ListItem>
                  ))}
                </List>
              </Paper>
            )}

            {/* Component States */}
            {Object.keys(debugContext.componentStates || {}).length > 0 && (
              <Paper sx={{ p: 2, mb: 2 }}>
                <Typography variant='subtitle2' gutterBottom>
                  Component States
                </Typography>
                {Object.entries(debugContext.componentStates).map(([component, state]) => (
                  <Box key={component} sx={{ mb: 1 }}>
                    <Typography variant='caption' color='text.secondary'>
                      {component}
                    </Typography>
                    <pre style={{ fontSize: '0.8em', margin: 0, overflow: 'auto' }}>
                      {JSON.stringify(state, null, 2)}
                    </pre>
                  </Box>
                ))}
              </Paper>
            )}

            {/* Render Counts */}
            {Object.keys(debugContext.renderCounts || {}).length > 0 && (
              <Paper sx={{ p: 2, mb: 2 }}>
                <Typography variant='subtitle2' gutterBottom>
                  Render Counts
                </Typography>
                <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                  {Object.entries(debugContext.renderCounts).map(([component, count]) => (
                    <Chip
                      key={component}
                      label={`${component}: ${count}`}
                      size='small'
                      variant='outlined'
                    />
                  ))}
                </Box>
              </Paper>
            )}

            {/* Recent Network Calls */}
            {debugContext.recentNetworkCalls?.length > 0 && (
              <Paper sx={{ p: 2, mb: 2 }}>
                <Typography variant='subtitle2' gutterBottom>
                  Recent Network Calls
                </Typography>
                <List dense>
                  {debugContext.recentNetworkCalls.map((call, index) => (
                    <ListItem key={index}>
                      <ListItemText
                        primary={`${call.method || 'GET'} ${call.url}`}
                        secondary={
                          <Box>
                            <Typography variant='caption'>
                              Status: {call.status} | Duration: {call.duration?.toFixed(2)}ms
                            </Typography>
                            {call.error && (
                              <Typography variant='caption' color='error' display='block'>
                                Error: {call.error}
                              </Typography>
                            )}
                          </Box>
                        }
                      />
                    </ListItem>
                  ))}
                </List>
              </Paper>
            )}
          </Box>
        </AccordionDetails>
      </Accordion>
    );
  }

  renderTechnicalDetails() {
    const { error, errorInfo, showTechnicalDetails } = this.state;

    if (process.env.NODE_ENV !== 'development') {
      return null;
    }

    return (
      <Accordion expanded={showTechnicalDetails} onChange={this.toggleTechnicalDetails}>
        <AccordionSummary expandIcon={<ExpandMore />}>
          <Typography variant='h6'>Technical Details</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Box sx={{ textAlign: 'left' }}>
            <Typography variant='subtitle2' gutterBottom>
              Error Message:
            </Typography>
            <Paper sx={{ p: 2, mb: 2, bgcolor: 'grey.100' }}>
              <Typography variant='body2' component='pre' sx={{ whiteSpace: 'pre-wrap' }}>
                {error?.toString()}
              </Typography>
            </Paper>

            <Typography variant='subtitle2' gutterBottom>
              Component Stack:
            </Typography>
            <Paper sx={{ p: 2, mb: 2, bgcolor: 'grey.100' }}>
              <Typography
                variant='body2'
                component='pre'
                sx={{ whiteSpace: 'pre-wrap', fontSize: '0.8em' }}
              >
                {errorInfo?.componentStack}
              </Typography>
            </Paper>

            <Typography variant='subtitle2' gutterBottom>
              Error Stack:
            </Typography>
            <Paper sx={{ p: 2, mb: 2, bgcolor: 'grey.100' }}>
              <Typography
                variant='body2'
                component='pre'
                sx={{ whiteSpace: 'pre-wrap', fontSize: '0.8em' }}
              >
                {error?.stack}
              </Typography>
            </Paper>
          </Box>
        </AccordionDetails>
      </Accordion>
    );
  }

  render() {
    if (this.state.hasError) {
      const { fallback: FallbackComponent, title, message } = this.props;

      // If a custom fallback component is provided, render it
      if (FallbackComponent) {
        return (
          <FallbackComponent
            error={this.state.enhancedError || this.state.error}
            errorInfo={this.state.errorInfo}
            resetError={this.handleReset}
            refreshPage={this.handleRefresh}
            errorId={this.state.errorId}
          />
        );
      }

      // Default enhanced fallback UI
      return (
        <Container maxWidth='md' sx={{ mt: 4, mb: 4 }}>
          <Box display='flex' flexDirection='column' alignItems='center' textAlign='center' gap={3}>
            <ErrorOutline
              sx={{
                fontSize: 80,
                color: 'error.main',
                mb: 2
              }}
            />

            <Typography variant='h4' component='h1' gutterBottom>
              {title || 'Oops! Something went wrong'}
            </Typography>

            <Typography variant='h6' color='text.secondary' paragraph>
              {message ||
                "We encountered an unexpected error. Don't worry, we're working to fix it."}
            </Typography>

            {this.renderErrorSummary()}
            {this.renderRecoveryActions()}
            {this.renderDebugDetails()}
            {this.renderTechnicalDetails()}

            <Divider sx={{ width: '100%', my: 2 }} />

            <Typography variant='body2' color='text.secondary'>
              If this problem persists, please contact support with Error ID: {this.state.errorId}
            </Typography>
          </Box>
        </Container>
      );
    }

    return this.props.children;
  }
}

