import { Warning, Refresh, ExpandMore, ExpandLess } from '@mui/icons-material';
import { Box, Typography, Button, Alert, Collapse, IconButton } from '@mui/material';
import React from 'react';

/**
 * Section Error Boundary Component
 * A smaller error boundary for specific sections or components
 * Displays a compact error message without taking over the entire page
 */
class SectionErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
      showDetails: false,
      errorId: null
    };
  }

  static getDerivedStateFromError(error) {
    return {
      hasError: true,
      errorId: Date.now().toString()
    };
  }

  componentDidCatch(error, errorInfo) {
    this.setState({
      error,
      errorInfo
    });

    // Log error details
    if (process.env.NODE_ENV === 'development') {
      console.error('Section Error Boundary caught an error:', {
        section: this.props.section || 'Unknown',
        error,
        errorInfo,
        errorId: this.state.errorId
      });
    }
  }

  handleReset = () => {
    this.setState({
      hasError: false,
      error: null,
      errorInfo: null,
      showDetails: false,
      errorId: null
    });

    // Call optional onReset callback
    if (this.props.onReset) {
      this.props.onReset();
    }
  };

  toggleDetails = () => {
    this.setState(prev => ({
      showDetails: !prev.showDetails
    }));
  };

  render() {
    if (this.state.hasError) {
      const { section = 'Section' } = this.props;

      return (
        <Box sx={{ p: 2, border: 1, borderColor: 'error.main', borderRadius: 1 }}>
          <Alert
            severity='error'
            action={
              <Button
                size='small'
                startIcon={<Refresh />}
                onClick={this.handleReset}
                variant='outlined'
                color='error'
              >
                Retry
              </Button>
            }
          >
            <Typography variant='body2' component='div'>
              <strong>Error in {section}</strong>
              <br />
              Something went wrong while loading this section.
            </Typography>
          </Alert>

          {process.env.NODE_ENV === 'development' && (
            <Box sx={{ mt: 1 }}>
              <Button
                size='small'
                onClick={this.toggleDetails}
                startIcon={this.state.showDetails ? <ExpandLess /> : <ExpandMore />}
                sx={{ textTransform: 'none' }}
              >
                {this.state.showDetails ? 'Hide' : 'Show'} Error Details
              </Button>

              <Collapse in={this.state.showDetails}>
                <Box sx={{ mt: 1, p: 1, bgcolor: 'grey.100', borderRadius: 1 }}>
                  <Typography variant='caption' component='div'>
                    <strong>Error ID:</strong> {this.state.errorId}
                    <br />
                    <strong>Error:</strong> {this.state.error?.toString()}
                    <br />
                    <strong>Component Stack:</strong>
                    <pre
                      style={{
                        whiteSpace: 'pre-wrap',
                        fontSize: '0.7em',
                        margin: '4px 0',
                        overflow: 'auto',
                        maxHeight: '200px'
                      }}
                    >
                      {this.state.errorInfo?.componentStack}
                    </pre>
                  </Typography>
                </Box>
              </Collapse>
            </Box>
          )}
        </Box>
      );
    }

    return this.props.children;
  }
}

export default SectionErrorBoundary;

import { Card, CardContent, CardHeader, Box, ToggleButton, ToggleButtonGroup } from '@mui/material';
import moment from 'moment';
import React, { useMemo } from 'react';
import { Calendar, momentLocalizer, Views } from 'react-big-calendar';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './CalendarOverrides.css';
import {
  CalendarToday as DayViewIcon,
  ViewWeek as WeekViewIcon,
  CalendarMonth as MonthViewIcon
} from '@mui/icons-material';

// Set up calendar localizer
const localizer = momentLocalizer(moment);

const CustomCalendar = ({
  events = [],
  onEventSelect,
  onDateChange,
  onRangeChange,
  date,
  view = Views.WEEK,
  onViewChange,
  toolbar = true
}) => {
  // Event styles for calendar
  const eventStyleGetter = event => {
    let backgroundColor = '#007BFF';
    let className = 'rbc-event-routine';

    if (event.type === 'Routine') {
      backgroundColor = '#007BFF';
      className = 'rbc-event-routine';
    }
    if (event.type === 'Monitoring') {
      backgroundColor = '#28A745';
      className = 'rbc-event-monitoring';
    }
    if (event.type === 'Maintenance') {
      backgroundColor = '#FD7E14';
      className = 'rbc-event-maintenance';
    }
    if (event.type === 'Feeding') {
      backgroundColor = '#007BFF';
      className = 'rbc-event-feeding';
    }
    if (event.type === 'Water Quality') {
      backgroundColor = '#28A745';
      className = 'rbc-event-water-quality';
    }
    if (event.type === 'Growth Sampling') {
      backgroundColor = '#6f42c1';
      className = 'rbc-event-growth-sampling';
    }

    const style = {
      backgroundColor,
      borderRadius: '4px',
      opacity: 0.9,
      color: 'white',
      border: '0px',
      display: 'block'
    };

    return {
      style,
      className
    };
  };

  // Map MUI view names to react-big-calendar view names
  const getViewValue = viewName => {
    switch (viewName) {
      case 'day':
        return Views.DAY;
      case 'month':
        return Views.MONTH;
      default:
        return Views.WEEK;
    }
  };

  // Map react-big-calendar view names to MUI view names
  const getMuiViewName = viewValue => {
    switch (viewValue) {
      case Views.DAY:
        return 'day';
      case Views.MONTH:
        return 'month';
      default:
        return 'week';
    }
  };

  const processedEvents = useMemo(() => {
    return events.map(event => ({
      ...event,
      start: new Date(event.start),
      end: new Date(event.end)
    }));
  }, [events]);

  return (
    <Card elevation={3} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <CardHeader
        title='Events Calendar'
        action={
          <ToggleButtonGroup
            value={getMuiViewName(view)}
            exclusive
            onChange={(event, newView) => {
              if (newView !== null && onViewChange) {
                onViewChange(getViewValue(newView));
              }
            }}
            size='small'
            sx={{ height: 36 }}
          >
            <ToggleButton value='month' aria-label='month view'>
              <MonthViewIcon />
            </ToggleButton>
            <ToggleButton value='week' aria-label='week view'>
              <WeekViewIcon />
            </ToggleButton>
            <ToggleButton value='day' aria-label='day view'>
              <DayViewIcon />
            </ToggleButton>
          </ToggleButtonGroup>
        }
      />
      <CardContent sx={{ flexGrow: 1, p: { xs: 1, sm: 2 }, height: '100%' }}>
        <Box
          sx={{
            height: '100%',
            minHeight: { xs: 400, sm: 500, md: 600 },
            '& .rbc-calendar': {
              height: '100%'
            }
          }}
        >
          <Calendar
            localizer={localizer}
            events={processedEvents}
            startAccessor='start'
            endAccessor='end'
            style={{ height: '100%' }}
            onSelectEvent={onEventSelect}
            onNavigate={onDateChange}
            onView={onViewChange}
            onRangeChange={onRangeChange}
            eventPropGetter={eventStyleGetter}
            views={[Views.MONTH, Views.WEEK, Views.DAY]}
            view={view}
            date={date}
            toolbar={false} // We're using our own toolbar
            formats={{
              dateFormat: 'd',
              dayFormat: 'ddd D/M',
              timeGutterFormat: 'HH:mm',
              eventTimeRangeFormat: ({ start, end }) =>
                `${moment(start).format('HH:mm')} - ${moment(end).format('HH:mm')}`
            }}
          />
        </Box>
      </CardContent>
    </Card>
  );
};

export default CustomCalendar;

import { TextField, FormHelperText, Box } from '@mui/material';
import PropTypes from 'prop-types';
import React from 'react';

/**
 * ValidatedDatePicker - A date input component with built-in validation
 * Note: Using TextField with type="date" for simplicity
 * In production, consider using @mui/x-date-pickers for better functionality
 */
const ValidatedDatePicker = ({
  name,
  value,
  onChange,
  onBlur,
  errors = [],
  touched = false,
  label,
  required = false,
  disabled = false,
  fullWidth = true,
  variant = 'outlined',
  size = 'medium',
  minDate,
  maxDate,
  helperText,
  ...props
}) => {
  const hasError = touched && errors.length > 0;
  const displayErrors = hasError ? errors : [];

  // Format date value for input
  const formatDateForInput = date => {
    if (!date) return '';

    const dateObj = new Date(date);
    if (isNaN(dateObj.getTime())) return '';

    return dateObj.toISOString().split('T')[0];
  };

  // Handle date change
  const handleDateChange = event => {
    const dateValue = event.target.value;
    if (onChange) {
      // Create a proper event object for consistency
      const syntheticEvent = {
        target: {
          name,
          value: dateValue ? new Date(dateValue).toISOString() : ''
        }
      };
      onChange(syntheticEvent);
    }
  };

  return (
    <Box>
      <TextField
        name={name}
        value={formatDateForInput(value)}
        onChange={handleDateChange}
        onBlur={onBlur}
        label={label}
        type='date'
        required={required}
        disabled={disabled}
        fullWidth={fullWidth}
        variant={variant}
        size={size}
        error={hasError}
        helperText={helperText && !hasError ? helperText : undefined}
        inputProps={{
          min: minDate ? formatDateForInput(minDate) : undefined,
          max: maxDate ? formatDateForInput(maxDate) : undefined
        }}
        InputLabelProps={{
          shrink: true
        }}
        {...props}
      />
      {displayErrors.length > 0 && (
        <Box sx={{ mt: 0.5 }}>
          {displayErrors.map((error, index) => (
            <FormHelperText key={index} error>
              {error}
            </FormHelperText>
          ))}
        </Box>
      )}
    </Box>
  );
};

export default ValidatedDatePicker;

// Add PropTypes validation
ValidatedDatePicker.propTypes = {
  name: PropTypes.string.isRequired,
  value: PropTypes.oneOfType([PropTypes.string, PropTypes.instanceOf(Date)]),
  onChange: PropTypes.func.isRequired,
  onBlur: PropTypes.func,
  errors: PropTypes.arrayOf(PropTypes.string),
  touched: PropTypes.bool,
  label: PropTypes.string,
  required: PropTypes.bool,
  disabled: PropTypes.bool,
  fullWidth: PropTypes.bool,
  variant: PropTypes.oneOf(['outlined', 'filled', 'standard']),
  size: PropTypes.oneOf(['small', 'medium']),
  minDate: PropTypes.oneOfType([PropTypes.string, PropTypes.instanceOf(Date)]),
  maxDate: PropTypes.oneOfType([PropTypes.string, PropTypes.instanceOf(Date)]),
  helperText: PropTypes.string
};

// Add default props
ValidatedDatePicker.defaultProps = {
  errors: [],
  touched: false,
  required: false,
  disabled: false,
  fullWidth: true,
  variant: 'outlined',
  size: 'medium'
};

