};

/**
 * Section Loading Component
 * For loading sections within a page
 */
export const SectionLoader = ({
  message,
  height = 200,
  variant = 'skeleton' // 'spinner', 'skeleton', 'linear'
}) => {
  if (variant === 'spinner') {
    return (
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          height,
          border: 1,
          borderColor: 'grey.200',
          borderRadius: 1,
          backgroundColor: 'grey.50'
        }}
      >
        <CircularProgress />
        {message && (
          <Typography variant='body2' color='text.secondary' sx={{ mt: 1 }}>
            {message}
          </Typography>
        )}
      </Box>
    );
  }

  if (variant === 'linear') {
    return (
      <Box sx={{ p: 2 }}>
        <LinearLoader message={message} />
      </Box>
    );
  }

  // Default to skeleton
  return <SkeletonCard height={height} />;
};

/**
 * Button Loading Component
 * For buttons with loading states
 */
export const LoadingButton = ({
  loading = false,
  children,
  loadingText = 'Loading...',
  startIcon,
  ...props
}) => {
  return (
    <Box sx={{ position: 'relative', display: 'inline-block' }}>
      <button
        disabled={loading}
        {...props}
        style={{
          ...props.style,
          opacity: loading ? 0.6 : 1
        }}
      >
        {loading ? (
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <CircularProgress size={16} />
            {loadingText}
          </Box>
        ) : (
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            {startIcon}
            {children}
          </Box>
        )}
      </button>
    </Box>
  );
};

/**
 * Data Loading States Hook
 * Manage different loading states for data fetching
 */
export const useLoadingStates = (initialState = {}) => {
  const [loadingStates, setLoadingStates] = React.useState({
    initial: true,
    refetch: false,
    submit: false,
    delete: false,
    ...initialState
  });

  const setLoading = (key, value) => {
    setLoadingStates(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const isAnyLoading = React.useMemo(() => {
    return Object.values(loadingStates).some(Boolean);
  }, [loadingStates]);

  return {
    loadingStates,
    setLoading,
    isAnyLoading
  };
};

export default {
  SpinnerLoader,
  LinearLoader,
  SkeletonCard,
  SkeletonTable,
  SkeletonChart,
  PageLoader,
  SectionLoader,
  LoadingButton,
  useLoadingStates
};

