import React, { createContext, useContext, useEffect, useState } from 'react';

import { processSyncQueue, getSyncQueueItems } from '../utils/offlineSync';

// Create context
const OfflineSyncContext = createContext();

// Provider component
export const OfflineSyncProvider = ({ children }) => {
  const [isOnline, setIsOnline] = useState(navigator.onLine);
  const [syncQueue, setSyncQueue] = useState([]);
  const [isSyncing, setIsSyncing] = useState(false);

  // Check online status
  useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // Load sync queue items
  useEffect(() => {
    const loadSyncQueue = async () => {
      const items = await getSyncQueueItems();
      setSyncQueue(items);
    };

    loadSyncQueue();
  }, []);

  // Process sync queue when coming online
  useEffect(() => {
    if (isOnline && syncQueue.length > 0 && !isSyncing) {
      // Process queue with a delay to ensure connectivity
      const timer = setTimeout(() => {
        processQueue();
      }, 2000);

      return () => clearTimeout(timer);
    }
  }, [isOnline, syncQueue, isSyncing]);

  // Process sync queue
  const processQueue = async () => {
    if (isSyncing) return;

    setIsSyncing(true);

    try {
      // This is a placeholder for the actual API call function
      // In a real implementation, you would pass the actual API call function
      const apiCall = async (endpoint, method, data) => {
        const url = `${
          process.env.REACT_APP_API_BASE_URL || 'http://localhost:5001/api'
        }${endpoint}`;

        const options = {
          method,
          headers: {
            'Content-Type': 'application/json'
          }
        };

        if (data) {
          options.body = JSON.stringify(data);
        }

        return await fetch(url, options);
      };

      const result = await processSyncQueue(apiCall);

      if (result.success) {
        console.log(`Sync completed: ${result.processed} processed, ${result.failed} failed`);

        // Refresh queue items
        const items = await getSyncQueueItems();
        setSyncQueue(items);
      } else {
        console.error('Sync failed:', result.error);
      }
    } catch (error) {
      console.error('Error during sync:', error);
    } finally {
      setIsSyncing(false);
    }
  };

  // Add item to sync queue
  const addToQueue = async (endpoint, method, data, identifier) => {
    // In a real implementation, you would import and use the addToSyncQueue function
    // For now, we'll just update the local state
    const newItem = {
      id: Date.now() + Math.random(),
      endpoint,
      method,
      data,
      identifier,
      timestamp: new Date().toISOString()
    };

    setSyncQueue(prev => [...prev, newItem]);

    // In a real implementation, you would also call:
    // await addToSyncQueue(endpoint, method, data, identifier);
  };

  // Value for context
  const value = {
    isOnline,
    syncQueue,
    isSyncing,
    processQueue,
    addToQueue
  };

  return <OfflineSyncContext.Provider value={value}>{children}</OfflineSyncContext.Provider>;
};

// Hook to use the context
export const useOfflineSync = () => {
  const context = useContext(OfflineSyncContext);

  if (!context) {
    throw new Error('useOfflineSync must be used within an OfflineSyncProvider');
  }

  return context;
};

export default OfflineSyncContext;

import React, { createContext, useContext, useReducer, useEffect, useCallback } from 'react';

import { getSeasons } from '../services/api';
import {
  createAsyncReducer,
  createAsyncActions,
  ActionTypes,
  createAsyncState
} from '../utils/stateManagement';

const SeasonContext = createContext();

// Custom actions for season-specific functionality
const seasonActions = {
  SELECT_SEASON: 'SELECT_SEASON',
  SET_DEFAULT_SEASON: 'SET_DEFAULT_SEASON'
};

// Enhanced reducer with season-specific actions
const seasonReducer = createAsyncReducer({
  [seasonActions.SELECT_SEASON]: (state, action) => ({
    ...state,
    selectedSeason: action.payload
  }),
  [seasonActions.SET_DEFAULT_SEASON]: (state, action) => {
    if (!state.selectedSeason && state.data && state.data.length > 0) {
      // Find the active season or use the first one
      const activeSeason = state.data.find(season => season.status === 'Active') || state.data[0];
      return {
        ...state,
        selectedSeason: activeSeason
      };
    }
    return state;
  }
});

export const useSeason = () => {
  const context = useContext(SeasonContext);
  if (!context) {
    throw new Error('useSeason must be used within a SeasonProvider');
  }
  return context;
};

export const SeasonProvider = ({ children }) => {
  const [state, dispatch] = useReducer(seasonReducer, {
    ...createAsyncState([]),
    selectedSeason: null
  });

  const actions = createAsyncActions(dispatch);

  // Enhanced actions for season management
  const selectSeason = useCallback(season => {
    dispatch({ type: seasonActions.SELECT_SEASON, payload: season });
  }, []);

  const fetchSeasons = useCallback(async () => {
    actions.setLoading(true);
    try {
      const data = await getSeasons();
      actions.setData(data);
      // Set default season after data is loaded
      dispatch({ type: seasonActions.SET_DEFAULT_SEASON });
    } catch (err) {
      actions.setError(err);
    }
  }, [actions]);

  useEffect(() => {
    fetchSeasons();
  }, [fetchSeasons]);

  // Context value with standardized state structure
  const contextValue = {
    seasons: state.data,
    selectedSeason: state.selectedSeason,
    loading: state.loading,
    error: state.error,
    lastUpdated: state.lastUpdated,
    // Actions
    selectSeason,
    setSelectedSeason: selectSeason, // Backward compatibility
    refetchSeasons: fetchSeasons,
    ...actions
  };

  return <SeasonContext.Provider value={contextValue}>{children}</SeasonContext.Provider>;
};

