const formatBytes = bytes => {
  if (bytes === 0) return '0 Bytes';
  const i = Math.floor(Math.log(bytes) / Math.log(KILO_BYTES));
  return `${parseFloat((bytes / KILO_BYTES ** i).toFixed(2))} ${SIZES[i]}`;
};

