// Type definitions for API utilities
export interface ApiError {
  message: string;
  status?: number;
  code?: string;
}

export interface ApiResponse<T = any> {
  data: T;
  message?: string;
  success?: boolean;
  total?: number;
  page?: number;
  limit?: number;
}

export interface UseApiDataReturn<T> {
  data: T | null;
  loading: boolean;
  error: ApiError | null;
  refetch: () => Promise<T | null>;
  clearCache: () => void;
}

