// Custom hook for data fetching with loading and error states
export const useApiData = <T = any>(
  apiFunction: ApiFunction<T> | null,
  dependencies: React.DependencyList = [],
  cacheKey: string | null = null,
  retryCount: number = 3
): UseApiDataReturn<T> => {
  const [data, setData] = useState<T | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<ApiError | null>(null);
  const retryTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  const fetchData = useCallback(
    async (useCache: boolean = true, retries: number = retryCount): Promise<T | null> => {
      // Clear any existing retry timeout
      if (retryTimeoutRef.current) {
        clearTimeout(retryTimeoutRef.current);
        retryTimeoutRef.current = null;
      }

      // If apiFunction is null, don't make the request
      if (!apiFunction) {
        setData(null);
        setLoading(false);
        setError(null);
        return null;
      }

      try {
        setLoading(true);
        setError(null);

        // Use the enhanced caching system through apiFunction
        const result = await apiFunction();

        // Handle different response formats
        let processedData: T;
        if (Array.isArray(result)) {
          processedData = result as T;
        } else if (
          result &&
          typeof result === 'object' &&
          'data' in result &&
          Array.isArray((result as any).data)
        ) {
          processedData = (result as ApiResponse<T>).data;
        } else if (result && typeof result === 'object') {
          processedData = result as T;
        } else {
          processedData = null as T;
        }

        setData(processedData);
        return processedData;
      } catch (err) {
        // Retry logic
        if (retries > 0) {
          console.warn(`API call failed, retrying... (${retryCount - retries + 1}/${retryCount})`);
          retryTimeoutRef.current = setTimeout(() => {
            fetchData(useCache, retries - 1);
          }, 1000 * (retryCount - retries)); // Exponential backoff
          return null;
        }

        const apiError: ApiError = {
          message: err instanceof Error ? err.message : 'An unknown error occurred',
          status: (err as any)?.status,
          code: (err as any)?.code
        };
        setError(apiError);
        return null;
      } finally {
        setLoading(false);
      }
    },
    [apiFunction, cacheKey, retryCount]
  );

  useEffect(() => {
    fetchData();

    // Cleanup function to clear any pending retries
    return () => {
      if (retryTimeoutRef.current) {
        clearTimeout(retryTimeoutRef.current);
        retryTimeoutRef.current = null;
      }
    };
  }, dependencies); // eslint-disable-line react-hooks/exhaustive-deps

  const refetch = useCallback(async (): Promise<T | null> => {
    return await fetchData(false); // Don't use cache when refetching
  }, [fetchData]);

  // Clear cache for this key using the enhanced cache system
  const clearCacheKey = useCallback((): void => {
    if (cacheKey) {
      apiCache.delete(cacheKey);
    }
  }, [cacheKey]);

  return { data, loading, error, refetch, clearCache: clearCacheKey };
};

// Custom hook for API mutations (POST, PUT, DELETE)
export const useApiMutation = <TArgs extends any[] = any[], TReturn = any>(
  apiFunction: MutationFunction<TArgs, TReturn>,
  options: UseApiMutationOptions = {}
): UseApiMutationReturn<TReturn> => {
  const { maxRetryCount = 3, invalidatePatterns = [], onSuccess = null, onError = null } = options;

  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<ApiError | null>(null);
  const retryTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  const mutate = useCallback(
    async (...args: TArgs): Promise<MutationResult<TReturn>> => {
      // Wrapper function that handles retries with decrementing count
      const attemptMutation = async (
        remainingRetries: number
      ): Promise<MutationResult<TReturn>> => {
        // Clear any existing retry timeout
        if (retryTimeoutRef.current) {
          clearTimeout(retryTimeoutRef.current);
          retryTimeoutRef.current = null;
        }

        try {
          setLoading(true);
          setError(null);
          const result = await apiFunction(...args);

          // Invalidate cache patterns after successful mutation
          if (invalidatePatterns.length > 0) {
            invalidatePatterns.forEach(pattern => apiCache.invalidatePattern(pattern));
          }

          // Call success callback if provided
          if (onSuccess) {
            onSuccess(result);
          }

          return { data: result, error: null };
        } catch (err) {
          // Retry logic
          if (remainingRetries > 0) {
            console.warn(
              `API mutation failed, retrying... (${
                maxRetryCount - remainingRetries + 1
              }/${maxRetryCount})`
            );
            retryTimeoutRef.current = setTimeout(() => {
              attemptMutation(remainingRetries - 1);
            }, 1000 * (maxRetryCount - remainingRetries)); // Exponential backoff
            return { data: null, error: null }; // Return early for retry
          }

          const apiError: ApiError = {
            message: err instanceof Error ? err.message : 'An unknown error occurred',
            status: (err as any)?.status,
            code: (err as any)?.code
          };
          setError(apiError);

          // Call error callback if provided
          if (onError) {
            onError(err instanceof Error ? err : new Error('Unknown error'));
          }

          return { data: null, error: apiError.message };
        } finally {
          setLoading(false);
        }
      };

      return await attemptMutation(maxRetryCount);
    },
    [apiFunction, maxRetryCount, invalidatePatterns, onSuccess, onError]
  );

  // Cleanup function to clear any pending retries
  useEffect(() => {
    return () => {
      if (retryTimeoutRef.current) {
        clearTimeout(retryTimeoutRef.current);
        retryTimeoutRef.current = null;
      }
    };
  }, []);

  return { mutate, loading, error };
};

// Cache management utilities with TypeScript
export const useCacheManagement = () => {
  const clearSpecificCache = useCallback((patterns: string[]) => {
    patterns.forEach(pattern => apiCache.invalidatePattern(pattern));
  }, []);

  const getCacheStatistics = useCallback(() => {
    return apiCache.getStats();
  }, []);

  return {
    clearCache: clearSpecificCache,
    getCacheStats: getCacheStatistics
  };
};

// Utility hooks for common API patterns
export const usePaginatedApi = <T = any>(
  baseApiFunction: (page: number, limit: number) => Promise<ApiResponse<T[]>>,
  initialPage: number = 1,
  pageSize: number = 10
) => {
  const [currentPage, setCurrentPage] = useState<number>(initialPage);
  const [allData, setAllData] = useState<T[]>([]);
  const [hasMore, setHasMore] = useState<boolean>(true);

  const apiFunction = useCallback(() => {
    return baseApiFunction(currentPage, pageSize);
  }, [baseApiFunction, currentPage, pageSize]);

  const { data, loading, error, refetch } = useApiData<ApiResponse<T[]>>(apiFunction, [
    currentPage,
    pageSize
  ]);

  useEffect(() => {
    if (data && Array.isArray(data.data)) {
      if (currentPage === 1) {
        setAllData(data.data);
      } else {
        setAllData(prev => [...prev, ...data.data]);
      }
      setHasMore(data.data.length === pageSize);
    }
  }, [data, currentPage, pageSize]);

  const loadMore = useCallback(() => {
    if (!loading && hasMore) {
      setCurrentPage(prev => prev + 1);
    }
  }, [loading, hasMore]);

  const reset = useCallback(() => {
    setCurrentPage(1);
    setAllData([]);
    setHasMore(true);
  }, []);

  return {
    data: allData,
    loading,
    error,
    hasMore,
    loadMore,
    reset,
    refetch,
    currentPage
  };
};

export { apiCall };
export default {
  useApiData,
  useApiMutation,
  useCacheManagement,
  usePaginatedApi,
  apiCall
};

/**
 * Custom hooks for async operations and error handling
 * Extracts common async operation patterns
 */

import { useState, useCallback, useRef, useEffect } from 'react';

/**
 * Hook for managing async operations with consistent loading/error states
 * @param {Function} asyncFunction - Async function to execute
 * @param {Object} options - Configuration options
 * @returns {Object} Async operation state and execute function
 */
export const useAsyncOperation = (asyncFunction, options = {}) => {
  const {
    immediate = false,
    dependencies = [],
    onSuccess = null,
    onError = null,
    retryCount = 0,
    retryDelay = 1000
  } = options;

  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [lastExecuted, setLastExecuted] = useState(null);
  const retryTimeoutRef = useRef(null);
  const isMountedRef = useRef(true);

  // Cleanup function
  useEffect(() => {
    isMountedRef.current = true;
    return () => {
      isMountedRef.current = false;
      if (retryTimeoutRef.current) {
        clearTimeout(retryTimeoutRef.current);
      }
    };
  }, []);

  const execute = useCallback(
    async (...args) => {
      const attemptOperation = async remainingRetries => {
        if (!isMountedRef.current) return null;

        try {
          setLoading(true);
          setError(null);

          const result = await asyncFunction(...args);

          if (!isMountedRef.current) return null;

          setData(result);
          setLastExecuted(new Date());

          if (onSuccess) {
            onSuccess(result);
          }

          return result;
        } catch (err) {
          if (!isMountedRef.current) return null;

          // Retry logic
          if (remainingRetries > 0) {
            retryTimeoutRef.current = setTimeout(() => {
              attemptOperation(remainingRetries - 1);
            }, retryDelay);
            return null;
          }

          setError(err);

          if (onError) {
            onError(err);
          }

          throw err;
        } finally {
          if (isMountedRef.current) {
            setLoading(false);
          }
        }
      };

      return attemptOperation(retryCount);
    },
    [asyncFunction, retryCount, retryDelay, onSuccess, onError]
  );

  // Auto-execute on mount if immediate flag is set
  useEffect(() => {
    if (immediate) {
      execute();
    }
  }, [immediate, ...dependencies]); // eslint-disable-line react-hooks/exhaustive-deps

  const reset = useCallback(() => {
    setData(null);
    setError(null);
    setLoading(false);
    setLastExecuted(null);
    if (retryTimeoutRef.current) {
      clearTimeout(retryTimeoutRef.current);
    }
  }, []);

  const retry = useCallback(() => {
    return execute();
  }, [execute]);

  return {
    data,
    loading,
    error,
    lastExecuted,
    execute,
    reset,
    retry,
    isSuccess: !loading && !error && data !== null,
    isError: !loading && error !== null,
    isIdle: !loading && error === null && data === null
  };
};

/**
 * Hook for managing multiple async operations
 * @param {Object} operations - Object with named async operations
 * @param {Object} options - Configuration options
 * @returns {Object} State for all operations and control functions
 */
export const useAsyncOperations = (operations = {}, options = {}) => {
  const [states, setStates] = useState({});

  const updateState = useCallback((operationName, updates) => {
    setStates(prev => ({
      ...prev,
      [operationName]: {
        ...prev[operationName],
        ...updates
      }
    }));
  }, []);

  const executeOperation = useCallback(
    async (operationName, ...args) => {
      const operation = operations[operationName];
      if (!operation) {
        throw new Error(`Operation "${operationName}" not found`);
      }

      updateState(operationName, { loading: true, error: null });

      try {
        const result = await operation(...args);
        updateState(operationName, {
          data: result,
          loading: false,
          lastExecuted: new Date()
        });
        return result;
      } catch (error) {
        updateState(operationName, { error, loading: false });
        throw error;
      }
    },
    [operations, updateState]
  );

  const resetOperation = useCallback(operationName => {
    setStates(prev => ({
      ...prev,
      [operationName]: {
        data: null,
        loading: false,
        error: null,
        lastExecuted: null
      }
    }));
  }, []);

  const resetAll = useCallback(() => {
    setStates({});
  }, []);

  // Create individual operation executors
  const executors = Object.keys(operations).reduce((acc, operationName) => {
    acc[operationName] = (...args) => executeOperation(operationName, ...args);
    return acc;
  }, {});

  // Get state for specific operation
  const getOperationState = useCallback(
    operationName => {
      return (
        states[operationName] || {
          data: null,
          loading: false,
          error: null,
          lastExecuted: null
        }
      );
    },
    [states]
  );

  // Check if any operation is loading
  const anyLoading = Object.values(states).some(state => state.loading);

  return {
    states,
    executors,
    executeOperation,
    resetOperation,
    resetAll,
    getOperationState,
    anyLoading
  };
};

/**
 * Hook for handling API errors with retry and fallback mechanisms
 * @param {Object} options - Configuration options
 * @returns {Object} Error handling utilities
 */
export const useErrorHandler = (options = {}) => {
  const { defaultRetryCount = 3, defaultRetryDelay = 1000, onUnhandledError = null } = options;

  const [errorHistory, setErrorHistory] = useState([]);

  const logError = useCallback(
    (error, context = '') => {
      const errorEntry = {
        id: Date.now(),
        error,
        context,
        timestamp: new Date(),
        retryCount: 0
      };

      setErrorHistory(prev => [errorEntry, ...prev].slice(0, 50)); // Keep last 50 errors

      if (onUnhandledError) {
        onUnhandledError(error, context);
      }
    },
    [onUnhandledError]
  );

  const handleError = useCallback(
    async (error, operation, context = '') => {
      logError(error, context);

      // Determine if error is retryable
      const isRetryable = isRetryableError(error);

      if (isRetryable && operation) {
        // Implement exponential backoff retry
        for (let attempt = 1; attempt <= defaultRetryCount; attempt++) {
          try {
            await new Promise(resolve =>
              setTimeout(resolve, defaultRetryDelay * Math.pow(2, attempt - 1))
            );

            const result = await operation();
            return { success: true, data: result };
          } catch (retryError) {
            if (attempt === defaultRetryCount) {
              logError(retryError, `${context} (final retry attempt)`);
              return { success: false, error: retryError };
            }
          }
        }
      }

      return { success: false, error };
    },
    [defaultRetryCount, defaultRetryDelay, logError]
  );

  const clearErrorHistory = useCallback(() => {
    setErrorHistory([]);
  }, []);

  const getErrorStats = useCallback(() => {
    const now = new Date();
    const lastHour = errorHistory.filter(entry => now - entry.timestamp < 60 * 60 * 1000);

    return {
      total: errorHistory.length,
      lastHour: lastHour.length,
      mostRecent: errorHistory[0] || null
    };
  }, [errorHistory]);

  return {
    handleError,
    logError,
    clearErrorHistory,
    getErrorStats,
    errorHistory: errorHistory.slice(0, 10) // Return only last 10 for UI
  };
};

/**
 * Hook for managing request cancellation
 * @returns {Object} Cancellation utilities
 */
export const useCancellation = () => {
  const abortControllersRef = useRef(new Map());

  useEffect(() => {
    // Cleanup all controllers on unmount
    return () => {
      abortControllersRef.current.forEach(controller => controller.abort());
      abortControllersRef.current.clear();
    };
  }, []);

  const createCancellableRequest = useCallback((requestId, requestFunction) => {
    // Cancel previous request with same ID if exists
    if (abortControllersRef.current.has(requestId)) {
      abortControllersRef.current.get(requestId).abort();
    }

    const controller = new AbortController();
    abortControllersRef.current.set(requestId, controller);

    const cancellablePromise = requestFunction(controller.signal);

    // Clean up controller when request completes
    cancellablePromise.finally(() => {
      abortControllersRef.current.delete(requestId);
    });

    return {
      promise: cancellablePromise,
      cancel: () => {
        controller.abort();
        abortControllersRef.current.delete(requestId);
      }
    };
  }, []);

  const cancelRequest = useCallback(requestId => {
    const controller = abortControllersRef.current.get(requestId);
    if (controller) {
      controller.abort();
      abortControllersRef.current.delete(requestId);
    }
  }, []);

  const cancelAllRequests = useCallback(() => {
    abortControllersRef.current.forEach(controller => controller.abort());
    abortControllersRef.current.clear();
  }, []);

  return {
    createCancellableRequest,
    cancelRequest,
    cancelAllRequests,
    hasActiveRequests: () => abortControllersRef.current.size > 0
  };
};

// Helper function to determine if an error is retryable
const isRetryableError = error => {
  // Network errors, timeouts, and 5xx server errors are typically retryable
  const retryableStatuses = [408, 429, 500, 502, 503, 504];

  if (error.code === 'NETWORK_ERROR' || error.code === 'TIMEOUT') {
    return true;
  }

  if (error.status && retryableStatuses.includes(error.status)) {
    return true;
  }

  return false;
};

/**
 * React Hooks for Cache Management
 *
 * This module provides React hooks that integrate with the CacheManager
 * to provide intelligent caching capabilities for components and API calls.
 *
 * Features:
 * - Automatic cache management for API calls
 * - Cache-aware data fetching with multiple strategies
 * - Cache invalidation and dependency management
 * - Performance monitoring and metrics
 * - Automatic cleanup on unmount
 */

import { useState, useEffect, useCallback, useRef, useMemo } from 'react';

import { globalCache, CacheStrategy, CacheConfig } from '../utils/cacheManager';
import logger from '../utils/logger';

/**
 * Hook for cached API data fetching with intelligent strategies
 *
 * @param {string} key - Unique cache key
 * @param {Function} fetcher - Function that returns a Promise with the data
 * @param {Object} options - Configuration options
 * @returns {Object} Data, loading state, error state, and control functions
 */
export const useCachedData = (key, fetcher, options = {}) => {
  const {
    strategy = CacheConfig.STRATEGIES.CACHE_FIRST,
    ttl = CacheConfig.DEFAULT_TTL,
    category = CacheConfig.CATEGORIES.API_RESPONSES,
    dependencies = [],
    enabled = true,
    suspense = false,
    onSuccess = null,
    onError = null,
    retryCount = 3,
    retryDelay = 1000
  } = options;

  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastFetch, setLastFetch] = useState(null);

  const cacheStrategy = useRef(new CacheStrategy(globalCache, strategy));
  const retryTimeoutRef = useRef(null);
  const isMountedRef = useRef(true);

  // Cleanup on unmount
  useEffect(() => {
    isMountedRef.current = true;
    return () => {
      isMountedRef.current = false;
      if (retryTimeoutRef.current) {
        clearTimeout(retryTimeoutRef.current);
      }
    };
  }, []);

  // Fetch data function with retry logic
  const fetchData = useCallback(
    async (currentRetryCount = retryCount) => {
      if (!enabled || !isMountedRef.current) return;

      try {
        setLoading(true);
        setError(null);

        const result = await cacheStrategy.current.execute(key, fetcher, {
          ttl,
          category,
          dependencies,
          level: CacheConfig.LEVELS.MEMORY
        });

        if (!isMountedRef.current) return;

        setData(result);
        setLastFetch(new Date());

        if (onSuccess) {
          onSuccess(result);
        }
      } catch (err) {
        if (!isMountedRef.current) return;

        if (currentRetryCount > 0) {
          retryTimeoutRef.current = setTimeout(() => {
            fetchData(currentRetryCount - 1);
          }, retryDelay * (retryCount - currentRetryCount + 1));
          return;
        }

        setError(err);

        if (onError) {
          onError(err);
        }
      } finally {
        if (isMountedRef.current) {
          setLoading(false);
        }
      }
    },
    [key, fetcher, enabled, ttl, category, dependencies, onSuccess, onError, retryCount, retryDelay]
  );

  // Fetch data when key or dependencies change
  useEffect(() => {
    fetchData();
  }, [fetchData, ...dependencies]);

  // Manual refetch function
  const refetch = useCallback(() => {
    // Clear cache for this key and refetch
    globalCache.delete(key);
    return fetchData();
  }, [key, fetchData]);

  // Invalidate cache for this key
  const invalidate = useCallback(() => {
    globalCache.delete(key);
    setData(null);
    setError(null);
  }, [key]);

  return {
    data,
    loading,
    error,
    lastFetch,
    refetch,
    invalidate,
    isStale: data && globalCache.memoryCache.get(key)?.isStale(),
    isCached: globalCache.has(key)
  };
};

/**
 * Hook for caching computed values
 *
 * @param {Function} compute - Function that computes the value
 * @param {Array} deps - Dependencies array
 * @param {Object} options - Configuration options
 * @returns {any} Cached computed value
 */
export const useCachedComputation = (compute, deps = [], options = {}) => {
  const {
    ttl = 60 * 1000, // 1 minute for computed values
    category = CacheConfig.CATEGORIES.COMPUTED_DATA
  } = options;

  const key = useMemo(() => `computed_${JSON.stringify(deps)}`, [deps]);

  return useMemo(() => {
    // Check cache first
    const cached = globalCache.get(key);
    if (cached) {
      return cached;
    }

    // Compute value
    const result = compute();

    // Cache the result
    globalCache.set(key, result, { ttl, category });

    return result;
  }, [compute, key, ttl, category]);
};

/**
 * Hook for managing cache invalidation patterns
 *
 * @param {Array} patterns - Array of cache key patterns to manage
 * @returns {Object} Cache management functions
 */
export const useCacheInvalidation = (patterns = []) => {
  const invalidatePattern = useCallback(pattern => {
    if (typeof pattern === 'string') {
      // Simple string matching
      for (const [key] of globalCache.memoryCache) {
        if (key.includes(pattern)) {
          globalCache.delete(key);
        }
      }
    } else if (pattern instanceof RegExp) {
      // Regex pattern matching
      for (const [key] of globalCache.memoryCache) {
        if (pattern.test(key)) {
          globalCache.delete(key);
        }
      }
    }
  }, []);

  const invalidateAll = useCallback(() => {
    patterns.forEach(pattern => invalidatePattern(pattern));
  }, [patterns, invalidatePattern]);

  const invalidateCategory = useCallback(category => {
    globalCache.clear(category);
  }, []);

  return {
    invalidatePattern,
    invalidateAll,
    invalidateCategory,
    clearCache: () => globalCache.clear()
  };
};

/**
 * Hook for cache performance monitoring
 *
 * @returns {Object} Cache statistics and performance metrics
 */
export const useCacheMetrics = () => {
  const [metrics, setMetrics] = useState(globalCache.getStats());

  useEffect(() => {
    const updateMetrics = () => {
      setMetrics(globalCache.getStats());
    };

    // Update metrics every 5 seconds
    const interval = setInterval(updateMetrics, 5000);

    return () => clearInterval(interval);
  }, []);

  const clearMetrics = useCallback(() => {
    globalCache.metrics.hits = 0;
    globalCache.metrics.misses = 0;
    globalCache.metrics.evictions = 0;
    setMetrics(globalCache.getStats());
  }, []);

  return {
    ...metrics,
    clearMetrics,
    refreshMetrics: () => setMetrics(globalCache.getStats())
  };
};

/**
 * Hook for cache warming and preloading
 *
 * @param {Array} warmupData - Array of data to preload
 * @returns {Object} Warmup functions and status
 */
export const useCacheWarmup = (warmupData = []) => {
  const [warming, setWarming] = useState(false);
  const [warmed, setWarmed] = useState(false);

  const warmCache = useCallback(
    async (data = warmupData) => {
      setWarming(true);
      try {
        await globalCache.warmCache(data);
        setWarmed(true);
      } catch (error) {
        logger.error('Cache warmup failed:', error);
      } finally {
        setWarming(false);
      }
    },
    [warmupData]
  );

  const preloadData = useCallback(async (key, fetcher, options = {}) => {
    return globalCache.preload(key, fetcher, options);
  }, []);

  return {
    warming,
    warmed,
    warmCache,
    preloadData
  };
};

/**
 * Hook for form data caching (draft saving)
 *
 * @param {string} formId - Unique form identifier
 * @param {Object} initialValues - Initial form values
 * @param {Object} options - Configuration options
 * @returns {Object} Form state management with caching
 */
export const useCachedForm = (formId, initialValues = {}, options = {}) => {
  const {
    autosave = true,
    autosaveInterval = 5000, // 5 seconds
    ttl = 24 * 60 * 60 * 1000 // 24 hours
  } = options;

  const cacheKey = `form_draft_${formId}`;

  // Load cached form data or use initial values
  const [values, setValues] = useState(() => {
    const cached = globalCache.get(cacheKey);
    return cached || initialValues;
  });

  const [isDirty, setIsDirty] = useState(false);

  // Auto-save timer
  const saveTimerRef = useRef(null);

  // Save form data to cache
  const saveToCache = useCallback(() => {
    globalCache.set(cacheKey, values, {
      ttl,
      category: CacheConfig.CATEGORIES.FORM_DATA,
      level: CacheConfig.LEVELS.LOCAL // Persist across sessions
    });
    setIsDirty(false);
  }, [cacheKey, values, ttl]);

  // Update form values
  const updateValues = useCallback(
    newValues => {
      setValues(newValues);
      setIsDirty(true);

      if (autosave) {
        if (saveTimerRef.current) {
          clearTimeout(saveTimerRef.current);
        }
        saveTimerRef.current = setTimeout(saveToCache, autosaveInterval);
      }
    },
    [autosave, autosaveInterval, saveToCache]
  );

  // Clear cached form data
  const clearCache = useCallback(() => {
    globalCache.delete(cacheKey);
    setValues(initialValues);
    setIsDirty(false);
  }, [cacheKey, initialValues]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (saveTimerRef.current) {
        clearTimeout(saveTimerRef.current);
      }
      if (autosave && isDirty) {
        saveToCache();
      }
    };
  }, [autosave, isDirty, saveToCache]);

  return {
    values,
    updateValues,
    saveToCache,
    clearCache,
    isDirty,
    hasCachedData: globalCache.has(cacheKey)
  };
};

/**
 * Hook for managing cache dependencies and relationships
 *
 * @param {string} mainKey - Main cache key
 * @param {Array} dependentKeys - Keys that depend on the main key
 * @returns {Object} Dependency management functions
 */
export const useCacheDependencies = (mainKey, dependentKeys = []) => {
  const invalidateDependents = useCallback(() => {
    dependentKeys.forEach(key => {
      globalCache.delete(key);
    });
  }, [dependentKeys]);

  const refreshAll = useCallback(async () => {
    // This would need to be implemented with actual refresh logic
    // For now, just invalidate all dependent caches
    globalCache.delete(mainKey);
    invalidateDependents();
  }, [mainKey, invalidateDependents]);

  return {
    invalidateDependents,
    refreshAll,
    addDependency: key => {
      if (!dependentKeys.includes(key)) {
        dependentKeys.push(key);
      }
    }
  };
};

/**
 * Hook that provides a cache-aware version of useState
 *
 * @param {string} key - Cache key
 * @param {any} initialValue - Initial value
 * @param {Object} options - Configuration options
 * @returns {Array} [value, setValue] similar to useState
 */
export const useCachedState = (key, initialValue, options = {}) => {
  const {
    ttl = CacheConfig.DEFAULT_TTL,
    category = CacheConfig.CATEGORIES.USER_PREFERENCES,
    persist = false
  } = options;

  const [value, setValue] = useState(() => {
    const cached = globalCache.get(key);
    return cached !== null ? cached : initialValue;
  });

  const setCachedValue = useCallback(
    newValue => {
      const finalValue = typeof newValue === 'function' ? newValue(value) : newValue;
      setValue(finalValue);

      globalCache.set(key, finalValue, {
        ttl,
        category,
        level: persist ? CacheConfig.LEVELS.LOCAL : CacheConfig.LEVELS.MEMORY
      });
    },
    [key, value, ttl, category, persist]
  );

  return [value, setCachedValue];
};

const CachingHooks = {
  useCachedData,
  useCachedComputation,
  useCacheInvalidation,
  useCacheMetrics,
  useCacheWarmup,
  useCachedForm,
  useCacheDependencies,
  useCachedState
};

export default CachingHooks;

/**
 * Custom hooks for data management patterns
 * Extracts common list/table management logic
 */

import { useState, useCallback, useMemo, useEffect, useRef } from 'react';

import { useDebounce } from '../utils/performanceOptimization';

/**
 * Hook for managing search, filter, and pagination state
 * @param {Array} data - Source data to search/filter
 * @param {Object} options - Configuration options
 * @returns {Object} Processed data and control functions
 */
export const useDataTable = (data = [], options = {}) => {
  const {
    searchFields = [],
    filterField = null,
    defaultFilter = 'all',
    itemsPerPage = 10,
    searchDebounceMs = 300
  } = options;

  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState(defaultFilter);
  const [page, setPage] = useState(1);
  const [sortConfig, setSortConfig] = useState({ field: null, direction: 'asc' });

  // Debounce search term to improve performance
  const debouncedSearchTerm = useDebounce(searchTerm, searchDebounceMs);

  // Filtered and searched data
  const processedData = useMemo(() => {
    let result = [...(data || [])];

    // Apply search
    if (debouncedSearchTerm && searchFields.length > 0) {
      const searchLower = debouncedSearchTerm.toLowerCase();
      result = result.filter(item =>
        searchFields.some(field => {
          const value = getNestedValue(item, field);
          return value?.toString().toLowerCase().includes(searchLower);
        })
      );
    }

    // Apply filter
    if (filter !== defaultFilter && filterField) {
      result = result.filter(item => {
        const value = getNestedValue(item, filterField);
        return value?.toString().toLowerCase() === filter.toLowerCase();
      });
    }

    // Apply sorting
    if (sortConfig.field) {
      result.sort((a, b) => {
        const aValue = getNestedValue(a, sortConfig.field);
        const bValue = getNestedValue(b, sortConfig.field);

        if (aValue < bValue) return sortConfig.direction === 'asc' ? -1 : 1;
        if (aValue > bValue) return sortConfig.direction === 'asc' ? 1 : -1;
        return 0;
      });
    }

    return result;
  }, [data, debouncedSearchTerm, filter, sortConfig, searchFields, filterField, defaultFilter]);

  // Paginated data
  const paginatedData = useMemo(() => {
    const startIndex = (page - 1) * itemsPerPage;
    return processedData.slice(startIndex, startIndex + itemsPerPage);
  }, [processedData, page, itemsPerPage]);

  // Total pages
  const totalPages = useMemo(
    () => Math.ceil(processedData.length / itemsPerPage),
    [processedData.length, itemsPerPage]
  );

  // Reset page when search/filter changes
  useEffect(() => {
    setPage(1);
  }, [debouncedSearchTerm, filter]);

  // Event handlers
  const handleSearch = useCallback(value => {
    setSearchTerm(value);
  }, []);

  const handleFilter = useCallback(value => {
    setFilter(value);
  }, []);

  const handleSort = useCallback(field => {
    setSortConfig(prev => ({
      field,
      direction: prev.field === field && prev.direction === 'asc' ? 'desc' : 'asc'
    }));
  }, []);

  const handlePageChange = useCallback(
    newPage => {
      setPage(Math.max(1, Math.min(newPage, totalPages)));
    },
    [totalPages]
  );

  const reset = useCallback(() => {
    setSearchTerm('');
    setFilter(defaultFilter);
    setPage(1);
    setSortConfig({ field: null, direction: 'asc' });
  }, [defaultFilter]);

  return {
    // Processed data
    data: paginatedData,
    allData: processedData,
    totalItems: processedData.length,

    // Pagination
    page,
    totalPages,
    itemsPerPage,

    // Search and filter
    searchTerm,
    filter,
    sortConfig,

    // Handlers
    handleSearch,
    handleFilter,
    handleSort,
    handlePageChange,
    reset,

    // Helper functions
    setPage,
    setSearchTerm,
    setFilter
  };
};

/**
 * Hook for managing CRUD operations with optimistic updates
 * @param {Object} apiMethods - Object containing CRUD API methods
 * @param {Object} options - Configuration options
 * @returns {Object} CRUD handlers and state
 */
export const useCrudOperations = (apiMethods = {}, options = {}) => {
  const { onSuccess = () => {}, onError = () => {}, optimisticUpdates = true } = options;

  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState({});
  const [errors, setErrors] = useState({});

  // Use ref to store apiMethods to avoid dependency issues
  const apiMethodsRef = useRef(apiMethods);
  useEffect(() => {
    apiMethodsRef.current = apiMethods;
  }, [apiMethods]);

  // Generic operation handler
  const handleOperation = useCallback(
    async (operation, operationType, itemId = null, data = null) => {
      const loadingKey = itemId ? `${operationType}_${itemId}` : operationType;

      setLoading(prev => ({ ...prev, [loadingKey]: true }));
      setErrors(prev => ({ ...prev, [loadingKey]: null }));

      try {
        let result;
        let optimisticUpdate = null;

        // Apply optimistic update for immediate UI feedback
        if (optimisticUpdates && itemId) {
          switch (operationType) {
            case 'update':
              optimisticUpdate = items.map(item =>
                item.id === itemId ? { ...item, ...data } : item
              );
              setItems(optimisticUpdate);
              break;
            case 'delete':
              optimisticUpdate = items.filter(item => item.id !== itemId);
              setItems(optimisticUpdate);
              break;
          }
        }

        // Perform actual operation
        result = await operation();

        // Update state based on operation type
        switch (operationType) {
          case 'create':
            setItems(prev => [result, ...prev]);
            break;
          case 'update':
            if (!optimisticUpdates) {
              setItems(prev => prev.map(item => (item.id === itemId ? result : item)));
            }
            break;
          case 'delete':
            if (!optimisticUpdates) {
              setItems(prev => prev.filter(item => item.id !== itemId));
            }
            break;
          case 'fetch':
            setItems(Array.isArray(result) ? result : [result]);
            break;
        }

        onSuccess(result, operationType);
        return result;
      } catch (error) {
        // Revert optimistic update on error
        if (optimisticUpdates && itemId) {
          // Only revert if we had an optimistic update
          switch (operationType) {
            case 'update':
            case 'delete':
              setItems(items); // Revert to original state
              break;
          }
        }

        setErrors(prev => ({ ...prev, [loadingKey]: error }));
        onError(error, operationType);
        throw error;
      } finally {
        setLoading(prev => ({ ...prev, [loadingKey]: false }));
      }
    },
    [items, optimisticUpdates, onSuccess, onError]
  );

  // CRUD operation wrappers
  const create = useCallback(
    data => {
      if (!apiMethodsRef.current.create) throw new Error('Create method not provided');
      return handleOperation(() => apiMethodsRef.current.create(data), 'create', null, data);
    },
    [handleOperation]
  );

  const update = useCallback(
    (id, data) => {
      if (!apiMethodsRef.current.update) throw new Error('Update method not provided');
      return handleOperation(() => apiMethodsRef.current.update(id, data), 'update', id, data);
    },
    [handleOperation]
  );

  const remove = useCallback(
    id => {
      if (!apiMethodsRef.current.delete) throw new Error('Delete method not provided');
      return handleOperation(() => apiMethodsRef.current.delete(id), 'delete', id);
    },
    [handleOperation]
  );

  const fetch = useCallback(
    (params = {}) => {
      if (!apiMethodsRef.current.fetch) throw new Error('Fetch method not provided');
      return handleOperation(() => apiMethodsRef.current.fetch(params), 'fetch');
    },
    [handleOperation]
  );

  const refresh = useCallback(() => {
    return fetch();
  }, [fetch]);

  return {
    items,
    setItems,
    loading,
    errors,
    create,
    update,
    remove,
    fetch,
    refresh,
    clearError: key => setErrors(prev => ({ ...prev, [key]: null })),
    clearAllErrors: () => setErrors({})
  };
};

/**
 * Hook for managing selection state (checkboxes, multi-select)
 * @param {Array} items - Available items for selection
 * @param {string} keyField - Field to use as unique key
 * @returns {Object} Selection state and handlers
 */
export const useSelection = (items = [], keyField = 'id') => {
  const [selectedIds, setSelectedIds] = useState(new Set());

  // Get selected items
  const selectedItems = useMemo(
    () => items.filter(item => selectedIds.has(item[keyField])),
    [items, selectedIds, keyField]
  );

  // Selection handlers
  const toggleItem = useCallback(id => {
    setSelectedIds(prev => {
      const newSet = new Set(prev);
      if (newSet.has(id)) {
        newSet.delete(id);
      } else {
        newSet.add(id);
      }
      return newSet;
    });
  }, []);

  const selectAll = useCallback(() => {
    setSelectedIds(new Set(items.map(item => item[keyField])));
  }, [items, keyField]);

  const selectNone = useCallback(() => {
    setSelectedIds(new Set());
  }, []);

  const toggleAll = useCallback(() => {
    if (selectedIds.size === items.length) {
      selectNone();
    } else {
      selectAll();
    }
  }, [selectedIds, items.length, selectAll, selectNone]);

  const isSelected = useCallback(id => selectedIds.has(id), []);

  const isAllSelected = useMemo(
    () => items.length > 0 && selectedIds.size === items.length,
    [items.length, selectedIds.size]
  );

  const isIndeterminate = useMemo(
    () => selectedIds.size > 0 && selectedIds.size < items.length,
    [selectedIds.size, items.length]
  );

  return {
    selectedIds: Array.from(selectedIds),
    selectedItems,
    selectedCount: selectedIds.size,
    isSelected,
    isAllSelected,
    isIndeterminate,
    toggleItem,
    selectAll,
    selectNone,
    toggleAll,
    clear: selectNone
  };
};

// Helper function to get nested object values
const getNestedValue = (obj, path) => {
  if (typeof path === 'string') {
    return path.split('.').reduce((current, key) => current?.[key], obj);
  }
  return obj?.[path];
};

/**
 * React Hooks for Data Validation
 *
 * This module provides React hooks that integrate data validation seamlessly
 * with components, forms, and API calls. It ensures data integrity throughout
 * the application lifecycle.
 */

import { useState, useCallback, useMemo, useEffect, useRef } from 'react';

import {
  DataValidator,
  ValidationError,
  Schemas,
  validateApiResponse,
  validateFormData,
  createValidationMiddleware,
  BulkValidator
} from '../utils/dataValidation';

/**
 * Hook for form validation with real-time feedback
 * Provides comprehensive form validation with error handling
 */
export const useFormValidation = (schemaName, initialValues = {}, options = {}) => {
  const {
    validateOnChange = true,
    validateOnBlur = true,
    sanitizeOnChange = true,
    showWarnings = true,
    debounceMs = 300
  } = options;

  const [values, setValues] = useState(initialValues);
  const [errors, setErrors] = useState({});
  const [warnings, setWarnings] = useState({});
  const [touched, setTouched] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState(null);

  const debounceTimeoutRef = useRef(null);
  const validatorRef = useRef(
    new DataValidator({
      strict: true,
      coerceTypes: true,
      sanitizeStrings: sanitizeOnChange,
      allowUnknownFields: false
    })
  );

  const schema = useMemo(() => Schemas[schemaName], [schemaName]);

  // Validate a single field
  const validateField = useCallback(
    (fieldName, value) => {
      if (!schema?.properties?.[fieldName]) {
        return { isValid: true, errors: [], warnings: [] };
      }

      const fieldSchema = schema.properties[fieldName];
      const result = validatorRef.current.validate(value, fieldSchema, fieldName);

      return {
        isValid: result.isValid,
        errors: result.errors.map(e => e.message),
        warnings: result.warnings.map(w => w.message),
        sanitizedValue: result.data
      };
    },
    [schema]
  );

  // Validate all fields
  const validateAll = useCallback(() => {
    if (!schema) {
      return { isValid: true, errors: {}, warnings: {} };
    }

    const result = validatorRef.current.validate(values, schema);

    const fieldErrors = {};
    const fieldWarnings = {};

    result.errors.forEach(error => {
      const field = error.path || 'general';
      if (!fieldErrors[field]) fieldErrors[field] = [];
      fieldErrors[field].push(error.message);
    });

    result.warnings.forEach(warning => {
      const field = warning.path || 'general';
      if (!fieldWarnings[field]) fieldWarnings[field] = [];
      fieldWarnings[field].push(warning.message);
    });

    return {
      isValid: result.isValid,
      errors: fieldErrors,
      warnings: fieldWarnings,
      sanitizedData: result.data
    };
  }, [values, schema]);

  // Handle field changes with validation
  const handleChange = useCallback(
    fieldName => event => {
      const value = event.target ? event.target.value : event;

      setValues(prev => ({ ...prev, [fieldName]: value }));
      setSubmitError(null);

      if (validateOnChange && touched[fieldName]) {
        // Debounce validation for performance
        if (debounceTimeoutRef.current) {
          clearTimeout(debounceTimeoutRef.current);
        }

        debounceTimeoutRef.current = setTimeout(() => {
          const validation = validateField(fieldName, value);

          setErrors(prev => ({
            ...prev,
            [fieldName]: validation.errors
          }));

          if (showWarnings) {
            setWarnings(prev => ({
              ...prev,
              [fieldName]: validation.warnings
            }));
          }

          // Update value with sanitized version if different
          if (sanitizeOnChange && validation.sanitizedValue !== value) {
            setValues(prevValues => ({
              ...prevValues,
              [fieldName]: validation.sanitizedValue
            }));
          }
        }, debounceMs);
      }
    },
    [validateOnChange, touched, validateField, showWarnings, sanitizeOnChange, debounceMs]
  );

  // Handle field blur with validation
  const handleBlur = useCallback(
    fieldName => () => {
      setTouched(prev => ({ ...prev, [fieldName]: true }));

      if (validateOnBlur) {
        const validation = validateField(fieldName, values[fieldName]);

        setErrors(prev => ({
          ...prev,
          [fieldName]: validation.errors
        }));

        if (showWarnings) {
          setWarnings(prev => ({
            ...prev,
            [fieldName]: validation.warnings
          }));
        }
      }
    },
    [validateOnBlur, values, validateField, showWarnings]
  );

  // Handle form submission
  const handleSubmit = useCallback(
    async onSubmit => {
      if (!onSubmit) return false;

      setIsSubmitting(true);
      setSubmitError(null);

      try {
        const validation = validateAll();

        if (!validation.isValid) {
          setErrors(validation.errors);
          if (showWarnings) {
            setWarnings(validation.warnings);
          }

          // Mark all fields as touched to show errors
          const allTouched = Object.keys(schema?.properties || {}).reduce((acc, key) => {
            acc[key] = true;
            return acc;
          }, {});
          setTouched(allTouched);

          setIsSubmitting(false);
          return false;
        }

        const result = await onSubmit(validation.sanitizedData);
        setIsSubmitting(false);
        return result;
      } catch (error) {
        setSubmitError(error);
        setIsSubmitting(false);
        return false;
      }
    },
    [validateAll, showWarnings, schema]
  );

  // Reset form
  const reset = useCallback(
    (newValues = initialValues) => {
      setValues(newValues);
      setErrors({});
      setWarnings({});
      setTouched({});
      setSubmitError(null);
      setIsSubmitting(false);
    },
    [initialValues]
  );

  // Set field value programmatically
  const setValue = useCallback(
    (fieldName, value) => {
      setValues(prev => ({ ...prev, [fieldName]: value }));

      if (validateOnChange && touched[fieldName]) {
        const validation = validateField(fieldName, value);
        setErrors(prev => ({ ...prev, [fieldName]: validation.errors }));
        if (showWarnings) {
          setWarnings(prev => ({ ...prev, [fieldName]: validation.warnings }));
        }
      }
    },
    [validateOnChange, touched, validateField, showWarnings]
  );

  // Get field props for easy binding
  const getFieldProps = useCallback(
    fieldName => ({
      value: values[fieldName] || '',
      onChange: handleChange(fieldName),
      onBlur: handleBlur(fieldName),
      error: touched[fieldName] && errors[fieldName]?.length > 0,
      helperText: touched[fieldName]
        ? errors[fieldName]?.[0] || (showWarnings && warnings[fieldName]?.[0])
        : undefined
    }),
    [values, handleChange, handleBlur, touched, errors, warnings, showWarnings]
  );

  // Cleanup debounce timeout
  useEffect(() => {
    return () => {
      if (debounceTimeoutRef.current) {
        clearTimeout(debounceTimeoutRef.current);
      }
    };
  }, []);

  return {
    values,
    errors,
    warnings,
    touched,
    isSubmitting,
    submitError,
    isValid: Object.keys(errors).every(key => !errors[key]?.length),
    handleChange,
    handleBlur,
    handleSubmit,
    reset,
    setValue,
    getFieldProps,
    validateField,
    validateAll
  };
};

/**
 * Hook for API data validation
 * Validates API responses and provides error handling
 */
export const useApiValidation = (schemaName, options = {}) => {
  const { throwOnError = false, logErrors = true, enableCaching = true } = options;

  const [validationCache] = useState(new Map());

  const validator = useMemo(
    () => createValidationMiddleware(schemaName, { throwOnError, logErrors }),
    [schemaName, throwOnError, logErrors]
  );

  const validate = useCallback(
    data => {
      if (!data) return { isValid: false, data: null, errors: ['No data provided'] };

      const cacheKey = enableCaching ? JSON.stringify(data) : null;

      if (enableCaching && cacheKey && validationCache.has(cacheKey)) {
        return validationCache.get(cacheKey);
      }

      const result = validator(data);

      if (enableCaching && cacheKey) {
        validationCache.set(cacheKey, result);

        // Limit cache size
        if (validationCache.size > 100) {
          const firstKey = validationCache.keys().next().value;
          validationCache.delete(firstKey);
        }
      }

      return result;
    },
    [validator, enableCaching, validationCache]
  );

  const clearCache = useCallback(() => {
    validationCache.clear();
  }, [validationCache]);

  return { validate, clearCache };
};

/**
 * Hook for bulk data validation
 * Efficiently validates large datasets
 */
export const useBulkValidation = (schemaName, options = {}) => {
  const [isValidating, setIsValidating] = useState(false);
  const [progress, setProgress] = useState(0);
  const [results, setResults] = useState(null);

  const bulkValidatorRef = useRef(null);

  // Initialize bulk validator
  useEffect(() => {
    bulkValidatorRef.current = new BulkValidator(schemaName, options);
  }, [schemaName, options]);

  const validateBatch = useCallback(async (dataArray, batchSize = 100) => {
    if (!dataArray?.length) {
      setResults({ validItems: [], invalidItems: [], totalErrors: 0, totalWarnings: 0 });
      return;
    }

    setIsValidating(true);
    setProgress(0);

    try {
      const totalBatches = Math.ceil(dataArray.length / batchSize);
      const allResults = [];

      for (let i = 0; i < totalBatches; i++) {
        const start = i * batchSize;
        const end = Math.min(start + batchSize, dataArray.length);
        const batch = dataArray.slice(start, end);

        const batchResult = bulkValidatorRef.current.validateBatch(batch);
        allResults.push(batchResult);

        setProgress(((i + 1) / totalBatches) * 100);

        // Allow UI to update
        await new Promise(resolve => setTimeout(resolve, 0));
      }

      // Combine all results
      const combinedResults = {
        validItems: allResults.flatMap(r => r.validItems),
        invalidItems: allResults.flatMap(r => r.invalidItems),
        totalErrors: allResults.reduce((sum, r) => sum + r.totalErrors, 0),
        totalWarnings: allResults.reduce((sum, r) => sum + r.totalWarnings, 0)
      };

      setResults(combinedResults);
      return combinedResults;
    } finally {
      setIsValidating(false);
      setProgress(100);
    }
  }, []);

  const reset = useCallback(() => {
    setResults(null);
    setProgress(0);
    setIsValidating(false);
  }, []);

  return {
    validateBatch,
    isValidating,
    progress,
    results,
    reset,
    hasResults: !!results,
    validCount: results?.validItems?.length || 0,
    invalidCount: results?.invalidItems?.length || 0,
    errorCount: results?.totalErrors || 0,
    warningCount: results?.totalWarnings || 0
  };
};

/**
 * Hook for real-time data validation
 * Validates data as it changes in real-time
 */
export const useRealtimeValidation = (data, schemaName, options = {}) => {
  const { debounceMs = 500, enableWarnings = true } = options;

  const [validationResult, setValidationResult] = useState(null);
  const [isValidating, setIsValidating] = useState(false);

  const debounceTimeoutRef = useRef(null);
  const { validate } = useApiValidation(schemaName, {
    throwOnError: false,
    logErrors: false
  });

  // Validate data with debouncing
  useEffect(() => {
    if (debounceTimeoutRef.current) {
      clearTimeout(debounceTimeoutRef.current);
    }

    if (!data) {
      setValidationResult(null);
      return;
    }

    setIsValidating(true);

    debounceTimeoutRef.current = setTimeout(() => {
      try {
        const result = validate(data);
        setValidationResult(result);
      } catch (error) {
        setValidationResult({
          isValid: false,
          errors: [error.message],
          warnings: []
        });
      } finally {
        setIsValidating(false);
      }
    }, debounceMs);

    return () => {
      if (debounceTimeoutRef.current) {
        clearTimeout(debounceTimeoutRef.current);
      }
    };
  }, [data, validate, debounceMs]);

  return {
    isValid: validationResult?.isValid ?? null,
    errors: validationResult?.errors || [],
    warnings: enableWarnings ? validationResult?.warnings || [] : [],
    isValidating,
    validatedData: validationResult?.data,
    hasResult: validationResult !== null
  };
};

/**
 * Hook for validation state management
 * Provides centralized validation state for complex forms
 */
export const useValidationState = () => {
  const [validationStates, setValidationStates] = useState({});

  const registerField = useCallback((fieldName, initialState = {}) => {
    setValidationStates(prev => ({
      ...prev,
      [fieldName]: {
        isValid: true,
        errors: [],
        warnings: [],
        ...initialState
      }
    }));
  }, []);

  const updateFieldValidation = useCallback((fieldName, validation) => {
    setValidationStates(prev => ({
      ...prev,
      [fieldName]: {
        ...prev[fieldName],
        ...validation
      }
    }));
  }, []);

  const unregisterField = useCallback(fieldName => {
    setValidationStates(prev => {
      const newState = { ...prev };
      delete newState[fieldName];
      return newState;
    });
  }, []);

  const clearAll = useCallback(() => {
    setValidationStates({});
  }, []);

  const isFormValid = useMemo(() => {
    return Object.values(validationStates).every(state => state.isValid);
  }, [validationStates]);

  const getAllErrors = useMemo(() => {
    return Object.entries(validationStates).reduce((acc, [field, state]) => {
      if (state.errors.length > 0) {
        acc[field] = state.errors;
      }
      return acc;
    }, {});
  }, [validationStates]);

  const getAllWarnings = useMemo(() => {
    return Object.entries(validationStates).reduce((acc, [field, state]) => {
      if (state.warnings.length > 0) {
        acc[field] = state.warnings;
      }
      return acc;
    }, {});
  }, [validationStates]);

  return {
    validationStates,
    registerField,
    updateFieldValidation,
    unregisterField,
    clearAll,
    isFormValid,
    getAllErrors,
    getAllWarnings,
    errorCount: Object.values(getAllErrors).flat().length,
    warningCount: Object.values(getAllWarnings).flat().length
  };
};

export default {
  useFormValidation,
  useApiValidation,
  useBulkValidation,
  useRealtimeValidation,
  useValidationState
};

/**
 * Debug Hooks for React Components
 * Provides easy-to-use hooks for debugging React components and tracking state changes
 */

import { useEffect, useRef, useCallback, useMemo } from 'react';

import {
  ComponentDebugger,
  PerformanceDebugger,
  UserActionDebugger,
  ErrorContextEnhancer,
  DEBUG_CONFIG
} from './debugUtils';

// ===================
// COMPONENT DEBUG HOOK
// ===================

/**
 * Hook to debug component renders, props, and state changes
 * @param {string} componentName - Name of the component
 * @param {Object} props - Component props
 * @param {Object} state - Component state (optional)
 * @param {Object} options - Debug options
 */
export const useComponentDebug = (componentName, props = {}, state = null, options = {}) => {
  const {
    trackRenders = true,
    trackProps = true,
    trackState = true,
    logToConsole = false
  } = options;

  const prevPropsRef = useRef(props);
  const prevStateRef = useRef(state);
  const renderCountRef = useRef(0);

  useEffect(() => {
    if (!DEBUG_CONFIG.enabled) return;

    renderCountRef.current += 1;

    if (trackRenders) {
      ComponentDebugger.trackComponent(componentName, props, state);
    }

    if (logToConsole && renderCountRef.current > 1) {
      console.group(`ðŸ”„ ${componentName} Render #${renderCountRef.current}`);
      console.log('Props:', props);
      if (state) console.log('State:', state);
      console.groupEnd();
    }
  });

  useEffect(() => {
    if (!DEBUG_CONFIG.enabled || !trackProps) return;

    const prevProps = prevPropsRef.current;
    if (prevProps && JSON.stringify(prevProps) !== JSON.stringify(props)) {
      ComponentDebugger.trackProps(componentName, prevProps, props);

      if (logToConsole) {
        console.group(`ðŸ”§ ${componentName} Props Changed`);
        console.log('Previous:', prevProps);
        console.log('Current:', props);
        console.groupEnd();
      }
    }
    prevPropsRef.current = props;
  }, [props, componentName, trackProps, logToConsole]);

  useEffect(() => {
    if (!DEBUG_CONFIG.enabled || !trackState || !state) return;

    const prevState = prevStateRef.current;
    if (prevState && JSON.stringify(prevState) !== JSON.stringify(state)) {
      ComponentDebugger.trackStateChange(componentName, prevState, state, 'useState');

      if (logToConsole) {
        console.group(`ðŸ“Š ${componentName} State Changed`);
        console.log('Previous:', prevState);
        console.log('Current:', state);
        console.groupEnd();
      }
    }
    prevStateRef.current = state;
  }, [state, componentName, trackState, logToConsole]);

  return {
    renderCount: renderCountRef.current,
    logRender: () => {
      console.log(`${componentName} rendered ${renderCountRef.current} times`);
    }
  };
};

// ===================
// PERFORMANCE DEBUG HOOK
// ===================

/**
 * Hook to measure and debug component performance
 * @param {string} componentName - Name of the component
 * @param {Object} options - Performance options
 */
export const usePerformanceDebug = (componentName, options = {}) => {
  const {
    trackRenderTime = true,
    trackEffectTime = true,
    warnThreshold = 16 // 16ms for 60fps
  } = options;

  const renderMarkRef = useRef(null);
  const effectMarksRef = useRef(new Map());

  useEffect(() => {
    if (!DEBUG_CONFIG.enabled || !trackRenderTime) return;

    if (renderMarkRef.current) {
      const duration = renderMarkRef.current.end();

      if (duration > warnThreshold) {
        console.warn(`âš ï¸ Slow render detected in ${componentName}: ${duration.toFixed(2)}ms`);
      }
    }

    renderMarkRef.current = PerformanceDebugger.measureRender(componentName);
  });

  const measureEffect = useCallback(
    (effectName, effectFn) => {
      if (!DEBUG_CONFIG.enabled || !trackEffectTime) {
        return effectFn();
      }

      return PerformanceDebugger.measureFunction(`${componentName}-${effectName}`, effectFn);
    },
    [componentName, trackEffectTime]
  );

  const measureAsyncEffect = useCallback(
    async (effectName, asyncFn) => {
      if (!DEBUG_CONFIG.enabled || !trackEffectTime) {
        return await asyncFn();
      }

      return await PerformanceDebugger.measureAsync(`${componentName}-${effectName}`, asyncFn);
    },
    [componentName, trackEffectTime]
  );

  return {
    measureEffect,
    measureAsyncEffect,
    startMark: label => PerformanceDebugger.startMark(`${componentName}-${label}`),
    endMark: markId => PerformanceDebugger.endMark(markId)
  };
};

// ===================
// USER ACTION DEBUG HOOK
// ===================

/**
 * Hook to track and debug user interactions
 * @param {string} componentName - Name of the component
 */
export const useUserActionDebug = componentName => {
  const trackClick = useCallback(
    (event, metadata = {}) => {
      if (!DEBUG_CONFIG.enabled) return;

      UserActionDebugger.trackClick(event.target, componentName, {
        ...metadata,
        eventType: event.type,
        button: event.button,
        ctrlKey: event.ctrlKey,
        shiftKey: event.shiftKey
      });
    },
    [componentName]
  );

  const trackSubmit = useCallback(
    (formName, formData, metadata = {}) => {
      if (!DEBUG_CONFIG.enabled) return;

      UserActionDebugger.trackFormSubmit(formName, formData, componentName);
    },
    [componentName]
  );

  const trackCustomAction = useCallback(
    (action, metadata = {}) => {
      if (!DEBUG_CONFIG.enabled) return;

      UserActionDebugger.trackAction(action, componentName, metadata);
    },
    [componentName]
  );

  const trackInput = useCallback(
    (fieldName, value, metadata = {}) => {
      if (!DEBUG_CONFIG.enabled) return;

      UserActionDebugger.trackAction('input_change', componentName, {
        ...metadata,
        fieldName,
        valueType: typeof value,
        valueLength: value?.toString?.()?.length || 0
      });
    },
    [componentName]
  );

  return {
    trackClick,
    trackSubmit,
    trackCustomAction,
    trackInput
  };
};

// ===================
// ERROR CONTEXT HOOK
// ===================

/**
 * Hook to enhance errors with debug context
 * @param {string} componentName - Name of the component
 * @param {Object} context - Additional context information
 */
export const useErrorContext = (componentName, context = {}) => {
  const enhanceError = useCallback(
    (error, additionalContext = {}) => {
      if (!DEBUG_CONFIG.enabled) return error;

      return ErrorContextEnhancer.enhanceError(error, {
        component: componentName,
        ...context,
        ...additionalContext
      });
    },
    [componentName, context]
  );

  const reportError = useCallback(
    (error, additionalContext = {}) => {
      const enhancedError = enhanceError(error, additionalContext);

      // Log enhanced error for debugging
      console.error(`âŒ Error in ${componentName}:`, enhancedError);

      return enhancedError;
    },
    [componentName, enhanceError]
  );

  return {
    enhanceError,
    reportError
  };
};

// ===================
// STATE DEBUG HOOK
// ===================

/**
 * Hook to debug state changes with detailed tracking
 * @param {*} state - State value to track
 * @param {string} stateName - Name of the state
 * @param {string} componentName - Name of the component
 */
export const useStateDebug = (state, stateName, componentName) => {
  const prevStateRef = useRef(state);
  const changeCountRef = useRef(0);
  const historyRef = useRef([]);

  useEffect(() => {
    if (!DEBUG_CONFIG.enabled) return;

    const prevState = prevStateRef.current;

    if (prevState !== state) {
      changeCountRef.current += 1;

      const change = {
        timestamp: new Date().toISOString(),
        from: prevState,
        to: state,
        changeNumber: changeCountRef.current
      };

      historyRef.current.push(change);

      // Keep only last 10 changes
      if (historyRef.current.length > 10) {
        historyRef.current.shift();
      }

      ComponentDebugger.trackStateChange(
        componentName,
        { [stateName]: prevState },
        { [stateName]: state },
        `${stateName}_change`
      );

      console.log(`ðŸ”„ ${componentName}.${stateName} changed:`, {
        from: prevState,
        to: state,
        changeCount: changeCountRef.current
      });
    }

    prevStateRef.current = state;
  }, [state, stateName, componentName]);

  return {
    changeCount: changeCountRef.current,
    history: historyRef.current,
    logHistory: () => {
      console.table(historyRef.current);
    }
  };
};

// ===================
// API DEBUG HOOK
// ===================

/**
 * Hook to debug API calls and responses
 * @param {string} componentName - Name of the component making API calls
 */
export const useApiDebug = componentName => {
  const trackApiCall = useCallback(
    async (apiCall, metadata = {}) => {
      if (!DEBUG_CONFIG.enabled) return await apiCall();

      const startTime = performance.now();
      console.log(`ðŸŒ API call started from ${componentName}:`, metadata);

      try {
        const result = await apiCall();
        const duration = performance.now() - startTime;

        console.log(`âœ… API call succeeded from ${componentName}:`, {
          duration: `${duration.toFixed(2)}ms`,
          result: result?.data || result,
          metadata
        });

        UserActionDebugger.trackAction('api_success', componentName, {
          ...metadata,
          duration,
          resultType: typeof result
        });

        return result;
      } catch (error) {
        const duration = performance.now() - startTime;

        console.error(`âŒ API call failed from ${componentName}:`, {
          duration: `${duration.toFixed(2)}ms`,
          error: error.message,
          metadata
        });

        UserActionDebugger.trackAction('api_error', componentName, {
          ...metadata,
          duration,
          error: error.message
        });

        throw error;
      }
    },
    [componentName]
  );

  return {
    trackApiCall
  };
};

// ===================
// EFFECT DEBUG HOOK
// ===================

/**
 * Hook to debug useEffect dependencies and executions
 * @param {Function} effect - Effect function
 * @param {Array} deps - Dependencies array
 * @param {string} effectName - Name for the effect
 * @param {string} componentName - Component name
 */
export const useEffectDebug = (effect, deps, effectName, componentName) => {
  const prevDepsRef = useRef(deps);
  const executionCountRef = useRef(0);

  useEffect(() => {
    if (!DEBUG_CONFIG.enabled) return effect();

    executionCountRef.current += 1;
    const prevDeps = prevDepsRef.current;

    // Log effect execution
    console.group(`âš¡ Effect "${effectName}" in ${componentName} #${executionCountRef.current}`);

    if (prevDeps && deps) {
      const changedDeps = deps.reduce((acc, dep, index) => {
        if (dep !== prevDeps[index]) {
          acc.push({
            index,
            from: prevDeps[index],
            to: dep
          });
        }
        return acc;
      }, []);

      if (changedDeps.length > 0) {
        console.log('Changed dependencies:', changedDeps);
      } else {
        console.log('No dependency changes detected');
      }
    } else {
      console.log('Initial effect execution');
    }

    console.groupEnd();

    prevDepsRef.current = deps;

    const mark = PerformanceDebugger.startMark(`effect-${componentName}-${effectName}`);
    const cleanup = effect();
    mark.end();

    return cleanup;
  }, deps);

  return {
    executionCount: executionCountRef.current
  };
};

// ===================
// COMPREHENSIVE DEBUG HOOK
// ===================

/**
 * All-in-one debug hook that provides comprehensive debugging capabilities
 * @param {string} componentName - Name of the component
 * @param {Object} props - Component props
 * @param {Object} state - Component state
 * @param {Object} options - Debug options
 */
export const useDebug = (componentName, props = {}, state = null, options = {}) => {
  const componentDebug = useComponentDebug(componentName, props, state, options);
  const performanceDebug = usePerformanceDebug(componentName, options);
  const userActionDebug = useUserActionDebug(componentName);
  const errorContext = useErrorContext(componentName);
  const apiDebug = useApiDebug(componentName);

  const debugInfo = useMemo(
    () => ({
      component: componentName,
      renderCount: componentDebug.renderCount,
      props: Object.keys(props),
      state: state ? Object.keys(state) : null,
      timestamp: new Date().toISOString()
    }),
    [componentName, componentDebug.renderCount, props, state]
  );

  // Log debug summary when component unmounts
  useEffect(() => {
    return () => {
      if (DEBUG_CONFIG.enabled && options.logSummaryOnUnmount) {
        console.log(`ðŸ“‹ ${componentName} Debug Summary:`, debugInfo);
      }
    };
  }, [componentName, debugInfo, options.logSummaryOnUnmount]);

  return {
    ...componentDebug,
    ...performanceDebug,
    ...userActionDebug,
    ...errorContext,
    ...apiDebug,
    debugInfo
  };
};

// ===================
// EXPORTS
// ===================

export default {
  useComponentDebug,
  usePerformanceDebug,
  useUserActionDebug,
  useErrorContext,
  useStateDebug,
  useApiDebug,
  useEffectDebug,
  useDebug
};

import { useState } from 'react';

import {
  createNurseryBatch,
  deleteNurseryBatch,
  getNurseryBatches,
  getSeasons,
  updateNurseryBatch
} from '../services/api';

import { useApiData, useApiMutation } from './useApi';

export const useNurseryManagement = () => {
  const [openDialog, setOpenDialog] = useState(false);
  const [editingBatch, setEditingBatch] = useState(null);
  const [formData, setFormData] = useState({
    batchName: '',
    startDate: new Date(),
    initialCount: '',
    species: '',
    source: '',
    seasonId: '',
    size: '',
    capacity: '',
    status: 'Planning'
  });

  const {
    data: nurseryBatchesData,
    loading: nurseryBatchesLoading,
    error: nurseryBatchesError,
    refetch: refetchNurseryBatches
  } = useApiData(getNurseryBatches, []);

  const {
    data: seasonsData,
    loading: seasonsLoading,
    error: seasonsError
  } = useApiData(getSeasons, []);

  const { mutate: createBatchMutation, loading: createBatchLoading } =
    useApiMutation(createNurseryBatch);
  const { mutate: updateBatchMutation, loading: updateBatchLoading } =
    useApiMutation(updateNurseryBatch);
  const { mutate: deleteBatchMutation, loading: deleteBatchLoading } =
    useApiMutation(deleteNurseryBatch);

  const getInitialFormData = (batch = null) => {
    if (batch) {
      return {
        batchName:
          typeof batch.batchName === 'object'
            ? batch.batchName.en || ''
            : batch.batchName || batch.name || '',
        startDate: batch.startDate ? new Date(batch.startDate) : new Date(),
        initialCount: batch.initialCount || '',
        species: batch.species || '',
        source: batch.source || '',
        seasonId: batch.seasonId || '',
        size: batch.size || '',
        capacity: batch.capacity || '',
        status: batch.status || 'Planning'
      };
    }
    return {
      batchName: '',
      startDate: new Date(),
      initialCount: '',
      species: '',
      source: '',
      seasonId: '',
      size: '',
      capacity: '',
      status: 'Planning'
    };
  };

  const handleOpenDialog = (batch = null) => {
    setEditingBatch(batch);
    setFormData(getInitialFormData(batch));
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setEditingBatch(null);
  };

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleDateChange = date => {
    setFormData(prev => ({
      ...prev,
      startDate: date
    }));
  };

  const handleSubmit = async e => {
    handleBatchSubmit({
      e,
      formData,
      editingBatch,
      updateBatchMutation,
      createBatchMutation,
      handleCloseDialog,
      refetchNurseryBatches
    });
  };

  const handleDeleteBatch = async batchId => {
    handleBatchDelete(batchId, deleteBatchMutation, refetchNurseryBatches);
  };

  return {
    nurseryBatches: nurseryBatchesData || [],
    seasons: seasonsData || [],
    loading: nurseryBatchesLoading || seasonsLoading,
    error: nurseryBatchesError || seasonsError,
    openDialog,
    editingBatch,
    formData,
    createBatchLoading,
    updateBatchLoading,
    deleteBatchLoading,
    handleOpenDialog,
    handleCloseDialog,
    handleInputChange,
    handleDateChange,
    handleSubmit,
    handleDeleteBatch
  };
};

const handleBatchSubmit = async ({
  e,
  formData,
  editingBatch,
  updateBatchMutation,
  createBatchMutation,
  handleCloseDialog,
  refetchNurseryBatches
}) => {
  e.preventDefault();

  try {
    const batchData = {
      batchName: { en: formData.batchName },
      startDate: formData.startDate,
      initialCount: parseInt(formData.initialCount),
      species: formData.species,
      source: formData.source,
      seasonId: formData.seasonId,
      size: parseFloat(formData.size),
      capacity: parseInt(formData.capacity),
      status: formData.status
    };

    if (editingBatch) {
      await updateBatchMutation(editingBatch._id || editingBatch.id, batchData);
    } else {
      await createBatchMutation(batchData);
    }

    handleCloseDialog();
    refetchNurseryBatches();
  } catch (error) {
    console.error('Error saving batch:', error);
  }
};

const handleBatchDelete = async (batchId, deleteBatchMutation, refetchNurseryBatches) => {
  if (window.confirm('Are you sure you want to delete this nursery batch?')) {
    try {
      await deleteBatchMutation(batchId);
      refetchNurseryBatches();
    } catch (error) {
      console.error('Error deleting batch:', error);
    }
  }
};

/**
 * Optimized Data Management Hooks
 *
 * This module provides hooks that replace common inefficient data management patterns
 * with optimized alternatives using proper data structures and algorithms.
 *
 * Key optimizations:
 * - Replace array.find() with Map lookups O(n) â†’ O(1)
 * - Replace array.indexOf() with Set operations O(n) â†’ O(1)
 * - Replace filter chains with indexed searches O(nÂ²) â†’ O(n)
 * - Implement efficient pagination and sorting
 * - Provide memoized transformations and computations
 */

import { useState, useMemo, useCallback, useRef, useEffect } from 'react';

import {
  IndexedCollection,
  SelectionManager,
  SearchIndex,
  dataPerformanceMonitor
} from '../utils/optimizedDataStructures';

/**
 * Optimized replacement for useEffect data fetching patterns
 * Includes intelligent caching and deduplication
 */
export const useOptimizedApiData = (apiFunction, dependencies = [], options = {}) => {
  const {
    cacheKey = null,
    cacheTimeout = 5 * 60 * 1000, // 5 minutes
    enableDeduplication = true,
    enablePerformanceLogging = false
  } = options;

  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cache and deduplication
  const cache = useRef(new Map());
  const pendingRequests = useRef(new Map());

  const fetchData = useCallback(async () => {
    const key = cacheKey || JSON.stringify(dependencies);

    // Check cache first
    if (cache.current.has(key)) {
      const cachedData = cache.current.get(key);
      const now = Date.now();

      if (now - cachedData.timestamp < cacheTimeout) {
        setData(cachedData.data);
        setLoading(false);
        setError(null);
        return;
      }
    }

    // Check for pending request (deduplication)
    if (enableDeduplication && pendingRequests.current.has(key)) {
      try {
        const result = await pendingRequests.current.get(key);
        setData(result);
        setLoading(false);
        setError(null);
      } catch (err) {
        setError(err);
        setLoading(false);
      }
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const startTime = performance.now();

      // Create and store the promise for deduplication
      const promise = apiFunction();
      if (enableDeduplication) {
        pendingRequests.current.set(key, promise);
      }

      const result = await promise;

      // Performance logging
      if (enablePerformanceLogging) {
        const endTime = performance.now();
        console.log(`ðŸš€ API call completed in ${(endTime - startTime).toFixed(2)}ms`);
      }

      // Cache the result
      cache.current.set(key, {
        data: result,
        timestamp: Date.now()
      });

      setData(result);
      setLoading(false);
    } catch (err) {
      setError(err);
      setLoading(false);
    } finally {
      // Clean up pending request
      if (enableDeduplication) {
        pendingRequests.current.delete(key);
      }
    }
  }, [
    apiFunction,
    cacheKey,
    cacheTimeout,
    enableDeduplication,
    enablePerformanceLogging,
    ...dependencies
  ]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const clearCache = useCallback(() => {
    cache.current.clear();
  }, []);

  const refetch = useCallback(() => {
    const key = cacheKey || JSON.stringify(dependencies);
    cache.current.delete(key);
    fetchData();
  }, [fetchData, cacheKey, dependencies]);

  return { data, loading, error, refetch, clearCache };
};

/**
 * Optimized list management hook
 * Replaces inefficient array operations with indexed collections
 */
export const useOptimizedList = (initialData = [], keyField = 'id', options = {}) => {
  const {
    enableSearch = false,
    searchFields = [],
    enableSelection = false,
    enablePerformanceLogging = false
  } = options;

  // Core data structures
  const collection = useRef(new IndexedCollection(initialData, keyField));
  const searchIndex = useRef(enableSearch ? new SearchIndex(initialData, searchFields) : null);
  const selection = useRef(enableSelection ? new SelectionManager() : null);

  const [version, setVersion] = useState(0);
  const [searchTerm, setSearchTerm] = useState('');

  // Update data structures when initial data changes
  useEffect(() => {
    collection.current.clear();
    collection.current.bulkAdd(initialData);

    if (searchIndex.current) {
      searchIndex.current.clear();
      initialData.forEach(item => searchIndex.current.addItem(item));
    }

    setVersion(v => v + 1);
  }, [initialData, keyField]);

  // Memoized filtered data
  const filteredData = useMemo(() => {
    if (!enableSearch || !searchTerm.trim()) {
      return collection.current.getAll();
    }

    if (enablePerformanceLogging) {
      return dataPerformanceMonitor.measureOperation('search', () => {
        return searchIndex.current.search(searchTerm);
      });
    }

    return searchIndex.current.search(searchTerm);
  }, [searchTerm, version, enableSearch, enablePerformanceLogging]);

  // Data management actions
  const actions = useMemo(
    () => ({
      // CRUD operations - O(1) complexity
      add: item => {
        if (enablePerformanceLogging) {
          dataPerformanceMonitor.measureOperation('add', () => {
            collection.current.add(item);
            searchIndex.current?.addItem(item);
          });
        } else {
          collection.current.add(item);
          searchIndex.current?.addItem(item);
        }
        setVersion(v => v + 1);
      },

      remove: key => {
        if (enablePerformanceLogging) {
          dataPerformanceMonitor.measureOperation('remove', () => {
            collection.current.remove(key);
            searchIndex.current?.removeItem(key);
            selection.current?.deselect(key);
          });
        } else {
          collection.current.remove(key);
          searchIndex.current?.removeItem(key);
          selection.current?.deselect(key);
        }
        setVersion(v => v + 1);
      },

      update: (key, updates) => {
        const oldItem = collection.current.get(key);
        if (oldItem) {
          const newItem = { ...oldItem, ...updates };

          if (enablePerformanceLogging) {
            dataPerformanceMonitor.measureOperation('update', () => {
              collection.current.update(key, updates);
              searchIndex.current?.removeItem(key);
              searchIndex.current?.addItem(newItem);
            });
          } else {
            collection.current.update(key, updates);
            searchIndex.current?.removeItem(key);
            searchIndex.current?.addItem(newItem);
          }
          setVersion(v => v + 1);
        }
      },

      // Fast lookups - O(1) complexity
      get: key => collection.current.get(key),
      has: key => collection.current.has(key),

      // Bulk operations for better performance
      bulkAdd: items => {
        if (enablePerformanceLogging) {
          dataPerformanceMonitor.measureOperation('bulkAdd', () => {
            collection.current.bulkAdd(items);
            items.forEach(item => searchIndex.current?.addItem(item));
          });
        } else {
          collection.current.bulkAdd(items);
          items.forEach(item => searchIndex.current?.addItem(item));
        }
        setVersion(v => v + 1);
      },

      bulkRemove: keys => {
        if (enablePerformanceLogging) {
          dataPerformanceMonitor.measureOperation('bulkRemove', () => {
            collection.current.bulkRemove(keys);
            keys.forEach(key => {
              searchIndex.current?.removeItem(key);
              selection.current?.deselect(key);
            });
          });
        } else {
          collection.current.bulkRemove(keys);
          keys.forEach(key => {
            searchIndex.current?.removeItem(key);
            selection.current?.deselect(key);
          });
        }
        setVersion(v => v + 1);
      },

      // Search operations
      search: term => {
        setSearchTerm(term);
      },

      clearSearch: () => {
        setSearchTerm('');
      },

      // Selection operations (if enabled)
      ...(enableSelection
        ? {
            select: key => {
              selection.current.select(key);
              setVersion(v => v + 1);
            },
            deselect: key => {
              selection.current.deselect(key);
              setVersion(v => v + 1);
            },
            toggleSelection: key => {
              selection.current.toggle(key);
              setVersion(v => v + 1);
            },
            selectAll: () => {
              const allKeys = collection.current.getAll().map(item => item[keyField]);
              selection.current.selectAll(allKeys);
              setVersion(v => v + 1);
            },
            deselectAll: () => {
              selection.current.deselectAll();
              setVersion(v => v + 1);
            },
            isSelected: key => selection.current.isSelected(key),
            getSelected: () => selection.current.getSelected(),
            getSelectedItems: () => {
              return selection.current.getSelected().map(key => collection.current.get(key));
            }
          }
        : {})
    }),
    [keyField, enableSelection, enablePerformanceLogging]
  );

  // Computed values
  const computedValues = useMemo(
    () => ({
      totalItems: collection.current.size(),
      filteredCount: filteredData.length,
      searchTerm,
      hasSearch: enableSearch && searchTerm.trim().length > 0,
      ...(enableSelection
        ? {
            selectedCount: selection.current.size(),
            selectedItems: selection.current.getSelected(),
            isSelectionEmpty: selection.current.isEmpty()
          }
        : {})
    }),
    [filteredData.length, searchTerm, enableSearch, enableSelection, version]
  );

  return {
    data: filteredData,
    actions,
    ...computedValues
  };
};

/**
 * Optimized pagination hook
 * Efficiently handles large datasets with minimal memory usage
 */
export const useOptimizedPagination = (data = [], pageSize = 10, options = {}) => {
  const { enablePerformanceLogging = false, serverSide = false } = options;

  const [currentPage, setCurrentPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(pageSize);

  // Memoized pagination calculations
  const paginationData = useMemo(() => {
    if (serverSide) {
      // For server-side pagination, return data as-is
      return {
        items: data,
        totalPages: Math.ceil(data.length / rowsPerPage),
        totalItems: data.length,
        hasNextPage: false,
        hasPrevPage: false
      };
    }

    const totalItems = data.length;
    const totalPages = Math.ceil(totalItems / rowsPerPage);
    const startIndex = currentPage * rowsPerPage;
    const endIndex = Math.min(startIndex + rowsPerPage, totalItems);

    const calculation = () => ({
      items: data.slice(startIndex, endIndex),
      totalPages,
      totalItems,
      hasNextPage: currentPage < totalPages - 1,
      hasPrevPage: currentPage > 0,
      startIndex,
      endIndex
    });

    if (enablePerformanceLogging) {
      return dataPerformanceMonitor.measureOperation('pagination', calculation);
    }

    return calculation();
  }, [data, currentPage, rowsPerPage, serverSide, enablePerformanceLogging]);

  // Navigation handlers
  const goToPage = useCallback(
    page => {
      const targetPage = Math.max(0, Math.min(page, paginationData.totalPages - 1));
      setCurrentPage(targetPage);
    },
    [paginationData.totalPages]
  );

  const nextPage = useCallback(() => {
    if (paginationData.hasNextPage) {
      setCurrentPage(prev => prev + 1);
    }
  }, [paginationData.hasNextPage]);

  const prevPage = useCallback(() => {
    if (paginationData.hasPrevPage) {
      setCurrentPage(prev => prev - 1);
    }
  }, [paginationData.hasPrevPage]);

  const changeRowsPerPage = useCallback(newRowsPerPage => {
    setRowsPerPage(newRowsPerPage);
    setCurrentPage(0); // Reset to first page
  }, []);

  const resetPagination = useCallback(() => {
    setCurrentPage(0);
  }, []);

  return {
    ...paginationData,
    currentPage,
    rowsPerPage,
    goToPage,
    nextPage,
    prevPage,
    changeRowsPerPage,
    resetPagination
  };
};

/**
 * Optimized sorting hook
 * Efficient sorting with stable sort algorithms and memoization
 */
export const useOptimizedSorting = (data = [], options = {}) => {
  const {
    defaultSortField = null,
    defaultSortDirection = 'asc',
    enablePerformanceLogging = false,
    enableMultiSort = false
  } = options;

  const [sortConfig, setSortConfig] = useState({
    field: defaultSortField,
    direction: defaultSortDirection,
    multiSort: enableMultiSort ? [] : null
  });

  // Memoized sorted data
  const sortedData = useMemo(() => {
    if (!sortConfig.field && (!enableMultiSort || sortConfig.multiSort.length === 0)) {
      return data;
    }

    const sorting = () => {
      const result = [...data];

      if (enableMultiSort && sortConfig.multiSort.length > 0) {
        // Multi-column sorting
        result.sort((a, b) => {
          for (const sort of sortConfig.multiSort) {
            const aValue = getNestedValue(a, sort.field);
            const bValue = getNestedValue(b, sort.field);
            const comparison = compareValues(aValue, bValue, sort.direction);

            if (comparison !== 0) {
              return comparison;
            }
          }
          return 0;
        });
      } else {
        // Single column sorting
        result.sort((a, b) => {
          const aValue = getNestedValue(a, sortConfig.field);
          const bValue = getNestedValue(b, sortConfig.field);
          return compareValues(aValue, bValue, sortConfig.direction);
        });
      }

      return result;
    };

    if (enablePerformanceLogging) {
      return dataPerformanceMonitor.measureOperation('sorting', sorting);
    }

    return sorting();
  }, [data, sortConfig, enableMultiSort, enablePerformanceLogging]);

  // Sorting handlers
  const sort = useCallback(
    (field, direction = null) => {
      const newDirection =
        direction ||
        (sortConfig.field === field && sortConfig.direction === 'asc' ? 'desc' : 'asc');

      setSortConfig({
        field,
        direction: newDirection,
        multiSort: enableMultiSort ? [{ field, direction: newDirection }] : null
      });
    },
    [sortConfig, enableMultiSort]
  );

  const addSort = useCallback(
    (field, direction = 'asc') => {
      if (!enableMultiSort) return;

      setSortConfig(prev => {
        const multiSort = [...(prev.multiSort || [])];
        const existingIndex = multiSort.findIndex(s => s.field === field);

        if (existingIndex >= 0) {
          multiSort[existingIndex].direction = direction;
        } else {
          multiSort.push({ field, direction });
        }

        return {
          ...prev,
          multiSort
        };
      });
    },
    [enableMultiSort]
  );

  const removeSort = useCallback(
    field => {
      if (!enableMultiSort) return;

      setSortConfig(prev => ({
        ...prev,
        multiSort: prev.multiSort.filter(s => s.field !== field)
      }));
    },
    [enableMultiSort]
  );

  const clearSort = useCallback(() => {
    setSortConfig({
      field: null,
      direction: 'asc',
      multiSort: enableMultiSort ? [] : null
    });
  }, [enableMultiSort]);

  return {
    data: sortedData,
    sortConfig,
    sort,
    addSort,
    removeSort,
    clearSort,
    isSorted: !!sortConfig.field || (enableMultiSort && sortConfig.multiSort.length > 0)
  };
};

// Helper functions
const getNestedValue = (obj, path) => {
  return path.split('.').reduce((current, key) => current?.[key], obj);
};

const compareValues = (a, b, direction) => {
  // Handle null/undefined values
  if (a == null && b == null) return 0;
  if (a == null) return direction === 'asc' ? -1 : 1;
  if (b == null) return direction === 'asc' ? 1 : -1;

  // Type-aware comparison
  if (typeof a === 'number' && typeof b === 'number') {
    return direction === 'asc' ? a - b : b - a;
  }

  if (a instanceof Date && b instanceof Date) {
    return direction === 'asc' ? a.getTime() - b.getTime() : b.getTime() - a.getTime();
  }

  // String comparison
  const aStr = String(a).toLowerCase();
  const bStr = String(b).toLowerCase();

  if (aStr < bStr) return direction === 'asc' ? -1 : 1;
  if (aStr > bStr) return direction === 'asc' ? 1 : -1;
  return 0;
};

export default {
  useOptimizedApiData,
  useOptimizedList,
  useOptimizedPagination,
  useOptimizedSorting
};

/**
 * Custom hooks for common UI patterns
 * Extracts reusable logic to reduce code duplication
 */

import { useState, useCallback, useEffect } from 'react';

/**
 * Hook for managing modal states (open/close/reset)
 * @param {Function} onReset - Optional callback when modal closes/resets
 * @returns {Object} Modal state and control functions
 */
export const useModal = (onReset = null) => {
  const [isOpen, setIsOpen] = useState(false);
  const [data, setData] = useState(null);

  const open = useCallback((initialData = null) => {
    setData(initialData);
    setIsOpen(true);
  }, []);

  const close = useCallback(() => {
    setIsOpen(false);
    setData(null);
    if (onReset) {
      onReset();
    }
  }, [onReset]);

  const toggle = useCallback(() => {
    if (isOpen) {
      close();
    } else {
      open();
    }
  }, [isOpen, open, close]);

  // Auto-cleanup when component unmounts
  useEffect(() => {
    return () => {
      setIsOpen(false);
      setData(null);
    };
  }, []);

  return {
    isOpen,
    data,
    open,
    close,
    toggle
  };
};

/**
 * Hook for managing form states with validation
 * @param {Object} initialValues - Initial form values
 * @param {Function} onSubmit - Form submission handler
 * @param {Function} validate - Optional validation function
 * @returns {Object} Form state and handlers
 */
export const useFormState = (initialValues = {}, onSubmit = null, validate = null) => {
  const [values, setValues] = useState(initialValues);
  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState(null);

  const setValue = useCallback(
    (name, value) => {
      setValues(prev => ({ ...prev, [name]: value }));

      // Clear error for this field if it exists
      if (errors[name]) {
        setErrors(prev => ({ ...prev, [name]: null }));
      }
    },
    [errors]
  );

  const handleChange = useCallback(
    e => {
      const { name, value, type, checked } = e.target;
      const newValue = type === 'checkbox' ? checked : value;
      setValue(name, newValue);
    },
    [setValue]
  );

  const handleBlur = useCallback(
    e => {
      const { name } = e.target;
      setTouched(prev => ({ ...prev, [name]: true }));

      // Validate field if validation function provided
      if (validate && touched[name]) {
        const fieldErrors = validate({ [name]: values[name] });
        if (fieldErrors[name]) {
          setErrors(prev => ({ ...prev, [name]: fieldErrors[name] }));
        }
      }
    },
    [validate, touched, values]
  );

  const reset = useCallback(
    (newValues = initialValues) => {
      setValues(newValues);
      setErrors({});
      setTouched({});
      setSubmitError(null);
      setIsSubmitting(false);
    },
    [initialValues]
  );

  const handleSubmit = useCallback(
    async e => {
      if (e && e.preventDefault) {
        e.preventDefault();
      }

      setIsSubmitting(true);
      setSubmitError(null);

      try {
        // Validate all fields if validator provided
        if (validate) {
          const validationErrors = validate(values);
          if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
            setIsSubmitting(false);
            return false;
          }
        }

        if (onSubmit) {
          const result = await onSubmit(values);
          setIsSubmitting(false);
          return result;
        }

        setIsSubmitting(false);
        return true;
      } catch (error) {
        setSubmitError(error);
        setIsSubmitting(false);
        return false;
      }
    },
    [values, validate, onSubmit]
  );

  return {
    values,
    errors,
    touched,
    isSubmitting,
    submitError,
    setValue,
    handleChange,
    handleBlur,
    handleSubmit,
    reset,
    setError: (name, error) => setErrors(prev => ({ ...prev, [name]: error })),
    clearError: name => setErrors(prev => ({ ...prev, [name]: null })),
    clearSubmitError: () => setSubmitError(null)
  };
};

/**
 * Hook for managing confirmation dialogs
 * @returns {Object} Confirmation state and handlers
 */
export const useConfirmation = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [config, setConfig] = useState({
    title: '',
    message: '',
    onConfirm: null,
    onCancel: null
  });

  const confirm = useCallback(options => {
    setConfig({
      title: options.title || 'Confirm Action',
      message: options.message || 'Are you sure?',
      onConfirm: options.onConfirm || (() => {}),
      onCancel: options.onCancel || (() => {})
    });
    setIsOpen(true);
  }, []);

  const handleConfirm = useCallback(() => {
    if (config.onConfirm) {
      config.onConfirm();
    }
    setIsOpen(false);
  }, [config]);

  const handleCancel = useCallback(() => {
    if (config.onCancel) {
      config.onCancel();
    }
    setIsOpen(false);
  }, [config]);

  return {
    isOpen,
    config,
    confirm,
    handleConfirm,
    handleCancel
  };
};

/**
 * Hook for managing toggle states (boolean flags)
 * @param {boolean} initialValue - Initial toggle state
 * @returns {Array} [value, toggle, setTrue, setFalse]
 */
export const useToggle = (initialValue = false) => {
  const [value, setValue] = useState(initialValue);

  const toggle = useCallback(() => setValue(prev => !prev), []);
  const setTrue = useCallback(() => setValue(true), []);
  const setFalse = useCallback(() => setValue(false), []);

  return [value, toggle, setTrue, setFalse];
};

/**
 * Hook for managing local storage state
 * @param {string} key - Storage key
 * @param {*} defaultValue - Default value if key doesn't exist
 * @returns {Array} [value, setValue]
 */
export const useLocalStorage = (key, defaultValue) => {
  const [value, setValue] = useState(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : defaultValue;
    } catch (error) {
      console.warn(`Error reading localStorage key "${key}":`, error);
      return defaultValue;
    }
  });

  const setStoredValue = useCallback(
    newValue => {
      try {
        setValue(newValue);
        if (newValue === undefined) {
          window.localStorage.removeItem(key);
        } else {
          window.localStorage.setItem(key, JSON.stringify(newValue));
        }
      } catch (error) {
        console.warn(`Error setting localStorage key "${key}":`, error);
      }
    },
    [key]
  );

  return [value, setStoredValue];
};

