import { useTranslation } from 'react-i18next';

import { useApiData } from '../hooks/useApi';
import {
  getFeedInputs,
  getFeedInputsByDateRange,
  getFeedInputsByPondId,
  getPonds
} from '../services/api';

const FeedViewPage = () => {
  const { t, i18n } = useTranslation();
  const [startDate, setStartDate] = useState(new Date(Date.now() - 30 * 24 * 60 * 60 * 1000)); // 30 days ago
  const [endDate, setEndDate] = useState(new Date());
  const [pond, setPond] = useState('');
  const [search, setSearch] = useState('');
  const [filteredFeedEntries, setFilteredFeedEntries] = useState([]);

  // Fetch all feed entries
  const {
    data: feedEntriesData,
    loading: feedEntriesLoading,
    error: feedEntriesError,
    refetch: refetchFeedEntries
  } = useApiData(getFeedInputs, []);

  // Fetch ponds
  const { data: pondsData, loading: pondsLoading, error: pondsError } = useApiData(getPonds, []);

  // Loading and error states
  const isLoading = feedEntriesLoading || pondsLoading;
  const hasError = feedEntriesError || pondsError;

  // Filter feed entries based on search term
  useEffect(() => {
    if (feedEntriesData && feedEntriesData.data) {
      let filtered = feedEntriesData.data;

      // Apply search filter
      if (search) {
        filtered = filtered.filter(
          entry =>
            (entry.feedType && entry.feedType.toLowerCase().includes(search.toLowerCase())) ||
            (entry.quantity && entry.quantity.toString().includes(search))
        );
      }

      // Apply pond filter
      if (pond) {
        filtered = filtered.filter(entry => entry.pondId === pond);
      }

      setFilteredFeedEntries(filtered);
    }
  }, [feedEntriesData, search, pond]);

  const handleFilter = async () => {
    try {
      // If pond is selected, fetch feed entries for that pond
      if (pond) {
        // Note: This would require a new API endpoint to filter by date range AND pond
        // For now, we'll filter client-side
        refetchFeedEntries();
      } else {
        // Fetch feed entries by date range
        // Note: This would require implementing the date range filter in the API
        refetchFeedEntries();
      }
    } catch (error) {
      console.error('Error filtering feed entries:', error);
    }
  };

  const handleExport = () => {
    // Implementation for exporting data would go here
    console.log('Exporting data');
  };

  const formatTime = time => {
    try {
      return new Date(time).toLocaleTimeString(i18n.language, {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (e) {
      return t('invalid_time');
    }
  };

  const getPondName = pondId => {
    if (!pondsData || !pondsData.data) return 'Unknown Pond';
    const pond = pondsData.data.find(p => p._id === pondId || p.id === pondId);
    return pond ? pond.name : 'Unknown Pond';
  };

  if (isLoading) {
    return (
      <Container
        maxWidth='lg'
        sx={{
          mt: 4,
          mb: 4,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100vh'
        }}
      >
        <CircularProgress />
      </Container>
    );
  }

  if (hasError) {
    return (
      <Container maxWidth='lg' sx={{ mt: 4, mb: 4 }}>
        <Alert severity='error'>Error loading data: {feedEntriesError || pondsError}</Alert>
      </Container>
    );
  }

  // Use real data or fallback to mock data
  const feedEntries =
    filteredFeedEntries.length > 0
      ? filteredFeedEntries
      : feedEntriesData
      ? feedEntriesData.data
      : [];
  const ponds = pondsData ? pondsData.data : [];

  return (
    <Container maxWidth='lg' sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant='h4' component='h1' gutterBottom>
          Feed History
        </Typography>
        <Button variant='contained' startIcon={<DownloadIcon />} onClick={handleExport}>
          Export Data
        </Button>
      </Box>

      <Card elevation={3} sx={{ mb: 4 }}>
        <CardHeader
          title='Filter Feed Data'
          subheader='Search and filter historical feed entries'
          action={
            <IconButton>
              <FilterIcon />
            </IconButton>
          }
        />
        <CardContent>
          <LocalizationProvider dateAdapter={AdapterDateFns}>
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <DatePicker
                  label='Start Date'
                  value={startDate}
                  onChange={newValue => setStartDate(newValue)}
                  renderInput={params => <TextField {...params} fullWidth />}
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <DatePicker
                  label='End Date'
                  value={endDate}
                  onChange={newValue => setEndDate(newValue)}
                  renderInput={params => <TextField {...params} fullWidth />}
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <FormControl fullWidth>
                  <InputLabel id='pond-select-label'>Pond</InputLabel>
                  <Select
                    labelId='pond-select-label'
                    value={pond}
                    label='Pond'
                    onChange={e => setPond(e.target.value)}
                  >
                    <MenuItem value=''>
                      <em>All Ponds</em>
                    </MenuItem>
                    {ponds.map(p => (
                      <MenuItem key={p._id || p.id} value={p._id || p.id}>
                        {p.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  label='Search'
                  value={search}
                  onChange={e => setSearch(e.target.value)}
                  fullWidth
                  InputProps={{
                    endAdornment: (
                      <IconButton>
                        <SearchIcon />
                      </IconButton>
                    )
                  }}
                />
              </Grid>

              <Grid item xs={12}>
                <Button
                  variant='contained'
                  startIcon={<SearchIcon />}
                  onClick={handleFilter}
                  size='large'
                  fullWidth
                >
                  Apply Filters
                </Button>
              </Grid>
            </Grid>
          </LocalizationProvider>
        </CardContent>
      </Card>

      <Card elevation={3}>
        <CardHeader title='Feed Entries' subheader='Historical feed input records' />
        <CardContent>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Date</TableCell>
                  <TableCell>Time</TableCell>
                  <TableCell>Pond</TableCell>
                  <TableCell>Feed Type</TableCell>
                  <TableCell>Quantity (kg)</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {feedEntries.map(entry => (
                  <TableRow key={entry._id || entry.id}>
                    <TableCell>
                      {entry.date ? new Date(entry.date).toLocaleDateString(i18n.language) : 'N/A'}
                    </TableCell>
                    <TableCell>{entry.time ? formatTime(entry.time) : 'N/A'}</TableCell>
                    <TableCell>{getPondName(entry.pondId)}</TableCell>
                    <TableCell>{entry.feedType || 'N/A'}</TableCell>
                    <TableCell>{entry.quantity || 0}</TableCell>
                    <TableCell>
                      <Tooltip title='View Details'>
                        <IconButton size='small'>
                          <SearchIcon />
                        </IconButton>
                      </Tooltip>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>
      </Card>
    </Container>
  );
};

export default FeedViewPage;

import { Insights as InsightsIcon, Download as DownloadIcon } from '@mui/icons-material';
import {
  Typography,
  Grid,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Checkbox,
  ListItemText,
  OutlinedInput,
  Box,
  Chip,
  Container,
  Card,
  CardContent,
  CardHeader,
  CircularProgress,
  Alert,
  Tabs,
  Tab,
  Paper,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormLabel,
  TextField
} from '@mui/material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  LineChart,
  Line,
  ComposedChart,
  Area
} from 'recharts';

import {
  useHistoricalSeasons,
  useHistoricalPondsForCurrentSeason,
  useHistoricalPondsBySeason,
  usePondComparisonCurrentSeason,
  usePondComparisonHistorical,
  useExportComparison
} from '../hooks/useHistoricalInsights';

const HistoricalInsightsPage = () => {
  const { t } = useTranslation();

  // Mode selection: 'current' or 'historical'
  const [mode, setMode] = useState('current');

  // Current season mode states
  const [startDate, setStartDate] = useState(new Date(Date.now() - 30 * 24 * 60 * 60 * 1000)); // 30 days ago
  const [endDate, setEndDate] = useState(new Date());
  const [selectedPondA, setSelectedPondA] = useState('');
  const [selectedPondB, setSelectedPondB] = useState('');

  // Historical mode states
  const [selectedSeason1, setSelectedSeason1] = useState('');
  const [selectedSeason2, setSelectedSeason2] = useState('');
  const [selectedPondA_H, setSelectedPondA_H] = useState('');
  const [selectedPondB_H, setSelectedPondB_H] = useState('');

  // Common states
  const [selectedMetrics, setSelectedMetrics] = useState([]);
  const [comparisonData, setComparisonData] = useState(null);
  const [activeTab, setActiveTab] = useState(0);

  // Metrics options for pond comparison
  const metricOptions = [
    { id: 'temperature', name: t('water_temperature') },
    { id: 'ph', name: t('ph_level') },
    { id: 'dissolved_oxygen', name: t('dissolved_oxygen') },
    { id: 'ammonia', name: t('ammonia_level') },
    { id: 'feed_consumption', name: t('feed_consumption') },
    { id: 'average_weight', name: t('average_shrimp_weight') }
  ];

  // Fetch seasons for historical mode
  const {
    data: historicalSeasonsData,
    loading: historicalSeasonsLoading,
    error: historicalSeasonsError
  } = useHistoricalSeasons();

  // Fetch ponds for current season mode
  const {
    data: currentSeasonPondsData,
    loading: currentSeasonPondsLoading,
    error: currentSeasonPondsError
  } = useHistoricalPondsForCurrentSeason();

  // Fetch ponds for selected seasons in historical mode
  const {
    data: season1PondsData,
    loading: season1PondsLoading,
    error: season1PondsError
  } = useHistoricalPondsBySeason(selectedSeason1, [selectedSeason1]);

  const {
    data: season2PondsData,
    loading: season2PondsLoading,
    error: season2PondsError
  } = useHistoricalPondsBySeason(selectedSeason2, [selectedSeason2]);

  // Pond comparison mutations
  const {
    mutate: compareCurrentMutate,
    loading: compareCurrentLoading,
    error: compareCurrentError
  } = usePondComparisonCurrentSeason();

  const {
    mutate: compareHistoricalMutate,
    loading: compareHistoricalLoading,
    error: compareHistoricalError
  } = usePondComparisonHistorical();

  // Export mutation
  const {
    mutate: exportMutate,
    loading: exportLoading,
    error: exportError
  } = useExportComparison();

  // Loading and error states
  const isLoading =
    historicalSeasonsLoading ||
    currentSeasonPondsLoading ||
    season1PondsLoading ||
    season2PondsLoading;
  const hasError =
    historicalSeasonsError || currentSeasonPondsError || season1PondsError || season2PondsError;
  const isProcessing = compareCurrentLoading || compareHistoricalLoading;
  const hasComparisonError = compareCurrentError || compareHistoricalError;

  // Handle current season comparison
  const handleCompareCurrentSeason = async () => {
    if (!selectedPondA || !selectedPondB || selectedMetrics.length === 0) {
      return;
    }

    try {
      const comparisonResult = await compareCurrentMutate({
        pond_a_id: selectedPondA,
        pond_b_id: selectedPondB,
        start_date: startDate.toISOString().split('T')[0],
        end_date: endDate.toISOString().split('T')[0],
        metrics: selectedMetrics
      });

      if (comparisonResult.data) {
        setComparisonData(comparisonResult.data.comparison_data);
      }
    } catch (error) {
      console.error('Error comparing ponds:', error);
    }
  };

  // Handle historical comparison
  const handleCompareHistorical = async () => {
    if (!selectedPondA_H || !selectedPondB_H || selectedMetrics.length === 0) {
      return;
    }

    try {
      const comparisonResult = await compareHistoricalMutate({
        pond_a_id: selectedPondA_H,
        pond_b_id: selectedPondB_H,
        metrics: selectedMetrics
      });

      if (comparisonResult.data) {
        setComparisonData(comparisonResult.data.comparison_data);
      }
    } catch (error) {
      console.error('Error comparing ponds:', error);
    }
  };

  // Handle export data
  const handleExportData = async () => {
    try {
      let exportParams = {
        metrics: selectedMetrics,
        format: 'csv'
      };

      if (mode === 'current') {
        exportParams = {
          ...exportParams,
          pond_a_id: selectedPondA,
          pond_b_id: selectedPondB,
          mode: 'current',
          start_date: startDate.toISOString().split('T')[0],
          end_date: endDate.toISOString().split('T')[0]
        };
      } else {
        exportParams = {
          ...exportParams,
          pond_a_id: selectedPondA_H,
          pond_b_id: selectedPondB_H,
          mode: 'historical'
        };
      }

      await exportMutate(exportParams);
    } catch (error) {
      console.error('Error exporting data:', error);
    }
  };

  // Format data for charts
  const formatChartData = metricData => {
    if (
      !metricData ||
      !metricData.pond_a_data ||
      !metricData.pond_b_data ||
      !metricData.differences
    ) {
      return [];
    }

    // For historical mode, data is day-based; for current mode, it's timestamp-based
    if (mode === 'historical') {
      // Create maps for day-based data
      const pondADayMap = new Map();
      const pondBDayMap = new Map();

      // Convert pond A data to day numbers
      metricData.pond_a_data.forEach(item => {
        const dayNumber =
          Math.floor(
            (new Date(item.timestamp) - new Date(comparisonData.pond_a.season.startDate)) /
              (1000 * 60 * 60 * 24)
          ) + 1;
        pondADayMap.set(dayNumber, item.value);
      });

      // Convert pond B data to day numbers
      metricData.pond_b_data.forEach(item => {
        const dayNumber =
          Math.floor(
            (new Date(item.timestamp) - new Date(comparisonData.pond_b.season.startDate)) /
              (1000 * 60 * 60 * 24)
          ) + 1;
        pondBDayMap.set(dayNumber, item.value);
      });

      // Create a map for differences (day-based)
      const diffMap = new Map(metricData.differences.map(item => [item.day, item.difference]));

      // Get all unique days
      const allDays = new Set([
        ...Array.from(pondADayMap.keys()),
        ...Array.from(pondBDayMap.keys()),
        ...metricData.differences.map(item => item.day)
      ]);

      // Format data for the chart (day-based)
      return Array.from(allDays)
        .map(day => {
          return {
            day: `${t('day')} ${day}`,
            pondA: pondADayMap.has(day) ? pondADayMap.get(day) : null,
            pondB: pondBDayMap.has(day) ? pondBDayMap.get(day) : null,
            difference: diffMap.get(day) || null
          };
        })
        .sort((a, b) => {
          const dayA = parseInt(a.day.replace(`${t('day')} `, ''));
          const dayB = parseInt(b.day.replace(`${t('day')} `, ''));
          return dayA - dayB;
        });
    }
    // Current season mode (timestamp-based)
    // Create a map for easier lookup
    const pondAMap = new Map(metricData.pond_a_data.map(item => [item.timestamp, item.value]));
    const pondBMap = new Map(metricData.pond_b_data.map(item => [item.timestamp, item.value]));
    const diffMap = new Map(metricData.differences.map(item => [item.timestamp, item.difference]));

    // Get all unique dates
    const allDates = new Set([
      ...metricData.pond_a_data.map(item => new Date(item.timestamp).toISOString().split('T')[0]),
      ...metricData.pond_b_data.map(item => new Date(item.timestamp).toISOString().split('T')[0])
    ]);

    // Format data for the chart
    return Array.from(allDates)
      .map(date => {
        const timestamp = new Date(date).toISOString();
        return {
          date,
          pondA: pondAMap.get(timestamp) || null,
          pondB: pondBMap.get(timestamp) || null,
          difference: diffMap.get(timestamp) || null
        };
      })
      .sort((a, b) => new Date(a.date) - new Date(b.date));
  };

  // Get pond name by ID
  const getPondName = pondId => {
    if (mode === 'current') {
      if (!currentSeasonPondsData || !currentSeasonPondsData.ponds) return '';
      const pond = currentSeasonPondsData.ponds.find(p => p.id === pondId);
      return pond ? `${pond.name} (${pond.season?.name || 'Unknown Season'})` : '';
    }
    // For historical mode, we need to check both season's ponds
    if (season1PondsData && season1PondsData.ponds) {
      const pond = season1PondsData.ponds.find(p => p.id === pondId);
      if (pond) return `${pond.name} (${pond.season?.name || 'Unknown Season'})`;
    }
    if (season2PondsData && season2PondsData.ponds) {
      const pond = season2PondsData.ponds.find(p => p.id === pondId);
      if (pond) return `${pond.name} (${pond.season?.name || 'Unknown Season'})`;
    }
    return '';
  };

  // Handle tab change
  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  if (isLoading) {
    return (
      <Container
        maxWidth='lg'
        sx={{
          mt: 4,
          mb: 4,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100vh'
        }}
      >
        <CircularProgress />
      </Container>
    );
  }

  if (hasError) {
    return (
      <Container maxWidth='lg' sx={{ mt: 4, mb: 4 }}>
        <Alert severity='error'>
          Error loading data:{' '}
          {historicalSeasonsError?.message ||
            currentSeasonPondsError?.message ||
            season1PondsError?.message ||
            season2PondsError?.message ||
            'Unknown error occurred'}
        </Alert>
      </Container>
    );
  }

  if (hasComparisonError) {
    return (
      <Container maxWidth='lg' sx={{ mt: 4, mb: 4 }}>
        <Alert severity='error'>
          Error comparing ponds:{' '}
          {compareCurrentError?.message ||
            compareHistoricalError?.message ||
            'Unknown error occurred'}
        </Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth='lg' sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant='h4' component='h1' gutterBottom>
          {t('historical_insights')}
        </Typography>
        {comparisonData && (
          <Button
            variant='outlined'
            startIcon={<DownloadIcon />}
            onClick={handleExportData}
            disabled={exportLoading}
          >
            {exportLoading ? `${t('exporting')}...` : t('export_data')}
          </Button>
        )}
      </Box>

      <Card elevation={3} sx={{ mb: 4 }}>
        <CardHeader title={t('comparison_mode')} subheader={t('select_the_type_of_comparison')} />
        <CardContent>
          <FormControl component='fieldset'>
            <RadioGroup row value={mode} onChange={e => setMode(e.target.value)}>
              <FormControlLabel
                value='current'
                control={<Radio />}
                label={t('current_season_comparison')}
              />
              <FormControlLabel
                value='historical'
                control={<Radio />}
                label={t('historical_comparison')}
              />
            </RadioGroup>
          </FormControl>
        </CardContent>
      </Card>

      <Card elevation={3} sx={{ mb: 4 }}>
        <CardHeader
          title={mode === 'current' ? t('current_season_comparison') : t('historical_comparison')}
          subheader={
            mode === 'current'
              ? t('current_season_comparison_desc')
              : t('historical_comparison_desc')
          }
        />
        <CardContent>
          <LocalizationProvider dateAdapter={AdapterDateFns}>
            <Grid container spacing={3}>
              {mode === 'current' ? (
                // Current Season Mode
                <>
                  <Grid item xs={12} md={6}>
                    <DatePicker
                      label={t('start_date')}
                      value={startDate}
                      onChange={newValue => setStartDate(newValue)}
                      renderInput={params => <TextField {...params} fullWidth />}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <DatePicker
                      label={t('end_date')}
                      value={endDate}
                      onChange={newValue => setEndDate(newValue)}
                      renderInput={params => <TextField {...params} fullWidth />}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth>
                      <InputLabel id='pond-a-select-label'>{t('pond_a')}</InputLabel>
                      <Select
                        labelId='pond-a-select-label'
                        value={selectedPondA}
                        onChange={e => setSelectedPondA(e.target.value)}
                        input={<OutlinedInput label={t('pond_a')} />}
                      >
                        {currentSeasonPondsData?.ponds?.map(pond => (
                          <MenuItem key={pond.id} value={pond.id}>
                            {pond.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth>
                      <InputLabel id='pond-b-select-label'>{t('pond_b')}</InputLabel>
                      <Select
                        labelId='pond-b-select-label'
                        value={selectedPondB}
                        onChange={e => setSelectedPondB(e.target.value)}
                        input={<OutlinedInput label={t('pond_b')} />}
                      >
                        {currentSeasonPondsData?.ponds?.map(pond => (
                          <MenuItem key={pond.id} value={pond.id}>
                            {pond.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                </>
              ) : (
                // Historical Mode
                <>
                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth>
                      <InputLabel id='season-1-select-label'>{t('season_1')}</InputLabel>
                      <Select
                        labelId='season-1-select-label'
                        value={selectedSeason1}
                        onChange={e => {
                          setSelectedSeason1(e.target.value);
                          setSelectedPondA_H(''); // Reset pond selection when season changes
                        }}
                        input={<OutlinedInput label={t('season_1')} />}
                      >
                        {historicalSeasonsData?.seasons?.map(season => (
                          <MenuItem key={season.id} value={season.id}>
                            {season.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth>
                      <InputLabel id='pond-a-historical-select-label'>{t('pond_a')}</InputLabel>
                      <Select
                        labelId='pond-a-historical-select-label'
                        value={selectedPondA_H}
                        onChange={e => setSelectedPondA_H(e.target.value)}
                        input={<OutlinedInput label={t('pond_a')} />}
                        disabled={!selectedSeason1}
                      >
                        {season1PondsData?.ponds?.map(pond => (
                          <MenuItem key={pond.id} value={pond.id}>
                            {pond.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth>
                      <InputLabel id='season-2-select-label'>{t('season_2')}</InputLabel>
                      <Select
                        labelId='season-2-select-label'
                        value={selectedSeason2}
                        onChange={e => {
                          setSelectedSeason2(e.target.value);
                          setSelectedPondB_H(''); // Reset pond selection when season changes
                        }}
                        input={<OutlinedInput label={t('season_2')} />}
                      >
                        {historicalSeasonsData?.seasons?.map(season => (
                          <MenuItem key={season.id} value={season.id}>
                            {season.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth>
                      <InputLabel id='pond-b-historical-select-label'>{t('pond_b')}</InputLabel>
                      <Select
                        labelId='pond-b-historical-select-label'
                        value={selectedPondB_H}
                        onChange={e => setSelectedPondB_H(e.target.value)}
                        input={<OutlinedInput label={t('pond_b')} />}
                        disabled={!selectedSeason2}
                      >
                        {season2PondsData?.ponds?.map(pond => (
                          <MenuItem key={pond.id} value={pond.id}>
                            {pond.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                </>
              )}

              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel id='metrics-select-label'>{t('metrics_to_compare')}</InputLabel>
                  <Select
                    labelId='metrics-select-label'
                    multiple
                    value={selectedMetrics}
                    onChange={e => setSelectedMetrics(e.target.value)}
                    input={<OutlinedInput label={t('metrics_to_compare')} />}
                    renderValue={selected => (
                      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                        {selected.map(value => {
                          const metric = metricOptions.find(m => m.id === value);
                          return <Chip key={value} label={metric?.name || value} size='small' />;
                        })}
                      </Box>
                    )}
                  >
                    {metricOptions.map(metric => (
                      <MenuItem key={metric.id} value={metric.id}>
                        <Checkbox checked={selectedMetrics.indexOf(metric.id) > -1} />
                        <ListItemText primary={metric.name} />
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>

              <Grid item xs={12}>
                <Button
                  variant='contained'
                  startIcon={<InsightsIcon />}
                  onClick={
                    mode === 'current' ? handleCompareCurrentSeason : handleCompareHistorical
                  }
                  size='large'
                  fullWidth
                  disabled={
                    isProcessing ||
                    (mode === 'current' &&
                      (!selectedPondA || !selectedPondB || selectedPondA === selectedPondB)) ||
                    (mode === 'historical' &&
                      (!selectedPondA_H ||
                        !selectedPondB_H ||
                        selectedPondA_H === selectedPondB_H)) ||
                    selectedMetrics.length === 0
                  }
                >
                  {isProcessing ? `${t('comparing')}...` : t('compare_ponds')}
                </Button>
              </Grid>
            </Grid>
          </LocalizationProvider>
        </CardContent>
      </Card>

      <Card elevation={3}>
        <CardHeader title={t('comparison_results')} subheader={t('pond_performance_comparison')} />
        <CardContent>
          {comparisonData ? (
            <Box>
              <Box sx={{ mb: 3 }}>
                <Typography variant='h6' gutterBottom>
                  {mode === 'current'
                    ? `${t('comparing')}: ${getPondName(selectedPondA)} vs ${getPondName(
                        selectedPondB
                      )}`
                    : `${t('comparing')}: ${getPondName(selectedPondA_H)} vs ${getPondName(
                        selectedPondB_H
                      )}`}
                </Typography>
                <Typography variant='body2' color='text.secondary'>
                  {mode === 'current'
                    ? `${t(
                        'period'
                      )}: ${startDate.toLocaleDateString()} - ${endDate.toLocaleDateString()}`
                    : `${t('season_1')}: ${comparisonData.pond_a.season?.name || 'N/A'} | ${t(
                        'season_2'
                      )}: ${comparisonData.pond_b.season?.name || 'N/A'}`}
                </Typography>
              </Box>

              <Tabs
                value={activeTab}
                onChange={handleTabChange}
                variant='scrollable'
                scrollButtons='auto'
              >
                {selectedMetrics.map(metricId => {
                  const metric = metricOptions.find(m => m.id === metricId);
                  return <Tab key={metricId} label={metric?.name || metricId} />;
                })}
              </Tabs>

              <Box sx={{ mt: 3 }}>
                {selectedMetrics.map((metricId, index) => {
                  const metric = metricOptions.find(m => m.id === metricId);
                  const metricData = comparisonData.metrics[metricId];
                  const chartData = formatChartData(metricData);

                  return (
                    <Box key={metricId} hidden={activeTab !== index}>
                      {activeTab === index && (
                        <Paper elevation={0} sx={{ p: 2 }}>
                          <Typography variant='h6' gutterBottom>
                            {metric?.name || metricId} {t('comparison')}
                          </Typography>

                          {chartData.length > 0 ? (
                            <Box sx={{ height: 400 }}>
                              <ResponsiveContainer width='100%' height='100%'>
                                <ComposedChart
                                  data={chartData}
                                  margin={{ top: 20, right: 30, left: 20, bottom: 50 }}
                                >
                                  <CartesianGrid strokeDasharray='3 3' />
                                  <XAxis
                                    dataKey={mode === 'historical' ? 'day' : 'date'}
                                    angle={-45}
                                    textAnchor='end'
                                    height={60}
                                    tick={{ fontSize: 12 }}
                                  />
                                  <YAxis />
                                  <Tooltip
                                    formatter={value => [Number(value).toFixed(2), '']}
                                    labelFormatter={label =>
                                      mode === 'historical'
                                        ? `${t('day')}: ${label}`
                                        : `${t('date')}: ${label}`
                                    }
                                  />
                                  <Legend />
                                  <Line
                                    type='monotone'
                                    dataKey='pondA'
                                    name={
                                      mode === 'current'
                                        ? getPondName(selectedPondA)
                                        : getPondName(selectedPondA_H)
                                    }
                                    stroke='#1f77b4'
                                    strokeWidth={2}
                                    dot={{ r: 2 }}
                                    activeDot={{ r: 6 }}
                                  />
                                  <Line
                                    type='monotone'
                                    dataKey='pondB'
                                    name={
                                      mode === 'current'
                                        ? getPondName(selectedPondB)
                                        : getPondName(selectedPondB_H)
                                    }
                                    stroke='#ff7f0e'
                                    strokeWidth={2}
                                    dot={{ r: 2 }}
                                    activeDot={{ r: 6 }}
                                  />
                                  <Area
                                    type='monotone'
                                    dataKey='difference'
                                    name={t('difference')}
                                    fill='#d62728'
                                    stroke='#d62728'
                                    fillOpacity={0.2}
                                    strokeWidth={1}
                                  />
                                </ComposedChart>
                              </ResponsiveContainer>
                            </Box>
                          ) : (
                            <Box sx={{ textAlign: 'center', py: 4 }}>
                              <Typography>{t('no_data_available_for_this_metric')}</Typography>
                            </Box>
                          )}

                          {/* Summary statistics */}
                          {metricData && (
                            <Box sx={{ mt: 3 }}>
                              <Grid container spacing={2}>
                                <Grid item xs={12} sm={4}>
                                  <Card variant='outlined'>
                                    <CardContent>
                                      <Typography variant='body2' color='text.secondary'>
                                        {t('data_points_pond_a')}
                                      </Typography>
                                      <Typography variant='h6'>
                                        {metricData.pond_a_data?.length || 0}
                                      </Typography>
                                    </CardContent>
                                  </Card>
                                </Grid>
                                <Grid item xs={12} sm={4}>
                                  <Card variant='outlined'>
                                    <CardContent>
                                      <Typography variant='body2' color='text.secondary'>
                                        {t('data_points_pond_b')}
                                      </Typography>
                                      <Typography variant='h6'>
                                        {metricData.pond_b_data?.length || 0}
                                      </Typography>
                                    </CardContent>
                                  </Card>
                                </Grid>
                                <Grid item xs={12} sm={4}>
                                  <Card variant='outlined'>
                                    <CardContent>
                                      <Typography variant='body2' color='text.secondary'>
                                        {t('average_difference')}
                                      </Typography>
                                      <Typography variant='h6'>
                                        {metricData.differences && metricData.differences.length > 0
                                          ? (
                                              metricData.differences.reduce(
                                                (sum, d) => sum + d.difference,
                                                0
                                              ) / metricData.differences.length
                                            ).toFixed(2)
                                          : 'N/A'}
                                      </Typography>
                                    </CardContent>
                                  </Card>
                                </Grid>
                              </Grid>
                            </Box>
                          )}
                        </Paper>
                      )}
                    </Box>
                  );
                })}
              </Box>
            </Box>
          ) : (
            <Box sx={{ textAlign: 'center', py: 4 }}>
              <InsightsIcon sx={{ fontSize: 60, color: 'primary.main', opacity: 0.3, mb: 2 }} />
              <Typography variant='h6' gutterBottom>
                {t('compare_pond_performance')}
              </Typography>
              <Typography variant='body1' color='text.secondary'>
                {t('select_comparison_mode')}
              </Typography>
            </Box>
          )}
        </CardContent>
      </Card>
    </Container>
  );
};

export default HistoricalInsightsPage;

import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import HistoryIcon from '@mui/icons-material/History';
import SearchIcon from '@mui/icons-material/Search';
import {
  Container,
  Typography,
  Box,
  Button,
  Paper,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  CircularProgress,
  Alert,
  TextField,
  InputAdornment,
  IconButton,
  ToggleButton,
  ToggleButtonGroup
} from '@mui/material';
import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useTranslation } from 'react-i18next';

import AdjustmentHistoryModal from '../components/features/inventory/AdjustmentHistoryModal'; // New import
import CurrentStockView from '../components/features/inventory/CurrentStockView'; // New import
import InventoryForm from '../components/features/inventory/InventoryForm';
import { useSeason } from '../context/SeasonContext';
import useApi from '../hooks/useApi';

const InventoryManagementPage = () => {
  const { t, i18n } = useTranslation();
  const { selectedSeason } = useSeason();
  const [inventoryItems, setInventoryItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('all');
  const [openForm, setOpenForm] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [openHistoryModal, setOpenHistoryModal] = useState(false);
  const [historyItem, setHistoryItem] = useState(null);
  const [view, setView] = useState('bought'); // 'bought' or 'stock'

  const api = useApi();

  const fetchInventoryItems = useCallback(async () => {
    // Don't fetch if no season is selected
    if (!selectedSeason || !selectedSeason._id) {
      setInventoryItems([]);
      setLoading(false);
      setError(null);
      return;
    }

    setLoading(true);
    setError(null);
    try {
      const response = await api.get(`/inventory-items?seasonId=${selectedSeason._id}`);
      setInventoryItems(Array.isArray(response) ? response : response.data || []);
    } catch (err) {
      console.error('Error fetching inventory items:', err);
      setError(t('failed_to_fetch_inventory_items'));
    } finally {
      setLoading(false);
    }
  }, [selectedSeason?._id, api, t]);

  useEffect(() => {
    if (view === 'bought') {
      fetchInventoryItems();
    } else {
      setInventoryItems([]);
    }
  }, [view, fetchInventoryItems]);

  const handleSearchChange = event => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event, newFilter) => {
    if (newFilter !== null) {
      setFilter(newFilter);
    }
  };

  const filteredItems = useMemo(() => {
    let items = inventoryItems;

    if (filter !== 'all') {
      items = items.filter(
        item => item.itemType && item.itemType.toLowerCase() === filter.toLowerCase()
      );
    }

    return items.filter(
      item =>
        (item.itemName &&
          (typeof item.itemName === 'object'
            ? (item.itemName[i18n.language] || item.itemName.en || '')
                .toLowerCase()
                .includes(searchTerm.toLowerCase())
            : item.itemName.toLowerCase().includes(searchTerm.toLowerCase()))) ||
        (item.itemType && item.itemType.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (item.supplier && item.supplier.toLowerCase().includes(searchTerm.toLowerCase()))
    );
  }, [inventoryItems, searchTerm, i18n.language, filter]);

  const itemTypes = useMemo(
    () => Array.from(new Set(inventoryItems.map(item => item.itemType).filter(Boolean))),
    [inventoryItems]
  );

  const handleOpenForm = (item = null) => {
    setEditingItem(item);
    setOpenForm(true);
  };

  const handleCloseForm = () => {
    setOpenForm(false);
    setEditingItem(null);
    fetchInventoryItems(); // Refresh data after form submission
  };

  const handleSaveForm = savedItem => {
    fetchInventoryItems(); // Refresh data
  };

  const handleOpenHistoryModal = item => {
    setHistoryItem(item);
    setOpenHistoryModal(true);
  };

  const handleCloseHistoryModal = () => {
    setOpenHistoryModal(false);
    setHistoryItem(null);
  };

  const handleDeleteItem = async id => {
    if (
      window.confirm(
        `${t('areYouSure')} ${t('delete')} ${t('inventory_item')}? ${t('action_not_reversible')}`
      )
    ) {
      try {
        await api.delete(`/inventory-items/${id}`);
        fetchInventoryItems(); // Refresh list
      } catch (err) {
        console.error('Error deleting inventory item:', err);
        setError(t('failed_to_delete_inventory_item'));
      }
    }
  };

  // Helper to get item name based on language
  const getItemName = item => {
    if (typeof item.itemName === 'object') {
      return item.itemName[i18n.language] || item.itemName.en || '';
    }
    return item.itemName || '';
  };

  return (
    <Container maxWidth='lg' sx={{ mt: 4, mb: 4 }}>
      <Box display='flex' justifyContent='space-between' alignItems='center' mb={3}>
        <Typography variant='h4' component='h1'>
          {t('inventory_management')}
        </Typography>
        <Button variant='contained' startIcon={<AddIcon />} onClick={() => handleOpenForm()}>
          {t('add_new_item')}
        </Button>
      </Box>

      <Box sx={{ mb: 2 }}>
        <ToggleButtonGroup
          color='primary'
          value={view}
          exclusive
          onChange={(e, newView) => {
            if (newView) setView(newView);
          }}
          aria-label='text alignment'
        >
          <ToggleButton value='bought' aria-label='left aligned'>
            {t('inventory_bought')}
          </ToggleButton>
          <ToggleButton value='stock' aria-label='centered'>
            {t('current_stock')}
          </ToggleButton>
        </ToggleButtonGroup>
      </Box>

      <Paper sx={{ p: 3, mb: 3 }}>
        <Box sx={{ display: 'flex', gap: 2, mb: 3, flexWrap: 'wrap' }}>
          <TextField
            fullWidth
            label={t('search_inventory')}
            variant='outlined'
            value={searchTerm}
            onChange={handleSearchChange}
            InputProps={{
              endAdornment: (
                <InputAdornment position='end'>
                  <SearchIcon />
                </InputAdornment>
              )
            }}
            sx={{ flexGrow: 1, minWidth: 300 }}
          />
          <ToggleButtonGroup size='small' value={filter} exclusive onChange={handleFilterChange}>
            <ToggleButton value='all'>{t('all')}</ToggleButton>
            {itemTypes.map(type => (
              <ToggleButton key={type} value={type.toLowerCase()}>
                {t(type.toLowerCase())}
              </ToggleButton>
            ))}
          </ToggleButtonGroup>
        </Box>
      </Paper>

      {loading && (
        <Box display='flex' justifyContent='center' mt={5}>
          <CircularProgress />
        </Box>
      )}

      {error && (
        <Alert severity='error' sx={{ mt: 2 }}>
          {error}
        </Alert>
      )}

      {!loading && !error && view === 'bought' && (
        <Paper>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>{t('itemName')}</TableCell>
                  <TableCell>{t('itemType')}</TableCell>
                  <TableCell>{t('purchaseDate')}</TableCell>
                  <TableCell>{t('supplier')}</TableCell>
                  <TableCell>{t('unit')}</TableCell>
                  <TableCell align='right'>{t('costPerUnit')}</TableCell>
                  <TableCell align='right'>{t('quantity_bought')}</TableCell>
                  <TableCell>{t('actions')}</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredItems.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={8} align='center'>
                      {t('no_inventory_items_found')}
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredItems.map(item => (
                    <TableRow
                      key={item._id}
                      sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                    >
                      <TableCell component='th' scope='row'>
                        {getItemName(item)}
                      </TableCell>
                      <TableCell>{item.itemType}</TableCell>
                      <TableCell>
                        {item.purchaseDate
                          ? new Date(item.purchaseDate).toLocaleDateString()
                          : 'N/A'}
                      </TableCell>
                      <TableCell>{item.supplier}</TableCell>
                      <TableCell>{item.unit}</TableCell>
                      <TableCell align='right'>{item.costPerUnit}</TableCell>
                      <TableCell align='right'>{item.quantityBought}</TableCell>
                      <TableCell>
                        <IconButton color='primary' onClick={() => handleOpenForm(item)}>
                          <EditIcon />
                        </IconButton>
                        <IconButton onClick={() => handleOpenHistoryModal(item)}>
                          <HistoryIcon />
                        </IconButton>
                        <IconButton color='error' onClick={() => handleDeleteItem(item._id)}>
                          <DeleteIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </Paper>
      )}

      {!loading && !error && view === 'stock' && <CurrentStockView />}

      {openForm && (
        <InventoryForm
          open={openForm}
          onClose={handleCloseForm}
          item={editingItem}
          onSave={handleSaveForm}
        />
      )}

      {openHistoryModal && (
        <AdjustmentHistoryModal
          open={openHistoryModal}
          onClose={handleCloseHistoryModal}
          item={historyItem}
        />
      )}
    </Container>
  );
};

export default InventoryManagementPage;

