    expect(screen.getByTestId('custom-calendar')).toBeInTheDocument();
  });

  test('shows loading state', async () => {
    const { useApiData } = require('../hooks/useApi');

    // Mock loading state
    useApiData.mockImplementation(apiFunction => {
      if (apiFunction.name === 'getNurseryBatchById') {
        return {
          data: null,
          loading: true,
          error: null,
          refetch: jest.fn()
        };
      }

      return {
        data: null,
        loading: false,
        error: null,
        refetch: jest.fn()
      };
    });

    render(
      <BrowserRouter>
        <NurseryBatchDetailPage />
      </BrowserRouter>
    );

    // Check that loading indicator is shown
    expect(screen.getByRole('progressbar')).toBeInTheDocument();
  });

  test('shows error state', async () => {
    const { useApiData } = require('../hooks/useApi');

    // Mock error state
    useApiData.mockImplementation(apiFunction => {
      if (apiFunction.name === 'getNurseryBatchById') {
        return {
          data: null,
          loading: false,
          error: 'Error loading data',
          refetch: jest.fn()
        };
      }

      return {
        data: null,
        loading: false,
        error: null,
        refetch: jest.fn()
      };
    });

    render(
      <BrowserRouter>
        <NurseryBatchDetailPage />
      </BrowserRouter>
    );

    // Check that error message is shown
    expect(screen.getByText(/error_loading_data/)).toBeInTheDocument();
  });
});

import {
