/**
 * Validated API Service
 *
 * This service demonstrates how to integrate data validation with API calls,
 * ensuring all data coming from and going to the server is properly validated.
 *
 * Features:
 * - Automatic request/response validation
 * - Schema-based data validation
 * - Error handling and recovery
 * - Performance monitoring
 * - Caching with validation
 * - Retry logic with validation
 */

import React from 'react';

import { useApiValidation } from '../hooks/useDataValidation';
import {
  validateApiResponse,
  validateFormData,
  ValidationError,
  createValidationMiddleware
} from '../utils/dataValidation';

// Base API configuration
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:3001/api';

class ValidatedApiService {
  constructor() {
    this.cache = new Map();
    this.retryAttempts = 3;
    this.retryDelay = 1000;
  }

  /**
   * Make validated API request
   */
  async request(endpoint, options = {}) {
    const {
      method = 'GET',
      data = null,
      schema = null,
      validateRequest = true,
      validateResponse = true,
      useCache = method === 'GET',
      retries = this.retryAttempts
    } = options;

    // Generate cache key
    const cacheKey = `${method}:${endpoint}:${JSON.stringify(data)}`;

    // Check cache for GET requests
    if (useCache && this.cache.has(cacheKey)) {
      const cachedData = this.cache.get(cacheKey);
      const now = Date.now();

      if (now - cachedData.timestamp < 5 * 60 * 1000) {
        // 5 minutes cache
        console.log(`ðŸ“‹ Cache hit for ${endpoint}`);
        return cachedData.data;
      }
    }

    let attempt = 0;
    let lastError;

    while (attempt <= retries) {
      try {
        // Validate request data
        if (validateRequest && data && schema) {
          console.log(`ðŸ” Validating request data for ${endpoint}`);
          const validatedData = validateFormData(data, schema);
          data = validatedData.data;
        }

        // Make API request
        console.log(
          `ðŸŒ API Request: ${method} ${endpoint}${attempt > 0 ? ` (retry ${attempt})` : ''}`
        );

        const response = await fetch(`${API_BASE_URL}${endpoint}`, {
          method,
          headers: {
            'Content-Type': 'application/json',
            ...options.headers
          },
          body: data ? JSON.stringify(data) : undefined
        });

        if (!response.ok) {
          throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }

        let responseData = await response.json();

        // Validate response data
        if (validateResponse && schema) {
          console.log(`âœ… Validating response data for ${endpoint}`);

          const validationResult = validateApiResponse(responseData, schema);

          if (!validationResult.isValid) {
            console.error('âŒ Response validation failed:', validationResult.errors);

            // Try to use the original data if validation fails but we want to be lenient
            if (options.lenientValidation) {
              console.warn('âš ï¸  Using unvalidated data due to lenient mode');
              // Log but don't throw
            } else {
              throw new ValidationError('Response validation failed', validationResult.errors);
            }
          } else {
            responseData = validationResult.data;

            if (validationResult.warnings.length > 0) {
              console.warn('âš ï¸  Response validation warnings:', validationResult.warnings);
            }
          }
        }

        // Cache successful responses
        if (useCache && method === 'GET') {
          this.cache.set(cacheKey, {
            data: responseData,
            timestamp: Date.now()
          });
        }

        console.log(`âœ… API Request successful: ${method} ${endpoint}`);
        return responseData;
      } catch (error) {
        lastError = error;
        attempt++;

        if (attempt <= retries && this.isRetryableError(error)) {
          console.warn(
            `ðŸ”„ Retrying request ${attempt}/${retries} for ${endpoint}: ${error.message}`
          );
          await this.delay(this.retryDelay * attempt);
        } else {
          break;
        }
      }
    }

    console.error(`âŒ API Request failed after ${attempt} attempts: ${endpoint}`, lastError);
    throw lastError;
  }

  /**
   * Check if error is retryable
   */
  isRetryableError(error) {
    if (error instanceof ValidationError) {
      return false; // Don't retry validation errors
    }

    return (
      error.message.includes('fetch') ||
      error.message.includes('network') ||
      error.message.includes('timeout')
    );
  }

  /**
   * Delay function for retries
   */
  delay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Clear cache
   */
  clearCache() {
    this.cache.clear();
  }

