      );
    });

    it('does not cache error responses', async () => {
      global.fetch
        .mockResolvedValueOnce({
          ok: false,
          status: 500,
          statusText: 'Internal Server Error'
        })
        .mockResolvedValueOnce({
          ok: true,
          json: () => Promise.resolve({ data: 'success' })
        });

      // First call should fail
      await expect(deduplicatedApiCall('/api/test', { method: 'GET' })).rejects.toThrow();

      // Second call should try again (not use cached error)
      const result = await deduplicatedApiCall('/api/test', { method: 'GET' });
      expect(result).toEqual({ data: 'success' });
      expect(global.fetch).toHaveBeenCalledTimes(2);
    });
  });

  describe('Edge Cases', () => {
    it('handles null/undefined endpoints', async () => {
      await expect(deduplicatedApiCall(null, { method: 'GET' })).rejects.toThrow();

      await expect(deduplicatedApiCall(undefined, { method: 'GET' })).rejects.toThrow();
    });

    it('handles empty cache clearing', () => {
      expect(() => clearCache()).not.toThrow();
      expect(() => clearCache([])).not.toThrow();
      expect(() => clearCache(['non-existent'])).not.toThrow();
    });

    it('handles invalid cache options gracefully', async () => {
      const mockResponse = { data: 'test data' };
      global.fetch.mockResolvedValue({
        ok: true,
        json: () => Promise.resolve(mockResponse)
      });

      // Should not crash with invalid options
      const result = await deduplicatedApiCall(
        '/api/test',
        { method: 'GET' },
        {
          useCache: 'invalid',
          cacheDuration: 'not-a-number',
          strategy: 'unknown-strategy'
        }
      );

      expect(result).toEqual(mockResponse);
    });
  });

  describe('Memory Management', () => {
    it('cleans up expired cache entries', async () => {
      const mockResponse = { data: 'test data' };
      global.fetch.mockResolvedValue({
        ok: true,
        json: () => Promise.resolve(mockResponse)
      });

      // Cache with very short duration
      await deduplicatedApiCall(
        '/api/test',
        { method: 'GET' },
        { useCache: true, cacheDuration: 10 }
      );

      let stats = getCacheStats();
      expect(stats.size).toBe(1);

      // Wait for expiration and trigger cleanup
      await new Promise(resolve => setTimeout(resolve, 50));

      // Make another request to trigger cleanup
      await deduplicatedApiCall('/api/other', { method: 'GET' });

      stats = getCacheStats();
      // Should have cleaned up expired entry
      expect(stats.size).toBe(1); // Only the new entry
    });
  });
});

/**
 * Performance Optimization Utilities Tests
 * Comprehensive test coverage for performance optimization features
 */

import { renderHook, act } from '@testing-library/react';
import React from 'react';

import {
  useStableCallback,
  useStableMemo,
  useStableObject,
  useStableArray,
  useDebounce,
  useThrottle,
  deepMemo,
  performanceMonitor
} from '../../utils/performanceOptimization';
import { waitUtils, mockUtils } from '../../utils/testUtils';

describe('Performance Optimization Utilities', () => {
  describe('useStableCallback', () => {
    it('returns same callback reference for same dependencies', () => {
      const mockFn = jest.fn();

      const { result, rerender } = renderHook(({ deps }) => useStableCallback(mockFn, deps), {
        initialProps: { deps: [1, 2, 3] }
      });

      const firstCallback = result.current;

      // Rerender with same dependencies
      rerender({ deps: [1, 2, 3] });

      expect(result.current).toBe(firstCallback);
    });

    it('returns new callback reference when dependencies change', () => {
      const mockFn = jest.fn();

      const { result, rerender } = renderHook(({ deps }) => useStableCallback(mockFn, deps), {
        initialProps: { deps: [1, 2, 3] }
      });

      const firstCallback = result.current;

      // Rerender with different dependencies
      rerender({ deps: [1, 2, 4] });

      expect(result.current).not.toBe(firstCallback);
    });

    it('executes callback with correct arguments', () => {
      const mockFn = jest.fn();

      const { result } = renderHook(() => useStableCallback((a, b) => mockFn(a, b), []));

      act(() => {
        result.current('arg1', 'arg2');
      });

      expect(mockFn).toHaveBeenCalledWith('arg1', 'arg2');
    });
  });

  describe('useStableMemo', () => {
    it('returns same value for same dependencies', () => {
      const expensiveCalculation = jest.fn(() => ({ result: 'calculated' }));

      const { result, rerender } = renderHook(
        ({ deps }) => useStableMemo(expensiveCalculation, deps),
        { initialProps: { deps: [1, 2, 3] } }
      );

      const firstResult = result.current;

      // Rerender with same dependencies
      rerender({ deps: [1, 2, 3] });

      expect(result.current).toBe(firstResult);
      expect(expensiveCalculation).toHaveBeenCalledTimes(1);
    });

    it('recalculates when dependencies change', () => {
      const expensiveCalculation = jest.fn(() => ({ result: Math.random() }));

      const { result, rerender } = renderHook(
        ({ deps }) => useStableMemo(expensiveCalculation, deps),
        { initialProps: { deps: [1] } }
      );

      const firstResult = result.current;

      // Rerender with different dependencies
      rerender({ deps: [2] });

      expect(result.current).not.toBe(firstResult);
      expect(expensiveCalculation).toHaveBeenCalledTimes(2);
    });
  });

  describe('useStableObject', () => {
    it('returns same reference for objects with same content', () => {
      const { result, rerender } = renderHook(({ obj }) => useStableObject(obj), {
        initialProps: { obj: { a: 1, b: 2 } }
      });

      const firstResult = result.current;

      // Rerender with object with same content but different reference
      rerender({ obj: { a: 1, b: 2 } });

      expect(result.current).toBe(firstResult);
