/**
 * CORS Preflight Handler
 * Handles CORS preflight failures with retry limits and graceful degradation
 */

import React from 'react';

import logger from './logger';
import { safeNavigateHome } from './safeNavigation';

// Configuration constants
const MAX_RETRY_ATTEMPTS = 3;
const RETRY_DELAY_BASE = 1000; // 1 second
const MAX_RETRY_DELAY = 8000; // 8 seconds max
const PREFLIGHT_TIMEOUT = 10000; // 10 seconds
const CORS_ERROR_THRESHOLD = 5; // Show error page after 5 consecutive CORS failures
const RESET_TIMEOUT = 300000; // Reset failure count after 5 minutes

// Global state for tracking CORS failures
class CORSFailureTracker {
  constructor() {
    this.failures = new Map(); // endpoint -> failure info
    this.globalFailureCount = 0;
    this.lastResetTime = Date.now();
    this.isInErrorState = false;
    this.isCORSProtectionEnabled = false;
  }

  recordFailure(endpoint, error) {
    const now = Date.now();

    // Reset counts if enough time has passed
    if (now - this.lastResetTime > RESET_TIMEOUT) {
      this.reset();
    }

    const current = this.failures.get(endpoint) || { count: 0, firstFailure: now, lastFailure: 0 };
    current.count += 1;
    current.lastFailure = now;
    current.lastError = error;

    this.failures.set(endpoint, current);
    this.globalFailureCount += 1;

    logger.warn(`CORS failure #${current.count} for ${endpoint}:`, error);

    // Check if we should enter error state
    if (this.globalFailureCount >= CORS_ERROR_THRESHOLD) {
      this.enterErrorState();
    }
  }

  getFailureCount(endpoint) {
    return this.failures.get(endpoint)?.count || 0;
  }

  shouldRetry(endpoint) {
    const failureInfo = this.failures.get(endpoint);
    return !failureInfo || failureInfo.count < MAX_RETRY_ATTEMPTS;
  }

  enterErrorState() {
    if (!this.isInErrorState) {
      this.isInErrorState = true;
      logger.error('CORS Error State: Too many preflight failures, redirecting to error page');
      this.showCORSErrorPage();
    }
  }

  showCORSErrorPage() {
    // Create and show a CORS-specific error overlay
    const errorOverlay = this.createCORSErrorOverlay();
    document.body.appendChild(errorOverlay);
  }

  createCORSErrorOverlay() {
    const overlay = document.createElement('div');
    overlay.id = 'cors-error-overlay';
    overlay.style.cssText = `
      position: fixed;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: rgba(0, 0, 0, 0.8);
      display: flex;
      align-items: center;
      justify-content: center;
      z-index: 10000;
      font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    `;

    overlay.innerHTML = `
      <div style="
        background: white;
        border-radius: 8px;
        padding: 32px;
        max-width: 500px;
        text-align: center;
        box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
      ">
        <div style="color: #d32f2f; font-size: 48px; margin-bottom: 16px;">ðŸŒ</div>
        <h2 style="color: #333; margin: 0 0 16px 0;">Connection Problem</h2>
        <p style="color: #666; margin: 0 0 24px 0;">
          We're having trouble connecting to our server. This might be due to:
        </p>
        <ul style="text-align: left; color: #666; margin: 0 0 24px 0;">
          <li>Network connectivity issues</li>
          <li>Server maintenance</li>
          <li>Firewall or security settings</li>
        </ul>
        <div style="display: flex; gap: 12px; justify-content: center;">
          <button 
            onclick="window.location.reload()" 
            style="
              background: #1976d2;
              color: white;
              border: none;
              padding: 12px 24px;
              border-radius: 4px;
              cursor: pointer;
              font-size: 14px;
            "
          >
            Reload Page
          </button>
          <button 
            onclick="window.corsTracker.goHome()" 
            style="
              background: #666;
              color: white;
              border: none;
              padding: 12px 24px;
              border-radius: 4px;
              cursor: pointer;
              font-size: 14px;
            "
          >
            Go Home
          </button>
        </div>
        <p style="color: #999; font-size: 12px; margin: 16px 0 0 0;">
          If this problem persists, please contact support.
        </p>
      </div>
    `;

    return overlay;
  }

  goHome() {
    // Remove error overlay
    const overlay = document.getElementById('cors-error-overlay');
    if (overlay) {
      overlay.remove();
    }
    // Navigate home safely
    safeNavigateHome();
  }

  enableCORSProtection() {
    logger.info('ðŸ›¡ï¸ CORS protection enabled');
    this.isCORSProtectionEnabled = true;
  }

  reset() {
    this.failures.clear();
    this.globalFailureCount = 0;
    this.lastResetTime = Date.now();
    this.isInErrorState = false;

    // Remove error overlay if present
    const overlay = document.getElementById('cors-error-overlay');
    if (overlay) {
      overlay.remove();
    }

    logger.info('ðŸ”„ CORS failure tracking reset');
  }
}

// Global tracker instance
const corsTracker = new CORSFailureTracker();

// Make it available globally for the error overlay
if (typeof window !== 'undefined') {
  window.corsTracker = corsTracker;
}

/**
 * Calculate exponential backoff delay
 */
const calculateBackoffDelay = retryCount => {
  return Math.min(RETRY_DELAY_BASE * Math.pow(2, retryCount - 1), MAX_RETRY_DELAY);
};

/**
 * Check if error is CORS-related
 */
const isCORSError = error => {
  return (
    (error.name === 'TypeError' && error.message.includes('fetch')) ||
    error.message.includes('CORS') ||
    error.message.includes('preflight') ||
    error.message.includes('cross-origin') ||
    error.name === 'AbortError' ||
    error.message.includes('Network request failed')
  );
};

/**
 * Enhanced fetch wrapper that handles CORS preflight failures
 */
export const corsAwareFetch = async (url, options = {}) => {
  const endpoint = url.toString();
  const method = options.method || 'GET';

  // Skip tracking for simple requests that don't trigger preflight
  const triggersPreflightRequest =
    (method !== 'GET' && method !== 'HEAD' && method !== 'POST') ||
    (options.headers &&
      Object.keys(options.headers).some(
        header =>
          !['accept', 'accept-language', 'content-language', 'content-type'].includes(
            header.toLowerCase()
          )
      ));

  let retryCount = 0;
  let lastError = null;

  while (retryCount <= MAX_RETRY_ATTEMPTS) {
    try {
      // Check if we should even attempt this request
      if (corsTracker.isInErrorState) {
        throw new Error('CORS Error State: Too many preflight failures');
      }

      if (retryCount > 0 && !corsTracker.shouldRetry(endpoint)) {
        throw new Error(`Max CORS retries exceeded for ${endpoint}`);
      }

      // Add timeout to detect hanging preflight requests
      const timeoutController = new AbortController();
      const timeoutId = setTimeout(() => {
        timeoutController.abort();
      }, PREFLIGHT_TIMEOUT);

      const fetchOptions = {
        ...options,
        signal: timeoutController.signal
      };

      const response = await fetch(url, fetchOptions);

      // Clear timeout if request succeeded
      clearTimeout(timeoutId);

      // Request succeeded, reset failure count for this endpoint
      if (corsTracker.failures.has(endpoint)) {
        corsTracker.failures.delete(endpoint);
      }

      return response;
    } catch (error) {
      lastError = error;

      // Check if this is a CORS-related error
      if (isCORSError(error) && triggersPreflightRequest) {
        corsTracker.recordFailure(endpoint, error);

        retryCount++;

        const shouldRetry = retryCount <= MAX_RETRY_ATTEMPTS && corsTracker.shouldRetry(endpoint);

        if (shouldRetry) {
          logger.warn(
            `CORS-aware fetch failed for ${endpoint}, retrying (${
              retryCount + 1
            }/${MAX_RETRY_ATTEMPTS}):`,
            error
          );
        }

        if (shouldRetry) {
          // Calculate exponential backoff delay
          const delay = calculateBackoffDelay(retryCount);
          await new Promise(resolve => setTimeout(resolve, delay));
          continue;
        }
      }

      // Non-CORS error or max retries exceeded
      throw error;
    }
  }

  throw lastError;
};

/**
 * Wrap the global fetch to use CORS-aware version
 */
export const enableCORSProtection = () => {
  if (typeof window !== 'undefined' && window.fetch) {
    const originalFetch = window.fetch;

    window.fetch = async (url, options) => {
      try {
        return await corsAwareFetch(url, options);
      } catch (error) {
        // If CORS-aware fetch fails, fall back to original fetch for one final attempt
        logger.warn('CORS-aware fetch failed, attempting fallback:', error);
        try {
          return await originalFetch(url, options);
        } catch (fallbackError) {
          // Both failed, throw the original error
          throw error;
        }
      }
    };

    corsTracker.enableCORSProtection();
  }
};

/**
 * Check if current error state should show CORS error page
 */
export const shouldShowCORSErrorPage = () => {
  return corsTracker.isInErrorState;
};

/**
 * Manually reset CORS failure tracking
 */
export const resetCORSFailures = () => {
  corsTracker.reset();
};

/**
 * Get current CORS failure statistics
 */
export const getCORSStats = () => {
  return {
    globalFailureCount: corsTracker.globalFailureCount,
    isInErrorState: corsTracker.isInErrorState,
    endpointFailures: Object.fromEntries(corsTracker.failures),
    timeSinceLastReset: Date.now() - corsTracker.lastResetTime
  };
};

/**
 * Hook for React components to check CORS status
 */
export const useCORSStatus = () => {
  const [isInErrorState, setIsInErrorState] = React.useState(corsTracker.isInErrorState);
  const [failureCount, setFailureCount] = React.useState(corsTracker.globalFailureCount);

  React.useEffect(() => {
    const checkStatus = () => {
      setIsInErrorState(corsTracker.isInErrorState);
      setFailureCount(corsTracker.globalFailureCount);
    };

    const interval = setInterval(checkStatus, 1000);
    return () => clearInterval(interval);
  }, []);

  return {
    isInErrorState,
    failureCount,
    reset: resetCORSFailures,
    stats: getCORSStats()
  };
};

export const CorsPreflightHandler = {
  corsAwareFetch,
  enableCORSProtection,
  shouldShowCORSErrorPage,
  resetCORSFailures,
  getCORSStats,
  useCORSStatus
};

export default CorsPreflightHandler;

/**
 * Comprehensive Data Validation System
 *
 * This module provides runtime data validation for API responses, user inputs,
 * and data transformations. It ensures data integrity and prevents runtime errors
 * caused by unexpected data structures.
 *
 * Features:
 * - Schema-based validation
 * - Type checking and coercion
 * - Custom validation rules
 * - Sanitization and transformation
 * - Error reporting and recovery
 * - Performance optimized validation
 */

// ===================
// VALIDATION TYPES
// ===================

export const ValidationTypes = {
  STRING: 'string',
  NUMBER: 'number',
  BOOLEAN: 'boolean',
  ARRAY: 'array',
  OBJECT: 'object',
  DATE: 'date',
  EMAIL: 'email',
  URL: 'url',
  UUID: 'uuid',
  ENUM: 'enum',
  CURRENCY: 'currency',
  PHONE: 'phone',
  REQUIRED: 'required',
  OPTIONAL: 'optional',
  NULL: 'null',
  UNDEFINED: 'undefined'
};

export const ValidationSeverity = {
  ERROR: 'error',
  WARNING: 'warning',
  INFO: 'info'
};

// ===================
// CORE VALIDATION CLASS
// ===================

export class DataValidator {
  constructor(options = {}) {
    this.options = {
      strict: false,
      coerceTypes: true,
      sanitizeStrings: true,
      allowUnknownFields: false,
      dateFormat: 'ISO',
      logErrors: process.env.NODE_ENV === 'development',
      ...options
    };

    this.errors = [];
    this.warnings = [];
    this.cache = new Map();
  }

  /**
   * Validate data against a schema
   * @param {any} data - Data to validate
   * @param {Object} schema - Validation schema
   * @param {string} path - Current validation path for error reporting
   * @returns {Object} Validation result with data, errors, and warnings
   */
  validate(data, schema, path = '') {
    this.errors = [];
    this.warnings = [];

    const result = this._validateValue(data, schema, path);

    return {
      isValid: this.errors.length === 0,
      data: result,
      errors: [...this.errors],
      warnings: [...this.warnings],
      hasWarnings: this.warnings.length > 0
    };
  }

  /**
   * Validate and sanitize data in one step
   * @param {any} data - Data to process
   * @param {Object} schema - Validation schema
   * @returns {Object} Processed result
   */
  validateAndSanitize(data, schema) {
    const validation = this.validate(data, schema);

    if (!validation.isValid) {
      throw new ValidationError('Validation failed', validation.errors);
    }

    return {
      data: validation.data,
      warnings: validation.warnings
    };
  }

  /**
   * Internal validation method
   */
  _validateValue(value, schema, path) {
    // Handle null/undefined
    if (value == null) {
      return this._handleNullValue(value, schema, path);
    }

    // Handle array validation
    if (schema.type === ValidationTypes.ARRAY) {
      return this._validateArray(value, schema, path);
    }

    // Handle object validation
    if (schema.type === ValidationTypes.OBJECT) {
      return this._validateObject(value, schema, path);
    }

    // Handle primitive type validation
    return this._validatePrimitive(value, schema, path);
  }

  _handleNullValue(value, schema, path) {
    if (schema.required) {
      this._addError(`Required field is missing`, path);
      return schema.default !== undefined ? schema.default : value;
    }

    if (schema.nullable === false && value === null) {
      this._addError(`Field cannot be null`, path);
      return schema.default !== undefined ? schema.default : value;
    }

    return schema.default !== undefined ? schema.default : value;
  }

  _validateArray(value, schema, path) {
    if (!Array.isArray(value)) {
      if (this.options.coerceTypes && typeof value === 'string') {
        try {
          value = JSON.parse(value);
        } catch {
          this._addError(`Expected array, got ${typeof value}`, path);
          return [];
        }
      } else {
        this._addError(`Expected array, got ${typeof value}`, path);
        return [];
      }
    }

    // Validate array constraints
    if (schema.minLength !== undefined && value.length < schema.minLength) {
      this._addError(`Array must have at least ${schema.minLength} items`, path);
    }

    if (schema.maxLength !== undefined && value.length > schema.maxLength) {
      this._addError(`Array must have at most ${schema.maxLength} items`, path);
    }

    // Validate array items
    if (schema.items) {
      return value.map((item, index) =>
        this._validateValue(item, schema.items, `${path}[${index}]`)
      );
    }

    return value;
  }

  _validateObject(value, schema, path) {
    if (typeof value !== 'object' || Array.isArray(value)) {
      this._addError(`Expected object, got ${typeof value}`, path);
      return {};
    }

    const result = {};
    const processedKeys = new Set();

    // Validate defined properties
    if (schema.properties) {
      Object.entries(schema.properties).forEach(([key, propertySchema]) => {
        const propertyPath = path ? `${path}.${key}` : key;
        result[key] = this._validateValue(value[key], propertySchema, propertyPath);
        processedKeys.add(key);
      });
    }

    // Handle unknown properties
    Object.keys(value).forEach(key => {
      if (!processedKeys.has(key)) {
        if (this.options.allowUnknownFields) {
          result[key] = value[key];
        } else if (this.options.strict) {
          this._addError(`Unknown property: ${key}`, path);
        } else {
          this._addWarning(`Unknown property: ${key}`, path);
          result[key] = value[key];
        }
      }
    });

    return result;
  }

  _validatePrimitive(value, schema, path) {
    let result = value;

    // Type validation and coercion
    switch (schema.type) {
      case ValidationTypes.STRING:
        result = this._validateString(value, schema, path);
        break;
      case ValidationTypes.NUMBER:
        result = this._validateNumber(value, schema, path);
        break;
      case ValidationTypes.BOOLEAN:
        result = this._validateBoolean(value, schema, path);
        break;
      case ValidationTypes.DATE:
        result = this._validateDate(value, schema, path);
        break;
      case ValidationTypes.EMAIL:
        result = this._validateEmail(value, schema, path);
        break;
      case ValidationTypes.URL:
        result = this._validateUrl(value, schema, path);
        break;
      case ValidationTypes.UUID:
        result = this._validateUuid(value, schema, path);
        break;
      case ValidationTypes.ENUM:
        result = this._validateEnum(value, schema, path);
        break;
      case ValidationTypes.CURRENCY:
        result = this._validateCurrency(value, schema, path);
        break;
      default:
        this._addWarning(`Unknown validation type: ${schema.type}`, path);
    }

    // Custom validation function
    if (schema.validate && typeof schema.validate === 'function') {
      try {
        const customResult = schema.validate(result, value, path);
        if (customResult !== true) {
          this._addError(customResult || 'Custom validation failed', path);
        }
      } catch (error) {
        this._addError(`Custom validation error: ${error.message}`, path);
      }
    }

    return result;
  }

  _validateString(value, schema, path) {
    if (typeof value !== 'string') {
      if (this.options.coerceTypes) {
        value = String(value);
      } else {
        this._addError(`Expected string, got ${typeof value}`, path);
        return '';
      }
    }

    // Sanitize string
    if (this.options.sanitizeStrings) {
      value = value.trim();
    }

    // Length validation
    if (schema.minLength !== undefined && value.length < schema.minLength) {
      this._addError(`String must be at least ${schema.minLength} characters`, path);
    }

    if (schema.maxLength !== undefined && value.length > schema.maxLength) {
      this._addError(`String must be at most ${schema.maxLength} characters`, path);
    }

    // Pattern validation
    if (schema.pattern && !schema.pattern.test(value)) {
      this._addError(`String does not match required pattern`, path);
    }

    return value;
  }

  _validateNumber(value, schema, path) {
    const numValue = Number(value);

    if (isNaN(numValue)) {
      this._addError(`Expected number, got ${typeof value}`, path);
      return 0;
    }

    // Range validation
    if (schema.min !== undefined && numValue < schema.min) {
      this._addError(`Number must be at least ${schema.min}`, path);
    }

    if (schema.max !== undefined && numValue > schema.max) {
      this._addError(`Number must be at most ${schema.max}`, path);
    }

    // Integer validation
    if (schema.integer && !Number.isInteger(numValue)) {
      this._addError(`Number must be an integer`, path);
    }

    return numValue;
  }

  _validateBoolean(value, schema, path) {
    if (typeof value === 'boolean') {
      return value;
    }

    if (this.options.coerceTypes) {
      if (value === 'true' || value === 1 || value === '1') return true;
      if (value === 'false' || value === 0 || value === '0') return false;
    }

    this._addError(`Expected boolean, got ${typeof value}`, path);
    return false;
  }

  _validateDate(value, schema, path) {
    let date;

    if (value instanceof Date) {
      date = value;
    } else if (typeof value === 'string' || typeof value === 'number') {
      date = new Date(value);
    } else {
      this._addError(`Invalid date format`, path);
      return null;
    }

    if (isNaN(date.getTime())) {
      this._addError(`Invalid date`, path);
      return null;
    }

    return date;
  }

  _validateEmail(value, schema, path) {
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (typeof value !== 'string' || !emailPattern.test(value)) {
      this._addError(`Invalid email format`, path);
      return '';
    }

    return value.toLowerCase();
  }

  _validateUrl(value, schema, path) {
    try {
      new URL(value);
      return value;
    } catch {
      this._addError(`Invalid URL format`, path);
      return '';
    }
  }

  _validateUuid(value, schema, path) {
    const uuidPattern =
      /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;

    if (typeof value !== 'string' || !uuidPattern.test(value)) {
      this._addError(`Invalid UUID format`, path);
      return '';
    }

    return value.toLowerCase();
  }

  _validateEnum(value, schema, path) {
    if (!schema.values || !schema.values.includes(value)) {
      this._addError(`Value must be one of: ${schema.values?.join(', ')}`, path);
      return schema.values?.[0] || null;
    }

    return value;
  }

  _validateCurrency(value, schema, path) {
    const numValue = parseFloat(value);

    if (isNaN(numValue)) {
      this._addError(`Invalid currency amount`, path);
      return 0;
    }

    // Round to 2 decimal places for currency
    return Math.round(numValue * 100) / 100;
  }

  _addError(message, path) {
    this.errors.push({
      message,
      path,
      severity: ValidationSeverity.ERROR
    });

    if (this.options.logErrors) {
      console.error(`Validation Error at ${path}: ${message}`);
    }
  }

  _addWarning(message, path) {
    this.warnings.push({
      message,
      path,
      severity: ValidationSeverity.WARNING
    });

    if (this.options.logErrors) {
      console.warn(`Validation Warning at ${path}: ${message}`);
    }
  }
}

