/**
 * Lazy Loading Utilities
 * Provides utilities for code splitting and lazy loading components
 */

import { CircularProgress, Box } from '@mui/material';
import React, { Suspense, lazy } from 'react';

// Default loading component
const DefaultLoader = ({ size = 40, text = 'Loading...' }) => (
  <Box
    display='flex'
    flexDirection='column'
    alignItems='center'
    justifyContent='center'
    minHeight='200px'
    gap={2}
  >
    <CircularProgress size={size} />
    {text && <div>{text}</div>}
  </Box>
);

// Enhanced lazy loading with error boundary and custom loader
export const createLazyComponent = (importFunc, options = {}) => {
  const {
    loader = <DefaultLoader />,
    fallback = null,
    errorBoundary = true,
    preload = false,
    retryCount = 3
  } = options;

  // Create the lazy component with retry logic
  const LazyComponent = lazy(() => {
    let retries = 0;

    const loadWithRetry = async () => {
      try {
        return await importFunc();
      } catch (error) {
        retries++;
        if (retries <= retryCount) {
          // Wait before retrying (exponential backoff)
          await new Promise(resolve => setTimeout(resolve, 1000 * retries));
          return loadWithRetry();
        }
        throw error;
      }
    };

    return loadWithRetry();
  });

  // Preload the component if requested
  if (preload) {
    importFunc().catch(() => {
      // Silently fail preloading
    });
  }

  // Wrapper component with suspense and error handling
  const WrappedComponent = props => {
    if (errorBoundary) {
      return (
        <LazyErrorBoundary fallback={fallback}>
          <Suspense fallback={loader}>
            <LazyComponent {...props} />
          </Suspense>
        </LazyErrorBoundary>
      );
    }

    return (
      <Suspense fallback={loader}>
        <LazyComponent {...props} />
      </Suspense>
    );
  };

  // Add preload method to component
  WrappedComponent.preload = () => {
    return importFunc();
  };

  return WrappedComponent;
};

// Error boundary for lazy loaded components
class LazyErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true, error };
  }

  componentDidCatch(error, errorInfo) {
    console.error('Lazy loading error:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback;
      }

      return (
        <Box
          display='flex'
          flexDirection='column'
          alignItems='center'
          justifyContent='center'
          minHeight='200px'
          gap={2}
        >
          <div>Failed to load component</div>
          <button
            onClick={() => this.setState({ hasError: false, error: null })}
            style={{
              padding: '8px 16px',
              backgroundColor: '#1976d2',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Retry
          </button>
        </Box>
      );
    }

    return this.props.children;
  }
}

// Route-based code splitting utilities
export const createLazyRoute = (importFunc, options = {}) => {
  return createLazyComponent(importFunc, {
    loader: <DefaultLoader text='Loading page...' />,
    ...options
  });
};

// Component-based code splitting for large components
export const createLazyModal = (importFunc, options = {}) => {
  return createLazyComponent(importFunc, {
    loader: <DefaultLoader size={30} text='Loading...' />,
    ...options
  });
};

// Chunk-based code splitting for feature modules
export const createLazyFeature = (importFunc, options = {}) => {
  return createLazyComponent(importFunc, {
    loader: <DefaultLoader text='Loading feature...' />,
    preload: true, // Preload features for better UX
    ...options
  });
};

// Hook for preloading components
export const usePreload = lazyComponents => {
  React.useEffect(() => {
    const preloadComponents = async () => {
      const preloadPromises = lazyComponents
        .filter(component => component && typeof component.preload === 'function')
        .map(component =>
          component.preload().catch(() => {
            // Silently fail preloading
          })
        );

      await Promise.allSettled(preloadPromises);
    };

    preloadComponents();
  }, [lazyComponents]);
};

// Bundle optimization utilities
export const bundleOptimization = {
  // Dynamic import with chunk naming
  dynamicImport: (chunkName, importFunc) => {
    return import(
      /* webpackChunkName: "[request]" */
      importFunc
    );
  },

  // Prefetch resources
  prefetchResource: (href, as = 'script') => {
    const link = document.createElement('link');
    link.rel = 'prefetch';
    link.href = href;
    link.as = as;
    document.head.appendChild(link);
  },

  // Preload critical resources
  preloadResource: (href, as = 'script') => {
    const link = document.createElement('link');
    link.rel = 'preload';
    link.href = href;
    link.as = as;
    document.head.appendChild(link);
  },

  // Check if component is in viewport for lazy loading
  isInViewport: element => {
    const rect = element.getBoundingClientRect();
    return (
      rect.top >= 0 &&
      rect.left >= 0 &&
      rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
      rect.right <= (window.innerWidth || document.documentElement.clientWidth)
    );
  }
};

// Tree shaking optimization utilities
export const optimizeImports = {
  // Import only specific functions from large libraries
  importSpecific: (library, functions) => {
    return functions.reduce((acc, func) => {
      acc[func] = require(`${library}/${func}`);
      return acc;
    }, {});
  },

  // Dynamic import for conditional features
  conditionalImport: async (condition, importFunc) => {
    if (condition) {
      return await importFunc();
    }
    return null;
  }
};

export default {
  createLazyComponent,
  createLazyRoute,
  createLazyModal,
  createLazyFeature,
  usePreload,
  bundleOptimization,
  optimizeImports,
  DefaultLoader
};

/**
 * Structured Logging Utility
 * Provides consistent logging with different levels and context
 */

export const LOG_LEVELS = {
  ERROR: 0,
  WARN: 1,
  INFO: 2,
  DEBUG: 3
};

class Logger {
  constructor(context = 'App') {
    this.context = context;
    // In production, only show errors; in development, show all logs
    this.level = process.env.NODE_ENV === 'production' ? LOG_LEVELS.ERROR : LOG_LEVELS.DEBUG;
  }

  /**
   * Log an error message
   * @param {string} message - Error message
   * @param {any} data - Additional data to log
   */
  error(message, data = null) {
    if (this.level >= LOG_LEVELS.ERROR) {
      // In a real implementation, this would send to a logging service
      // For now, we'll use a more structured approach
      const logEntry = {
        timestamp: new Date().toISOString(),
        level: 'ERROR',
        context: this.context,
        message,
        data
      };

      // Only log in development or when explicitly enabled
      if (process.env.NODE_ENV !== 'production' || process.env.ENABLE_LOGGING === 'true') {
        // We'll use console here but in a production system this would go to a logging service
        // Using console.error for proper error logging
        console.error(JSON.stringify(logEntry, null, 2));
      }
    }
  }

  /**
   * Log a warning message
   * @param {string} message - Warning message
   * @param {any} data - Additional data to log
   */
  warn(message, data = null) {
    if (this.level >= LOG_LEVELS.WARN) {
      const logEntry = {
        timestamp: new Date().toISOString(),
        level: 'WARN',
        context: this.context,
        message,
        data
      };

      if (process.env.NODE_ENV !== 'production' || process.env.ENABLE_LOGGING === 'true') {
        // Using console.warn for proper warning logging
        console.warn(JSON.stringify(logEntry, null, 2));
      }
    }
  }

  /**
   * Log an info message
   * @param {string} message - Info message
   * @param {any} data - Additional data to log
   */
  info(message, data = null) {
    if (this.level >= LOG_LEVELS.INFO) {
      const logEntry = {
        timestamp: new Date().toISOString(),
        level: 'INFO',
        context: this.context,
        message,
        data
      };

      if (process.env.NODE_ENV !== 'production' || process.env.ENABLE_LOGGING === 'true') {
        // Using console.info for proper info logging
        console.info(JSON.stringify(logEntry, null, 2));
      }
    }
  }

  /**
   * Log a debug message
   * @param {string} message - Debug message
   * @param {any} data - Additional data to log
   */
  debug(message, data = null) {
    if (this.level >= LOG_LEVELS.DEBUG) {
      const logEntry = {
        timestamp: new Date().toISOString(),
        level: 'DEBUG',
        context: this.context,
        message,
        data
      };

      if (process.env.NODE_ENV !== 'production' || process.env.ENABLE_LOGGING === 'true') {
        // Using console.log for proper debug logging
        console.log(JSON.stringify(logEntry, null, 2));
      }
    }
  }

  /**
   * Set the logging level
   * @param {number} level - Log level from LOG_LEVELS
   */
  setLevel(level) {
    this.level = level;
  }

  /**
   * Set the context for the logger
   * @param {string} context - Context name
   */
  setContext(context) {
    this.context = context;
  }
}

// Create default logger instance
const defaultLogger = new Logger();

/**
 * Create a logger with a specific context
 * @param {string} context - Context name for the logger
 * @returns {Logger} Logger instance
 */
export const createLogger = context => new Logger(context);

export default defaultLogger;

import localforage from 'localforage';

import logger from './logger';

// Initialize localForage
const offlineStore = localforage.createInstance({
  name: 'ShrimpFarmManagement',
  storeName: 'offlineData'
});

// Queue for offline data
const syncQueue = localforage.createInstance({
  name: 'ShrimpFarmManagement',
  storeName: 'syncQueue'
});

/**
 * Save data locally when offline
 * @param {string} key - Unique key for the data
 * @param {any} data - Data to save
 */
export const saveOfflineData = async (key, data) => {
  try {
    await offlineStore.setItem(key, data);
    return true;
  } catch (error) {
    logger.error('Error saving offline data:', error);
    return false;
  }
};

/**
 * Get offline data by key
 * @param {string} key - Key of the data to retrieve
 */
export const getOfflineData = async key => {
  try {
    return await offlineStore.getItem(key);
  } catch (error) {
    logger.error('Error retrieving offline data:', error);
    return null;
  }
};

/**
 * Remove offline data by key
 * @param {string} key - Key of the data to remove
 */
export const removeOfflineData = async key => {
  try {
    await offlineStore.removeItem(key);
    return true;
  } catch (error) {
    logger.error('Error removing offline data:', error);
    return false;
  }
};

/**
 * Add data to sync queue
 * @param {string} endpoint - API endpoint to sync to
 * @param {string} method - HTTP method (POST, PUT, DELETE)
 * @param {any} data - Data to sync
 * @param {string} identifier - Unique identifier for the record
 */
export const addToSyncQueue = async (endpoint, method, data, identifier) => {
  try {
    const queueItem = {
      id: Date.now() + Math.random(), // Unique ID for the queue item
      endpoint,
      method,
      data,
      identifier,
      timestamp: new Date().toISOString()
    };

    await syncQueue.setItem(queueItem.id.toString(), queueItem);
    return queueItem.id;
  } catch (error) {
    logger.error('Error adding to sync queue:', error);
    return null;
  }
};

/**
 * Process sync queue when online
 * @param {Function} apiCall - Function to make API calls
 */
export const processSyncQueue = async apiCall => {
  try {
    const keys = await syncQueue.keys();

    if (keys.length === 0) {
      logger.info('Sync queue is empty');
      return { success: true, processed: 0, failed: 0 };
    }

    logger.info(`Processing ${keys.length} items in sync queue`);

    let processed = 0;
    let failed = 0;

    for (const key of keys) {
      try {
        const item = await syncQueue.getItem(key);

        if (!item) continue;

        // Make API call
        const response = await apiCall(item.endpoint, item.method, item.data);

        if (response.ok) {
          // Remove from queue on success
          await syncQueue.removeItem(key);
          processed++;
          logger.info(`Successfully synced item ${item.id}`);
        } else {
          // Keep in queue on failure
          failed++;
          logger.error(`Failed to sync item ${item.id}:`, response.status, response.statusText);
        }
      } catch (error) {
        failed++;
        logger.error(`Error processing sync item ${key}:`, error);
      }
    }

    return { success: true, processed, failed };
  } catch (error) {
    logger.error('Error processing sync queue:', error);
    return { success: false, processed: 0, failed: 0, error: error.message };
  }
};

/**
 * Get all items in sync queue
 */
export const getSyncQueueItems = async () => {
  try {
    const keys = await syncQueue.keys();
    const items = [];

    for (const key of keys) {
      const item = await syncQueue.getItem(key);
      if (item) items.push(item);
    }

    return items;
  } catch (error) {
    logger.error('Error getting sync queue items:', error);
    return [];
  }
};

/**
 * Clear sync queue
 */
export const clearSyncQueue = async () => {
  try {
    await syncQueue.clear();
    return true;
  } catch (error) {
    logger.error('Error clearing sync queue:', error);
    return false;
  }
};

const offlineSync = {
  saveOfflineData,
  getOfflineData,
  removeOfflineData,
  addToSyncQueue,
  processSyncQueue,
  getSyncQueueItems,
  clearSyncQueue
};

export default offlineSync;

/**
 * Optimized Data Structures and Utilities
 *
 * This module provides efficient data structures and utilities to replace
 * inefficient array operations (O(n)) with optimized alternatives (O(1) or O(log n)).
 *
 * Key optimizations:
 * - Use Map for fast lookups instead of array.find()
 * - Use Set for selection management instead of array.indexOf()
 * - Use specialized data structures for search and filtering
 * - Implement virtualization for large datasets
 * - Provide memoized data transformations
 */

import { useMemo, useCallback, useRef, useState, useEffect } from 'react';

/**
 * Performance monitoring for data operations
 */
import logger from './logger';

// ===================
// OPTIMIZED DATA STRUCTURES
// ===================

/**
 * IndexedCollection - Fast lookups using Map
 * Replaces inefficient array.find() operations
 */
export class IndexedCollection {
  constructor(items = [], keyField = 'id') {
    this.keyField = keyField;
    this.items = new Map();
    this.orderedKeys = [];

    items.forEach(item => this.add(item));
  }

  add(item) {
    const key = item[this.keyField];
    if (!this.items.has(key)) {
      this.orderedKeys.push(key);
    }
    this.items.set(key, item);
    return this;
  }

  remove(key) {
    if (this.items.has(key)) {
      this.items.delete(key);
      this.orderedKeys = this.orderedKeys.filter(k => k !== key);
    }
    return this;
  }

  get(key) {
    return this.items.get(key);
  }

  update(key, updates) {
    const item = this.items.get(key);
    if (item) {
      this.items.set(key, { ...item, ...updates });
    }
    return this;
  }

  has(key) {
    return this.items.has(key);
  }

  getAll() {
    return this.orderedKeys.map(key => this.items.get(key));
  }

  filter(predicate) {
    return this.orderedKeys.map(key => this.items.get(key)).filter(predicate);
  }

  map(transform) {
    return this.orderedKeys.map(key => transform(this.items.get(key)));
  }

  find(predicate) {
    for (const key of this.orderedKeys) {
      const item = this.items.get(key);
      if (predicate(item)) {
        return item;
      }
    }
    return undefined;
  }

  size() {
    return this.items.size;
  }

  clear() {
    this.items.clear();
    this.orderedKeys = [];
    return this;
  }

  // Bulk operations for better performance
  bulkAdd(items) {
    items.forEach(item => this.add(item));
    return this;
  }

  bulkRemove(keys) {
    keys.forEach(key => this.remove(key));
    return this;
  }

  bulkUpdate(updates) {
    Object.entries(updates).forEach(([key, update]) => {
      this.update(key, update);
    });
    return this;
  }
}

/**
 * SelectionManager - Efficient selection state management
 * Replaces inefficient array.indexOf() operations with Set
 */
export class SelectionManager {
  constructor(initialSelected = []) {
    this.selected = new Set(initialSelected);
  }

  select(id) {
    this.selected.add(id);
    return this;
  }

  deselect(id) {
    this.selected.delete(id);
    return this;
  }

  toggle(id) {
    if (this.selected.has(id)) {
      this.selected.delete(id);
    } else {
      this.selected.add(id);
    }
    return this;
  }

  isSelected(id) {
    return this.selected.has(id);
  }

  selectAll(ids) {
    this.selected.clear();
    ids.forEach(id => this.selected.add(id));
    return this;
  }

  deselectAll() {
    this.selected.clear();
    return this;
  }

  getSelected() {
    return Array.from(this.selected);
  }

  getSelectedSet() {
    return new Set(this.selected);
  }

  size() {
    return this.selected.size;
  }

  isEmpty() {
    return this.selected.size === 0;
  }

  clone() {
    return new SelectionManager(Array.from(this.selected));
  }

  // Bulk operations
  bulkToggle(ids) {
    ids.forEach(id => this.toggle(id));
    return this;
  }

  bulkSelect(ids) {
    ids.forEach(id => this.selected.add(id));
    return this;
  }

  bulkDeselect(ids) {
    ids.forEach(id => this.selected.delete(id));
    return this;
  }
}

/**
 * SearchIndex - Fast text searching using inverted index
 * Replaces inefficient string.includes() searches
 */
export class SearchIndex {
  constructor(items = [], searchFields = []) {
    this.searchFields = searchFields;
    this.index = new Map(); // word -> Set of item IDs
    this.items = new Map(); // id -> item

    items.forEach(item => this.addItem(item));
  }

  addItem(item) {
    const id = item.id || item._id;
    this.items.set(id, item);

    // Index all searchable text
    this.searchFields.forEach(field => {
      const text = this._getNestedValue(item, field);
      if (text) {
        this._indexText(text.toString(), id);
      }
    });
  }

  removeItem(id) {
    const item = this.items.get(id);
    if (item) {
      // Remove from index
      this.searchFields.forEach(field => {
        const text = this._getNestedValue(item, field);
        if (text) {
          this._removeFromIndex(text.toString(), id);
        }
      });

      this.items.delete(id);
    }
  }

  search(query) {
    if (!query || query.trim() === '') {
      return Array.from(this.items.values());
    }

    const normalizedQuery = query.toLowerCase().trim();
    const words = normalizedQuery.split(/\s+/);

    if (words.length === 0) {
      return Array.from(this.items.values());
    }

    // Find intersection of all word searches
    let resultIds = null;

    for (const word of words) {
      const wordResults = this._searchWord(word);

      if (resultIds === null) {
        resultIds = wordResults;
      } else {
        // Intersection
        resultIds = resultIds.filter(id => wordResults.has(id));
      }

      // Early exit if no matches
      if (resultIds.length === 0) {
        break;
      }
    }

    return (resultIds || []).map(id => this.items.get(id));
  }

  _indexText(text, itemId) {
    const words = text.toLowerCase().split(/\s+/);
    words.forEach(word => {
      if (word.length > 0) {
        if (!this.index.has(word)) {
          this.index.set(word, new Set());
        }
        this.index.get(word).add(itemId);
      }
    });
  }

  _removeFromIndex(text, itemId) {
    const words = text.toLowerCase().split(/\s+/);
    words.forEach(word => {
      const itemSet = this.index.get(word);
      if (itemSet) {
        itemSet.delete(itemId);
        if (itemSet.size === 0) {
          this.index.delete(word);
        }
      }
    });
  }

  _searchWord(word) {
    const exactMatches = this.index.get(word) || new Set();

    // Also search for partial matches (prefix)
    const partialMatches = new Set();
    for (const [indexedWord, itemIds] of this.index) {
      if (indexedWord.startsWith(word)) {
        itemIds.forEach(id => partialMatches.add(id));
      }
    }

    return partialMatches.size > exactMatches.size ? partialMatches : exactMatches;
  }

  _getNestedValue(obj, path) {
    return path.split('.').reduce((current, key) => current?.[key], obj);
  }

  clear() {
    this.index.clear();
    this.items.clear();
  }

  size() {
    return this.items.size;
  }
}

/**
 * SortedIndex - Maintains sorted order for efficient range queries
 */
export class SortedIndex {
  constructor(items = [], keyField = 'id', sortField = null) {
    this.keyField = keyField;
    this.sortField = sortField;
    this.items = new IndexedCollection([], keyField);
    this.sortedKeys = [];

    items.forEach(item => this.add(item));
  }

  add(item) {
    this.items.add(item);
    this._insertSorted(item[this.keyField]);
    return this;
  }

  remove(key) {
    this.items.remove(key);
    this.sortedKeys = this.sortedKeys.filter(k => k !== key);
    return this;
  }

  update(key, updates) {
    this.items.update(key, updates);
    // Re-sort if sort field was updated
    if (this.sortField && updates[this.sortField] !== undefined) {
      this.sortedKeys = this.sortedKeys.filter(k => k !== key);
      this._insertSorted(key);
    }
    return this;
  }

  getInOrder() {
    return this.sortedKeys.map(key => this.items.get(key));
  }

  getRange(start, end) {
    return this.sortedKeys.slice(start, end).map(key => this.items.get(key));
  }

  _insertSorted(key) {
    if (!this.sortField) {
      this.sortedKeys.push(key);
      return;
    }

    const item = this.items.get(key);
    const value = item[this.sortField];

    // Binary search for insertion point
    let left = 0;
    let right = this.sortedKeys.length;

    while (left < right) {
      const mid = Math.floor((left + right) / 2);
      const midItem = this.items.get(this.sortedKeys[mid]);
      const midValue = midItem[this.sortField];

      if (midValue < value) {
        left = mid + 1;
      } else {
        right = mid;
      }
    }

    this.sortedKeys.splice(left, 0, key);
  }

  resort(newSortField) {
    this.sortField = newSortField;
    this.sortedKeys.sort((a, b) => {
      const aItem = this.items.get(a);
      const bItem = this.items.get(b);
      const aValue = aItem[newSortField];
      const bValue = bItem[newSortField];

      if (aValue < bValue) return -1;
      if (aValue > bValue) return 1;
      return 0;
    });
    return this;
  }
}

