// Page object patterns for complex interactions
export const createPageObject = containerSelector => {
  const getContainer = () => {
    if (containerSelector) {
      return screen.getByTestId(containerSelector);
    }
    return document.body;
  };

  return {
    // Find elements within the page/component
    find: {
      byRole: (role, options) => within(getContainer()).getByRole(role, options),
      byText: (text, options) => within(getContainer()).getByText(text, options),
      byTestId: testId => within(getContainer()).getByTestId(testId),
      byLabelText: label => within(getContainer()).getByLabelText(label)
    },

    // Query elements (returns null if not found)
    query: {
      byRole: (role, options) => within(getContainer()).queryByRole(role, options),
      byText: (text, options) => within(getContainer()).queryByText(text, options),
      byTestId: testId => within(getContainer()).queryByTestId(testId),
      byLabelText: label => within(getContainer()).queryByLabelText(label)
    },

    // Wait for elements to appear
    waitFor: {
      byRole: (role, options) => within(getContainer()).findByRole(role, options),
      byText: (text, options) => within(getContainer()).findByText(text, options),
      byTestId: testId => within(getContainer()).findByTestId(testId),
      byLabelText: label => within(getContainer()).findByLabelText(label)
    },

    // Complex interactions
    fillForm: async formData => {
      const container = getContainer();

      for (const [field, value] of Object.entries(formData)) {
        const input = within(container).getByLabelText(new RegExp(field, 'i'));
        await robustInteractions.type(input, String(value));
      }
    },

    submitForm: async () => {
      const container = getContainer();
      const form = within(container).getByRole('form') || container.querySelector('form');

      if (form) {
        await robustInteractions.submitForm(form);
      }
    },

    // Assertions specific to this page/component
    assertVisible: () => {
      expect(getContainer()).toBeInTheDocument();
      expect(getContainer()).toBeVisible();
    },

    assertLoading: () => {
      const container = getContainer();
      const loadingIndicator =
        within(container).queryByRole('status') || within(container).queryByText(/loading|Loading/);
      expect(loadingIndicator).toBeInTheDocument();
    },

    assertError: errorMessage => {
      const container = getContainer();
      const errorElement =
        within(container).getByRole('alert') || within(container).getByText(/error/i);
      expect(errorElement).toBeInTheDocument();

      if (errorMessage) {
        expect(errorElement).toHaveTextContent(errorMessage);
      }
    }
  };
};

// Retry mechanisms for flaky assertions
export const retryAssertion = async (assertion, maxRetries = 3, delay = 100) => {
  let lastError;

  for (let i = 0; i < maxRetries; i++) {
    try {
      await assertion();
      return; // Success
    } catch (error) {
      lastError = error;

      if (i < maxRetries - 1) {
        await new Promise(resolve => setTimeout(resolve, delay * (i + 1)));
      }
    }
  }

  throw lastError;
};

export default {
  robustSelectors,
  robustAssertions,
  robustInteractions,
  testDataBuilders,
  createPageObject,
  retryAssertion
};

/**
 * RTL (Right-to-Left) Support Utilities
 * Comprehensive RTL support for Arabic and other RTL languages
 */

import createCache from '@emotion/cache';
import { CacheProvider } from '@emotion/react';
import { styled, createTheme, ThemeProvider } from '@mui/material/styles';
import React, { createContext, useContext, useEffect } from 'react';
import { prefixer } from 'stylis';
import rtlPlugin from 'stylis-plugin-rtl';

import { useLanguage } from '../i18n/index';

// RTL languages configuration
export const rtlLanguages = ['ar'];

// Create RTL cache for emotion
export const createRTLCache = () => {
  return createCache({
    key: 'muirtl',
    stylisPlugins: [prefixer, rtlPlugin]
  });
};

// Create LTR cache for emotion
export const createLTRCache = () => {
  return createCache({
    key: 'muiltr'
  });
};

// RTL Context
const RTLContext = createContext({
  isRTL: false,
  direction: 'ltr',
  toggleDirection: () => {},
  setDirection: () => {}
});

// RTL Provider Component
export const RTLProvider = ({ children }) => {
  const { currentLanguage, isRTL: languageIsRTL } = useLanguage();
  const [isRTL, setIsRTL] = React.useState(languageIsRTL);
  const [direction, setDirection] = React.useState(languageIsRTL ? 'rtl' : 'ltr');

  // Update RTL state when language changes
  useEffect(() => {
    setIsRTL(languageIsRTL);
    setDirection(languageIsRTL ? 'rtl' : 'ltr');

    // Update document direction
    document.dir = languageIsRTL ? 'rtl' : 'ltr';
    document.documentElement.setAttribute('dir', languageIsRTL ? 'rtl' : 'ltr');

    // Add RTL class to body for CSS targeting
    document.body.classList.toggle('rtl', languageIsRTL);
    document.body.classList.toggle('ltr', !languageIsRTL);
  }, [languageIsRTL]);

  const toggleDirection = () => {
    const newIsRTL = !isRTL;
    setIsRTL(newIsRTL);
    setDirection(newIsRTL ? 'rtl' : 'ltr');
    document.dir = newIsRTL ? 'rtl' : 'ltr';
    document.body.classList.toggle('rtl', newIsRTL);
    document.body.classList.toggle('ltr', !newIsRTL);
  };

  const setDirectionValue = dir => {
    const newIsRTL = dir === 'rtl';
    setIsRTL(newIsRTL);
    setDirection(dir);
    document.dir = dir;
    document.body.classList.toggle('rtl', newIsRTL);
    document.body.classList.toggle('ltr', !newIsRTL);
  };

  // Create appropriate cache
  const cache = isRTL ? createRTLCache() : createLTRCache();

  return (
    <RTLContext.Provider
      value={{
        isRTL,
        direction,
        toggleDirection,
        setDirection: setDirectionValue
      }}
    >
      <CacheProvider value={cache}>{children}</CacheProvider>
    </RTLContext.Provider>
  );
};

// Hook to use RTL context
export const useRTL = () => {
  const context = useContext(RTLContext);
  if (!context) {
    throw new Error('useRTL must be used within RTLProvider');
  }
  return context;
};

// RTL-aware theme creation
