        if (element) return element;
      } catch (error) {
        // Element not found yet, continue waiting
      }

      await new Promise(resolve => setTimeout(resolve, 50));
    }

    throw new Error(`Element not found within ${timeout}ms`);
  },

  // Assert async conditions
  assertEventually: async (assertion, timeout = 3000) => {
    const startTime = Date.now();
    let lastError;

    while (Date.now() - startTime < timeout) {
      try {
        await assertion();
        return; // Success
      } catch (error) {
        lastError = error;
        await new Promise(resolve => setTimeout(resolve, 50));
      }
    }

    throw lastError || new Error(`Assertion failed within ${timeout}ms`);
  },

  // Assert with retry for flaky conditions
  assertWithRetry: async (assertion, maxRetries = 3) => {
    let lastError;

    for (let i = 0; i < maxRetries; i++) {
      try {
        await assertion();
        return; // Success
      } catch (error) {
        lastError = error;
        if (i < maxRetries - 1) {
          await new Promise(resolve => setTimeout(resolve, 100 * (i + 1)));
        }
      }
    }

    throw lastError;
  }
};

// Test environment setup
export const testSetup = {
  // Setup test environment
  setupTestEnvironment: () => {
    // Mock console methods to reduce noise
    const originalConsole = { ...console };
    console.warn = jest.fn();
    console.error = jest.fn();

    // Mock ResizeObserver
    global.ResizeObserver = jest.fn().mockImplementation(() => ({
      observe: jest.fn(),
      unobserve: jest.fn(),
      disconnect: jest.fn()
    }));

    // Mock IntersectionObserver
    global.IntersectionObserver = jest.fn().mockImplementation(() => ({
      observe: jest.fn(),
      unobserve: jest.fn(),
      disconnect: jest.fn()
    }));

    return {
      cleanup: () => {
        Object.assign(console, originalConsole);
        delete global.ResizeObserver;
        delete global.IntersectionObserver;
      }
    };
  },

  // Setup consistent test time
  setupTestTime: (date = '2024-01-01T00:00:00.000Z') => {
    const fixedDate = new Date(date);
    const originalDate = Date;

    global.Date = class extends Date {
      constructor(...args) {
        if (args.length === 0) {
          return fixedDate;
        }
        return new originalDate(...args);
      }

      static now() {
        return fixedDate.getTime();
      }
    };

    return {
      cleanup: () => {
        global.Date = originalDate;
      }
    };
  }
};

// Performance testing utilities
export const performanceUtils = {
  // Measure render time
  measureRenderTime: async renderFn => {
    const start = performance.now();
    const result = await renderFn();
    const end = performance.now();

    return {
      result,
      renderTime: end - start
    };
  },

  // Test for memory leaks
  detectMemoryLeaks: testFn => {
    const initialMemory = performance.memory?.usedJSHeapSize || 0;

    return {
      run: async () => {
        await testFn();

        // Force garbage collection if available
        if (global.gc) {
          global.gc();
        }

        const finalMemory = performance.memory?.usedJSHeapSize || 0;
        const memoryIncrease = finalMemory - initialMemory;

        return {
          initialMemory,
          finalMemory,
          memoryIncrease,
          hasLeak: memoryIncrease > 1024 * 1024 // 1MB threshold
        };
      }
    };
  }
};

export default {
  waitUtils,
  mockUtils,
  renderUtils,
  testDataFactories,
  assertionHelpers,
  testSetup,
  performanceUtils
};
